import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import Spinner from '../Utils/Spinner';

function DependenciasLista() {
    const [dependenciasLista, setDependenciasLista] = useState({ data: [] });
    const [dependenciasListaTotal, setDependenciasListaTotal] = useState({ data: [], links: [], meta: [] });

    useEffect(() => {
        async function fetchData() {
            cargarDependencias();
        }
        fetchData();
    }, []);

    const cargarDependencias = () => {
        window.showSpinner(true);
        GenericApi.getAllGeneric('mas-dependencia-origen').then(
            datos => {
                if (!datos.error) {
                    setDependenciasLista(datos);
                    setDependenciasListaTotal(datos);
                    window.showSpinner(false);
                }
                else {
                    window.showModal();
                    window.showSpinner(false);
                }

            }
        )
    }

    const cambiarEstado = (objeto, estado) => {

        const data = {
            "data": {
                "type": "mas_dependencia_origen",
                "attributes": {
                    "nombre": objeto.attributes.nombre,
                    "estado": estado,
                    "id_usuario_jefe": ""
                }
            }
        }

        GenericApi.updateGeneric('mas-dependencia-origen', objeto.id, data).then(
            datos => {
                if (!datos.error) {
                    cargarDependencias();
                }
                else
                    window.showModal();
            }
        )

    }

    const dependencias = () => {
        return (
            dependenciasLista.data.map((dependencia, i) => {
                return (
                    <tr key={dependencia.id}>
                        <td className="text-center">{dependencia.id}</td>
                        <td style={{ width: "50%" }}>
                            {dependencia.attributes.nombre}
                        </td>
                        <td style={{ width: "20%" }}>
                            {dependencia.attributes.nombre_usuario_jefe}
                        </td>
                        <td>
                            {dependencia.attributes.estado == "1" ? 'Activo' : 'Inactivo'}
                        </td>
                        <td>
                            <Link to={`${dependencia.id}`}>
                                <button type="button" className="btn btn-sm btn-primary" title='Editar'>
                                    <i className="fa fa-fw fa-edit"></i>
                                </button>
                            </Link>
                            {
                                (dependencia.attributes.estado == 0) ? (

                                    <button type='button' title='Activar Antecedente' className='btn btn-sm btn-success' onClick={() => cambiarEstado(dependencia, 1)}><i className="fas fa-plus-circle"></i></button>

                                ) : null
                            }

                            {
                                (dependencia.attributes.estado == 1) ? (

                                    <button type='button' title='Inactivar Antecedente' className='btn btn-sm btn-danger' onClick={() => cambiarEstado(dependencia, 0)}><i className="fas fa-minus-circle"></i></button>



                                ) : null
                            }
                        </td>
                    </tr>
                )
            })
        )
    }

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {
                setDependenciasLista(dependenciasListaTotal);
            }
            else {
                var filteredData = {
                    data: dependenciasListaTotal.data.filter(
                        suggestion => ((suggestion.id
                            + suggestion.attributes.nombre
                            + suggestion.attributes.nombre_usuario_jefe
                            + (suggestion.attributes.dependencia ? suggestion.attributes.dependencia.nombre : '')
                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(event.target.value.toLowerCase()))
                    )
                };
                setDependenciasLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="block block-rounded block-bordered">
            {<InfoErrorApi />}
            {<Spinner />}
            <div className="block block-themed">
                <div className="col-md-12">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <small>Lista de dependencias</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
                <div className="block-header">
                    <h3 className="block-title">Dependencias registradas</h3>
                    <Link to={`/Dependencia/Add`} >
                        <button type="button" className="btn btn-sm btn-dark mr-1 mb-3"><i class="far fa-plus-square"></i> Agregar dependencia</button>
                    </Link>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-12'>
                            <div className="form-group ">
                                <label htmlFor='search'>Buscar: </label>
                                <input type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th className="text-center">Id</th>
                                        <th>Dependencia</th>
                                        <th>Usuario jefe</th>
                                        <th>Estado</th>
                                        <th>Acciones</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {dependencias()}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default DependenciasLista;
