import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link, } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import GenericApi from '../Api/Services/GenericApi';
import Spinner from '../Utils/Spinner';
import { Navigate } from "react-router-dom";
import { hasAccess } from '../../components/Utils/Common';
import DataTable from 'react-data-table-component';

function RolesLista() {


    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    const [getRespuestaRolesSearch, setRespuestaRolesSearch] = useState(false);
    const [getRolesListaSearch, setRolesListaSearch] = useState({ data: [], links: [], meta: [] });

    const [getRespuestaRolesTotal, setRespuestaRolesTotal] = useState(false);
    const [getRolesListaTotal, setRolesListaTotal] = useState({ data: [], links: [], meta: [] });

    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);

    const columns = [
        {
            name: 'Nombre',
            selector: ciudad => ciudad.attributes.nombre,
            sortable: true,
            width: "80%"
        },
        {
            name: 'Acciones',


            cell: row => <div><Link to={`${row.id}`}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>
        }
    ];

    const redirectToRoutes = () => {
        return <Navigate to={`/RolesLista/`} />;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);

            cargarRoles(1, 10);


        }
        fetchData();
    }, []);

    const cargarRoles = (page, perPage) => {
        //cargamos todos los roles
        GenericApi.getAllGeneric('role/role-paginate' + '/' + page + '/' + perPage).then(
            datos => {

                if (!datos.error) {
                    if (datos["data"] != "") {
                        setRolesListaSearch(datos)
                        setRespuestaRolesSearch(true)
                        setRolesListaTotal(datos)
                        setRespuestaRolesTotal(true)
                        window.showSpinner(false);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                        window.showSpinner(false);

                    }
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                    window.showSpinner(false);
                }

            }
        )
    }

    const handlePageChange = page => {
        window.showSpinner(true);
        setPageActual(page);
        cargarRoles(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        cargarRoles(page, newPerPage);

    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const handleSearch = (event) => {

        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setRolesListaSearch(getRolesListaTotal);
            }
            else {

                var filteredData = {
                    data: getRolesListaTotal.data.filter(
                        suggestion => ((suggestion.attributes.nombre
                            + suggestion.attributes.apellido
                            + suggestion.attributes.name
                            + suggestion.attributes.email
                            + (suggestion.attributes.dependencia ? suggestion.attributes.dependencia.nombre : '')
                            + (suggestion.attributes.estado == '0' ? 'Inactivo' : 'Activo')).toLowerCase().includes(event.target.value.toLowerCase()))
                    )
                };
                setRolesListaSearch(filteredData)
            }
        } catch (error) {
            console.error(error);
        }

    };

    const enviarDatos = (estado, id) => {

        try {


        } catch (error) {
            window.showSpinner(false);
        }
    }


    const listaRoles = () => {

        if (getRolesListaSearch.data != null && typeof (getRolesListaSearch.data) != 'undefined') {
            return (

                getRolesListaSearch.data.map((rol, i) => {
                    return (
                        cuerpoTabla(rol)
                    )
                })
            )
        }
        else {
            return (

                getRolesListaSearch.map((rol, i) => {
                    return (
                        cuerpoTabla(rol)
                    )
                })
            )
        }

    }

    const cuerpoTabla = (rol) => {
        return (
            <tr key={rol.id}>

                <td>
                    {rol.attributes.nombre}
                </td>

                <td>

                    <Link to={`/Rol/${rol.id}`}>
                        <button type='button' className='btn btn-sm btn-primary' title='Editar'><span className="fas fa-edit"></span></button>
                    </Link>


                </td>
            </tr>
        )
    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////



    return (
        <>




            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik>
                <Form>

                    <div className="block block-themed">
                        <div className="col-md-12">

                            <div className="block-content">
                                <nav aria-label="breadcrumb">
                                    <ol className="breadcrumb breadcrumb-alt push">
                                        <li class="breadcrumb-item"> <small>Lista de Roles</small></li>
                                    </ol>
                                </nav>
                            </div>

                        </div>
                        <div className="block-header">
                            <h3 className="block-title">Roles registrados</h3>

                            <Link to={`/Rol/Add`} >
                                <button type="button" className="btn btn-sm btn-light mr-1 mb-3"><i class="fas fa-plus"></i> Agregar Rol</button>
                            </Link>
                        </div>

                        <div className="block-content">

                            <div className='row'>

                                <div className='col-md-12'>
                                    <div className="form-group ">
                                        <label htmlFor='search'>Buscar: </label>
                                        <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                    </div>
                                </div>
                            </div>

                            <div className="row">
                                <div className="col-sm-12">

                                    <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                        columns={columns}
                                        data={getRolesListaSearch.data}
                                        perPage={perPage}
                                        page={pageActual}
                                        pagination
                                        noDataComponent="Sin datos"
                                        paginationServer
                                        paginationTotalRows={getRolesListaSearch.meta ? getRolesListaSearch.meta.total : 1}
                                        onChangePage={handlePageChange}
                                        onChangeRowsPerPage={handlePerRowsChange}
                                        defaultSortFieldId="Nombre"
                                        striped
                                        icon
                                        paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                        defaultSortAsc={false}
                                    />
                                </div>
                            </div>


                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );


}
export default RolesLista;
