import React, { useEffect, useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useLocation } from 'react-router-dom'
import LogApi from '../Api/Services/LogApi';
import moment from 'moment';
import { Link } from "react-router-dom";
import DataTable from 'react-data-table-component';

function LogEtapaLista() {


    const [errorApi, setErrorApi] = useState('');

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId; 

    const [getRespuestaLog, setRespuestaLog] = useState(false);
    const [getLogLista, setLogLista] = useState({ data: [], links: [], meta: [] });


    const columns = [

        {
            name: 'Etapa',
            selector: log => log.attributes.etapa.nombre,
            sortable: true,
            width: "20%"
        },

        {
            name: 'Fecha',
            selector: log => log.attributes.created_at,
            sortable: true,
            width: "20%"
        },

        {
            name: 'Funcionario Registra',

            cell: log => <div>{log.attributes.funcionario_registra.name}<br />
                {log.attributes.dependencia_origen.nombre}</div>,
            sortable: true,
            width: "20%"
        },

        {
            name: 'Funcionario Actual',

            cell: log => <div>{log.attributes.funcionario_registra.name}<br />
                {log.attributes.dependencia_origen.nombre}</div>,
            sortable: true,
            width: "20%"
        },

        {
            name: 'Fecha registro',
            selector: antecedente => antecedente.attributes.fecha_creado,
            sortable: true,
            width: "20%"
        },
        {
            name: 'Dependencia',
            selector: antecedente => antecedente.attributes.nombre_dependencia,
            sortable: true,
            width: "15%",
        },
        {
            name: 'Descripción',
            cell: antecedente => <div title={antecedente.attributes.descripcion}>{antecedente.attributes.descripcion_corta}</div>,
            sortable: true,
            width: "15%",
        },
        {
            name: 'Estado',
            selector: antecedente => (antecedente.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
            width: "15%",
        },
        {
            name: 'Acciones',
            width: "15%",

            cell: antecedente => <div className='row'>
                <div className='col-4'>
                    <button type='button' title='Consultar Antecedente' className='btn btn-sm btn-primary inline' data-toggle="modal" onClick={() => cargarDetalleCambiosEstado(antecedente.id)} data-target={'#modal-consultar-detalle'}><i class="fas fa-search"></i></button>
                </div>
                <div className='col'>
                    {
                        (hasAccess('CR_Antecedente', 'Inactivar')) ? ( 
                            <div>

                                {
                                    (antecedente.attributes.estado == 0) ? (
                                        <Link to={`/AntecedentesCambiarEstadoForm/${antecedente.id}`} state={{ from: from }}>
                                            <button type='button' title='Activar Antecedente' className='btn btn-sm btn-success' data-toggle="modal" data-target={'#modal-cambiar-estado'}><i className="fas fa-plus-circle"></i></button>
                                        </Link>
                                    ) : null
                                }

                                {
                                    (antecedente.attributes.estado == 1 &&  getAntecedentesLista.data.length > 1) ? (
                                        <Link to={`/AntecedentesCambiarEstadoForm/${antecedente.id}`} state={{ from: from }}>
                                            <button type='button' title='Inactivar Antecedente' className='btn btn-sm btn-danger'><i className="fas fa-minus-circle"></i></button>
                                        </Link>


                                    ) : null
                                }

                                <div className="modal fade" id={'modal-consultar-detalle'} tabindex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                                    <div className="modal-dialog modal-lg" role="document" >
                                        <div class="modal-content">
                                            <div class="block block-themed block-transparent mb-0">
                                                <div class="block-header bg-primary-dark">
                                                    <h3 class="block-title">Detalle del cambio en el antecedente</h3>
                                                    <div class="block-options">
                                                        <button type="button" class="btn-block-option" data-dismiss="modal" aria-label="Close">
                                                            <i class="fa fa-fw fa-times"></i>
                                                        </button>
                                                    </div>
                                                </div>
                                                <div class="block-content" style={{ 'height': '300px', 'overflow': 'scroll', 'display': 'block' }}>
                                                    Observaciones generales: {antecedente.attributes.descripcion}<br /><br />
                                                </div>
                                                {
                                                    (getListaDetalleCambios.data.length > 0) ? (
                                                        <div>
                                                            <div className="block-content">
                                                                <spam class="block-title">Historial de cambios en el antecedente</spam>
                                                            </div>

                                                            <div class="block-content" style={{ 'height': '300px', 'overflow': 'scroll', 'display': 'block' }}>
                                                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                                    <thead>
                                                                        <tr>
                                                                            <th>Usuario Registra</th>
                                                                            <th>Dependencia</th>
                                                                            <th>Descripción</th>
                                                                            <th>Fecha</th>
                                                                        </tr>
                                                                    </thead>
                                                                    <tbody>
                                                                        {listaDetalleCambios()}
                                                                    </tbody>
                                                                </table>
                                                            </div>
                                                        </div>
                                                    ) : null
                                                }


                                                <div class="block-content block-content-full text-right bg-light">
                                                    <button type="button" class="btn btn-sm btn-primary" data-dismiss="modal">Cerrar</button>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ) : null
                    }
                </div>
            </div>
        }
    ];



    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "log_etapa",
                    "attributes": {
                        "descripcion": "descripcion",
                        "id_etapa": "fecha_registro",
                        "id_fase": "id_dependencia",
                        "id_proceso_disciplinario": "id_proceso_disciplinario",
                        "id_tipo_log": "id_tipo_log",
                        "id_estado": "id_estado",
                        "created_user": "created_user",
                        "id_dependencia_origen": "id_dependencia_origen"
                    }
                }
            }


            LogApi.getLogEtapaProcesoDisciplinario(data, procesoDisciplinarioId).then(
                datos => !datos.error ? (setLogLista(datos), setRespuestaLog(true)) : window.showModal(1)
            )

        }
        fetchData();
    }, []);


    const listaLog = () => {

        if (getLogLista.data != null && typeof (getLogLista.data) != 'undefined') {
            return (

                getLogLista.data.map((log, i) => {
                    return (
                        cargarLog(log, i)
                    )
                })
            )
        }
        else {
            return (

                getLogLista.map((log, i) => {
                    return (
                        cargarLog(log, i)
                    )
                })
            )
        }
    }


    const cargarLog = (log, i) => {

        return (
            <>

                <tr>
                    <td>
                        {(log.attributes.etapa ? log.attributes.etapa.nombre : "")}
                    </td>
                    <td>
                        {log.attributes.created_at}
                    </td>
                    <td>
                        {(log.attributes.funcionario_registra ? log.attributes.funcionario_registra.name : "")}<br/>
                        {(log.attributes.dependencia_origen ? log.attributes.dependencia_origen.nombre : "")}
                    </td>

                    <td>
                        {(log.attributes.funcionario_actual ? log.attributes.funcionario_actual.name : "")}<br/>
                        {(log.attributes.dep_funcionario_actual ? log.attributes.dep_funcionario_actual.nombre : "")}
                    </td>

                    <td>
                        {log.attributes.descripcion}
                    </td>

                    <td>
                        {(log.attributes.estado_etapa ? log.attributes.estado_etapa.nombre : "")}
                    </td>
                </tr>

                
                               
            </>
        )
    }


    return (
        <>

            <div className="block block-themed">
                    <div className="block-header">
                        <h3 className="block-title">Log Proceso disciplinario :: </h3>
                    </div>
                    <div className="block-content">

                    <div className='row'>

                        <div className='col-md-3'>
                            <div className="form-group ">
                                
                            </div>
                        </div>

                    </div>

                        <div className='col-md-12'>
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>

                                        <th>Etapa</th>
                                        <th>Fecha</th>
                                        <th>Funcionario Registra</th>
                                        <th>Funcionario Actual</th>
                                        <th>Descripción</th>
                                        <th>Estado del trámite</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {getRespuestaLog ? listaLog() : null}

                                </tbody>
                            </table>
                        </div>
                    </div>
            </div>

        </>
    );
}

export default LogEtapaLista;
