import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import { Link } from "react-router-dom";
import DatePicker from "react-datepicker";

function SoporteRadicadoForm() {

    const [listaTipoExpediente, setListaTipoExpediente] = useState({ data:{} });
    const [respuestaTipoExpediente, setRespuestaTipoExpediente] = useState(false);
    const [selectedTipoExpediente, setSelectedTipoExpediente] = useState("");

    const [listaTipoDerechoPeticion, setListaDerechoPeticion] = useState({ data:{} });
    const [selectedTipoDerechoPeticion, setSelectedDerechoPeticion] = useState("");
    const [respuestaTipoDerechoPeticion, setRespuestaDerechoPeticion] = useState(false);


    const [listaTerminosRespuesta, setListaTerminosRespuesta] = useState({ data:{} });
    const [selectedTerminosRespuesta, setSelectedTerminosRespuesta] = useState("");
    const [respuestaTerminosRespuesta, setRespuestaTerminosRespuesta] = useState(false);


    const [listaTiposQueja, setListaTiposQueja] = useState({ data:{} });
    const [selectedTiposQueja, setSelectedTiposQueja] = useState("");
    const [respuestaTiposQueja, setRespuestaTiposQueja] = useState(false);


    const [startDate, setStartDate] = useState(new Date());
    
    let { radicado, procesoDisciplinarioId } = useParams();


    return (
        <>

                <Formik
                    initialValues={{

                    }}
                    enableReinitialize

                    validate={(valores) => {

                    }}

                    onSubmit={(valores, {resetForm}) => {
                        //console.log (valores);
                        //enviarDatos(valores);
                    }}
                >

                    <Form>


                        <div className="col-md-12">
                            <div className="block block-rounded block-bordered">
                                <div className="block-content">
                                    <nav aria-label="breadcrumb">
                                        <ol className="breadcrumb breadcrumb-alt push">
                                            <li className="breadcrumb-item"><Link underline="hover" className="text-dark"
                                                                                  to={`/MisPendientes`}>
                                                <small>Mis Pendientes</small>
                                            </Link></li>
                                            <li className="breadcrumb-item"><Link underline="hover" className="text-dark"
                                                                                  to={`/RamasProceso/${radicado}/${radicado}`}>
                                                <small>Ramas del proceso</small>
                                            </Link></li>
                                            <li className="breadcrumb-item"><small>Soporte del radicado</small></li>
                                        </ol>
                                    </nav>
                                </div>
                            </div>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Soporte del radicado SINPROC No. {radicado}</h3>
                            </div>

                            <div className="block-content">
                                <div className="row">

                                    <div className="col-md-12 text-right">

                                        <Link to={`/EntidadInvestigadoLista/${radicado}/${procesoDisciplinarioId}`}>
                                            <button type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                                                <i className="fa fa-fw fa-archive mr-1"></i> Ver Historial
                                            </button>
                                        </Link>
                                    </div>

                                    <div className="col-md-12">
                                        <div className="alert alert-warning alert-dismissable" role="alert">
                                            <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                            <p className="mb-0"> ETAPA:   CAPTURA Y REPARTO - FASE:   SOPORTE(S) DEL RADICADO </p>
                                            <p className="mb-0"> Para cualquier archivo que requiera adjuntar valide previamente lo siguiente: </p>
                                            <p className="mb-0">
                                                NO puede superar QUINCE (15) Mb de peso/tamaño.
                                                Tipo/Formato permitido: [.doc], [.docx], [.pdf], [.xls], [.xlsx], [.zip], [.rar], [.png], [.jpg], [.mp4], [.avi], [.mpeg], [.mpg], [.mov], [.mp3], [.wav], [.wma].
                                            </p>
                                            <p className="mb-0">
                                                El nombre del archivo debe ser de máximo 40 caracteres.
                                            </p>
                                        </div>
                                    </div>

                                    <div className="col-md-2">
                                        <div className="form-group">
                                            <label htmlFor="numFolios">No. de folios <span className="text-danger">*</span></label>
                                            <Field as="input" className="form-control" type="text" id="numFolios" name="numFolios" placeholder="No. de folios" />
                                        </div>
                                    </div>

                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor="fileFolio">Archivo<span className="text-danger">*</span></label>
                                            <Field as="file" className="form-control" type="text" id="fileFolio" name="fileFolio" placeholder="Seleccionar archivo" />


                                            <label>Bootstrap’s Custom File Input</label>
                                            <div class="custom-file">
                                                <Field as="file" class="custom-file-input" data-toggle="custom-file-input" id="example-file-input-custom" name="example-file-input-custom" />
                                                <label class="custom-file-label" for="example-file-input-custom">Choose file</label>
                                            </div>


                                        </div>
                                    </div>

                                    <div className="col-md-12">
                                        <div className="block-content block-content-full text-right">
                                            <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>

                    </Form>

                </Formik>

        </>                 
    );
  
}
export default SoporteRadicadoForm;
