import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState, useRef } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';
import Center from '../../Utils/Styles/Center.js'
import moment from 'moment';

function DatoInteresadoMigracionForm() {

    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [countTextArea, setCountTextArea] = useState(0);
    const [getInteresado, setInteresado] = useState({ title: "", message: "", show: false });
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
   

    let { radicado, version, id, item } = useParams();

    const [existeInformacion, setExisteInformacion] = useState(false);
    const [nombres, setNombres] = useState('');
    const [apellidos, setApellidos] = useState('');
    const [identificacion, setIdentificacion] = useState('');
    const [tipoUsuario, setTipoUsuario] = useState('');
    const [genero, setGerenro] = useState('');


    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            interesados();
            obtenerParametros();
        }
        fetchData();
    }, []);

    
    function containsSpecialChars(str) {

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }

    const interesados= () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-datos-interesado/"+radicado+"/"+version+"/"+id+"/"+item).then(
                datos => {

                    if (!datos.error) {
                        setInteresado(datos);
                        setExisteInformacion(true);
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    const obtenerParametros = () => {
        try {

            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            GenericApi.getByDataGeneric("parametro/parametro-nombre",data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    const handleClick = (event, i) => {

        if(i===1){
            setNombres(getInteresado.data[0].attributes.interesado);
        }

        else if(i===2){
            setApellidos(getInteresado.data[0].attributes.interesado);
            
        }

    };


    const handleInputChange = (event, i) => {
     
        if(i===1){
            setDescripcion(event.currentTarget.value);
        }

        else if(i===2){
            setFecha(event.currentTarget.value)
        }

        return true;

        //return true;
    }



    const infoAntecedente = () => {

        if (getAntecedente.data != null && typeof (getAntecedente.data) != 'undefined') {
            return (

                getAntecedente.data.map((item, i) => {
                    return (
                        <>

                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="antecedente_origen">Antecedente</label>
                                <Field patt value={item.attributes.antecedente} autoComplete="off" as="textarea" className="form-control" id="antecedente_origen" name="antecedente_origen" rows="4">
                                  
                                </Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 1)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="antecedente_destino">Antecedente<span className="text-danger">*</span></label>
                                <Field as="textarea" className="form-control" id="antecedente_destino" name="antecedente_destino" rows="4" value={descripcion} onChange={event => handleInputChange(event, 1)}></Field>
                            </div>
                        </div> 

                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="fecha_antecedente_origen">Fecha <span className="text-danger">*</span></label>
                                <Field value={item.attributes.fecha} as="input" type="text" autoComplete="off" className="form-control" id="fecha_antecedente_origen" name="fecha_antecedente_origen"></Field>
                            </div>
                        </div>    

                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 2)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>  
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="fecha_antecedente_destino">Fecha <span className="text-danger">*</span></label>
                                <Field value={fecha} as="input" type="date" autoComplete="off" className="form-control" id="fecha_antecedente_destino" name="fecha_antecedente_destino" onChange={event => handleInputChange(event, 2)}></Field>
                            </div>
                        </div>   
                          
                        </>
                    )
                })
            )
        }
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;

        /*data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": datos.descripcion,
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "id_dependencia": dependencia ? dependencia : "",
                }
            }
        }

        GenericApi.addGeneric("antecedentes",data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: 'Registro Exitoso', show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.EXITO});
                }
                else {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
                window.showSpinner(false);

            }
        )*/
    }

    return (
        <>
            {<Spinner />}   
            {<ModalGen data={getModalState} />}
          
            <Formik
                initialValues={{
                    antecedente_origen: '',
                    antecedente_destino: '',
                    fecha_antecedente_origen: '',
                    fecha_antecedente_destino: '',
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                        <ol className="breadcrumb breadcrumb-alt push">
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/Buscador`}><small>Buscador</small></Link></li>
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ProcesoDisciplinarioMigracion/${radicado}/${version}/${id}`}><small>Inicio proceso de migraci√≥n</small></Link></li>
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ListarMigracionAntecedentes/${radicado}/${version}/${id}`}><small>Lista de antecedentes</small></Link></li>
                            <li class="breadcrumb-item"> <small>antecedente</small></li>
                        </ol>
                        </nav>
                    </div>

                      
                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No  :: <strong>Migrar antecedente {item}</strong></h3>
                            </div>

                            <div className="block-content">

                                <div className='text-right'>

                                    <Link to={`/AntecedentesLista/`} title='Regresar a lista de Antecedentes' >
                                        <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                    </Link>
                                </div>

                                <div className="row">

                                    {infoAntecedente()}
                                    
                                </div>

                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-rounded btn-primary">Registrar</button>

                                <Link to={`/AntecedentesLista/`}>
                                    <button type="button" className="btn btn-rounded btn-outline-primary">Cancelar</button>
                                </Link>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );
}


export default DatoInteresadoMigracionForm;
