import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { Field, Form, Formik } from 'formik';
import Spinner from '../Utils/Spinner';
import moment from 'moment';
import { useLocation } from 'react-router-dom';
import { hasAccess } from '../../components/Utils/Common';
import '../Utils/Constants';
import GenericApi from '../Api/Services/GenericApi';
import { getUser } from '../../components/Utils/Common';
import ModalCierreEtapaInfo from '../Utils/Modals/ModalCierreEtapaInfo';
import ModalGen from '../Utils/Modals/ModalGeneric';
import ClasificacionRadicadoApi from '../Api/Services/ClasificacionRadicadoApi';

function RamasProceso() {


  const [getRespuestaEncabezado, setRespuestaEncabezado] = useState(false);
  const [getEncabezado, setEncabezado] = useState(false);

  const location = useLocation();
  const { from, disable } = location.state;
  let radicado = from.radicado;
  let vigencia = from.vigencia;
  let procesoDisciplinarioId = from.procesoDisciplinarioId;

  useEffect(() => {

    getEncabezadoRamasProceso();

   
  }, []);


  /* Se consulta si el usuario que se encuentra en sesión es jefe de la dependencia*/
  const getEncabezadoRamasProceso = () => {

    // Se consume la API
    GenericApi.getGeneric("proceso-diciplinario/encabezadoDelProceso/" + procesoDisciplinarioId).then(

      // Se inicializa la variable de respuesta
      datos => {

        // Se valida que no haya error
        if (!datos.error) {
          setEncabezado(datos.data);
          setRespuestaEncabezado(true);
        } 
        
      }
    )
  }



  const componentAntecedente = () => {
    return (
      <>
        <div className="col-lg-12 push">
          <div className="alert alert-info" role="alert">
            <h4 className="txt-blue-primary">RADICADO: {radicado} - {vigencia}</h4>
            <span className="txt-blue-primary"><strong>ANTECEDENTE: </strong></span><br />
            <div className=''>
              <span className='txt-black-primary' title={(from.antecedente) ? from.antecedente : ""}>{from.antecedente ? from.antecedente.substring(0, 300) : ""}...</span>
            </div>
            <br />
            <div className="row">
              <div className="col-lg-6"><span className="txt-blue-primary"><strong >REGISTRADO POR: </strong> <span className='txt-black-primary'></span></span></div>
              <hr />
              <div className="col-lg-6"><span className="txt-blue-primary"><strong>DEPENDENCIA REGISTRO: </strong> <span className='txt-black-primary'></span></span></div>
              <hr />

              <div className="col-lg-6"><span className="txt-blue-primary"><strong>DEPENDENCIA DUEÑA: </strong> <span className='txt-black-primary'></span></span></div>
              <hr />

              <div className="col-lg-6"><span className="txt-blue-primary"><strong>USUARIO COMISIONADO: </strong> <span className='txt-black-primary'></span></span></div>
              <hr />             
            </div>

            <div className="row">
              <div className="col-lg-4"><span><strong className="txt-blue-primary">FECHA DE REGISTRO: </strong> </span></div>
              <div className="col-lg-4"><span><strong className="txt-blue-primary">FECHA DE INGRESO: </strong> </span></div>
              <div className="col-lg-4"><span><strong className="txt-blue-primary">FORMA DE INGRESO: </strong></span> <span className='txt-black-primary'></span></div>
            </div>
                       
          </div>
        </div>
      </>
    )
  }


  return (
    <>
      {<Spinner />}
      <div className="row">
        <div className="col-md-12">
          <div className="w2d_block">
            <nav aria-label="breadcrumb">
              <ol className="breadcrumb breadcrumb-alt push">
                <li className="breadcrumb-item">
                  <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                <li className="breadcrumb-item"><small>Ramas del proceso</small></li>
              </ol>
            </nav>
          </div>
        </div>
        {getRespuestaAntecedente ? componentAntecedente() : null}
      </div> 

      </>
  ); 

 
}


export default RamasProceso;
