import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import { Link } from "react-router-dom";
import DatePicker from "react-datepicker";

function SoporteRadicadoForm() {

    const [listaTipoExpediente, setListaTipoExpediente] = useState({ data:{} });
    const [respuestaTipoExpediente, setRespuestaTipoExpediente] = useState(false);
    const [selectedTipoExpediente, setSelectedTipoExpediente] = useState("");

    const [listaTipoDerechoPeticion, setListaDerechoPeticion] = useState({ data:{} });
    const [selectedTipoDerechoPeticion, setSelectedDerechoPeticion] = useState("");
    const [respuestaTipoDerechoPeticion, setRespuestaDerechoPeticion] = useState(false);


    const [listaTerminosRespuesta, setListaTerminosRespuesta] = useState({ data:{} });
    const [selectedTerminosRespuesta, setSelectedTerminosRespuesta] = useState("");
    const [respuestaTerminosRespuesta, setRespuestaTerminosRespuesta] = useState(false);


    const [listaTiposQueja, setListaTiposQueja] = useState({ data:{} });
    const [selectedTiposQueja, setSelectedTiposQueja] = useState("");
    const [respuestaTiposQueja, setRespuestaTiposQueja] = useState(false);


    const [startDate, setStartDate] = useState(new Date());
    
    let { radicado, procesoDisciplinarioId } = useParams();

    useEffect(() => {
        async function fetchData() {
            ParametrosMasApi.getAllTiposExpediente().then(
                datos => !datos.error ? (setListaTipoExpediente(datos), setRespuestaTipoExpediente(true) ) : window.showModal(1)
            )

        }
        fetchData();
    }, []);


    let selectChangeTipoExpediente = (e) => {
        setSelectedTipoExpediente(e);
    }


    //Componetes
    const selectTipoExpediente = () => {
        return(
            listaTipoExpediente.data.map((expediente, i) => {
                return (
                    <option key={expediente.id} value={expediente.id}>{expediente.attributes.nombre}</option>
                )
            })
        )
    }

    const enviarDatos = (datos) => {

        let data;
        //TIPO EXPEDIENTE => DERECHO DE PETICION => COPIAS 
        if(selectedTipoExpediente === '1' && selectedTipoDerechoPeticion === '1'){

                data = {
                    "data": {
                        "type": "clasificacion_radicado",
                        "attributes": {
                            "id_proceso_disciplinario": procesoDisciplinarioId,
                            "id_etapa": 1,
                            "id_tipo_expediente": selectedTipoExpediente,
                            "observaciones": datos.observaciones,
                            "id_tipo_queja": 0,
                            "id_termino_respuesta": 0,
                            "fecha_termino": null,
                            "hora_termino": 0,
                            "gestion_juridica": false,
                            "estado": true,
                            "id_estado_reparto": 2,
                        }
                    }
                }
        }
    }



    return (
        <>
            <div className="row">

                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">

                                    <li className="breadcrumb-item"><Link underline="hover" className="text-dark"
                                                                          to={`/MisPendientes`}>
                                        <small>Mis Pendientes</small>
                                    </Link></li>
                                    <li className="breadcrumb-item"><Link underline="hover" className="text-dark"
                                                                          to={`/RamasProceso/${radicado}/${radicado}`}>
                                        <small>Ramas del proceso</small>
                                    </Link></li>
                                    <li className="breadcrumb-item"><small>Clasificación del radicado</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                <div className="col-md-12 text-right">

                    <Link to={`/ClasificacionRadicadoLista/${radicado}/${procesoDisciplinarioId}`}>
                        <button type="button" className="btn btn-sm btn-primary mr-1 mb-3">
                            <i className="fa fa-fw fa-archive mr-1"></i> Ver Historial
                        </button>
                    </Link>
                </div>


                <div className="col-md-12">
                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">Clasificación del Radicado SINPROC No. {radicado}</h3>
                        </div>
                        <div className="block-content">
                            <div className="row">
                                <div className="col-md-3">
                                    <div className="form-group">
                                        <label htmlFor="numFolios">No. de folios <span className="text-danger">*</span></label>
                                        <Field as="input" className="form-control" type="text" id="numFolios" name="numFolios" placeholder="No. de folios" />
                                    </div>
                                </div>

                                <div className="col-md-9">
                                    <div className="form-group">
                                        <label htmlFor="fileFolio">Archivo<span className="text-danger">*</span></label>
                                        <Field as="file" className="form-control" type="text" id="fileFolio" name="fileFolio" placeholder="Seleccionar archivo" />
                                    </div>
                                </div>


                                <div className="col-md-12">
                                    <div className="form-group">
                                        <button type="submit" className="btn btn-primary">
                                            <i className="fas fa-save"></i> Adjuntar
                                        </button>
                                    </div>

                                </div>

                            </div>


                        </div>
                    </div>
                </div>
            </div>

        </>                 
    );
  
}
export default SoporteRadicadoForm;
