import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import DatosInteresadoApi from '../../Api/Services/DatosInteresadoApi';

function DatosInteresadoCambiarEstadoForm() {

   
    const [isRedirect, setIsRedirect] = useState(false);

    const [countTextArea, setCountTextArea] = useState(0);
    const [nombreUsuario, setNombreUsuario] = useState("");

    const location = useLocation()
    const { from } = location.state
	let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;

    let idDatosInteresado = from.idDatosInteresado;


    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/DatosInteresadoLista/`} state={{ from: from}}/>;
    }

    const [getErrorApi, setErrorApi] = useState('');
    const [getDatosInteresado, setDatosInteresado] = useState({ data: [], links: [], meta: [] });
    const [getRtaInfoDatos, setRtaInfoDatos] = useState(false);
    const [getEstado, setEstado] = useState("activa");

    useEffect(() => {
        async function fetchData() {

            console.log(idDatosInteresado);
            setNombreUsuario(getUser().nombre);
            DatosInteresadoApi.getAllDatosInteresadoById(idDatosInteresado).then(
                datos => {

                    console.log("==============================================");
                    console.log(datos);
                    console.log("==============================================");

                    if (!datos.error) {

                        if(datos.data[0].attributes.estado==true){
                            setEstado("inactiva");
                        }

                        setDatosInteresado(datos);
                        setRtaInfoDatos(true);
                    }
                    else{
                        setErrorApi(datos.error.toString())
                        window.showModal()

                    }
                      
                }
            )

        }
        fetchData();
    }, []);


    const enviarDatos = (datos) => {
        window.showSpinner(true);

        const data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": "",
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "update_user": nombreUsuario,
                    "update_at": Date.now(),
                    "estado_observacion": datos.descripcion,
                    "estado": true,
                }
            }
        }

        AntecedentesApi.updateAntecedente(data, antecedentesId).then(
            datos => {
                if (!datos.error) {
                    from.cambiaColorAntecedentes = "btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left";
                    window.showModal(2);
                    setIsRedirect(true);
                    from.estadoActividad = true;
                }
                else {
                    setErrorApi(datos.error.toString());
                    window.showModal(1);
                }
                window.showSpinner(false);
            }
        )
    }


    const CargarInfoDocumentoRadicado = () => {

        if (getDatosInteresado.data != null && typeof (getDatosInteresado.data) != 'undefined') {
            return (
                <tr>
                    <td style={{width: "15%"}}>
                        {getDatosInteresado.data[0].attributes.created_user}
                    </td>  
                    <td style={{width: "15%"}}>{getDatosInteresado.data[0].attributes.created_at}</td>
                    <td style={{width: "50%"}}>
                        {getDatosInteresado.data[0].attributes.primer_apellido +' '+
                        getDatosInteresado.data[0].attributes.segundo_apellido +' '+
                        getDatosInteresado.data[0].attributes.primer_nombre +' '+
                        getDatosInteresado.data[0].attributes.segundo_nombre}
                    </td>
                    <td>{getDatosInteresado.data[0].attributes.entidad}</td>
                </tr>

            )
        }

    }


    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
        
            {<InfoErrorApi error={getErrorApi} />}
            {<InfoExitoApi />}
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    setCountTextArea(valores.descripcion.length)
                    if (!valores.descripcion) {
                        errores.descripcion = 'Debe ingresar un antecedente'
                    }
                    else if (valores.descripcion.length < 10) {
                        errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                    }
                    else if (valores.descripcion.length > 4000) {
                        errores.descripcion = 'Debe ingresar mínimo 10 caracteres'
                    }
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="col-md-12">
                            <div className="block block-rounded block-bordered">
                                <div className="block-content">
                                    <nav aria-label="breadcrumb">
                                        <ol className="breadcrumb breadcrumb-alt push">
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from}}><small>Ramas del proceso</small></Link></li>
                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/DatosInteresadoLista/`} state={{ from: from}}><small>Datos del interesado</small></Link></li>
                                            <li class="breadcrumb-item"> <small>Cambiar estado de datos del interesado</small></li>
                                        </ol>
                                    </nav>
                                </div>
                            </div>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No {radicado} :: Datos del interesado</h3>
                            </div>
                            <div className="block-content">

                            
                            <div className='col-md-12'>

                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                    <thead>
                                        <tr>
                                            <th>Registrado por</th>
                                            <th>Fecha registro</th>
                                            <th>Persona</th>
                                            <th>Entidad</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                    {getRtaInfoDatos ? CargarInfoDocumentoRadicado() : null}

                                    </tbody>
                                </table>
                            </div>
                             



                                <div className="col-md-12">
                                    <div className="form-group">
                                        <label htmlFor="descripcion">Motivo por el que {getEstado} el registro del interesado <span className="text-danger">*</span></label>
                                        <Field as="textarea" className="form-control" id="descripcion" name="descripcion" rows="4" placeholder="Escriba en este espacio la observación"></Field>
                                        <div className="text-right">
                                            <span className="text-primary">{countTextArea} / 4000</span>
                                        </div>
                                        <ErrorMessage name="descripcion" component={() => (<span className="text-danger">{errors.descripcion}</span>)} />
                                    </div>
                                </div>
                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>

                                <Link to={`/DatosInteresadoLista/`} state={{ from: from}}>
                                    <button type="button" className="btn btn-danger"><i class="fas fa-window-close"></i> Cancelar</button>
                                </Link>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>
    );
}


export default DatosInteresadoCambiarEstadoForm;
