import React, { useEffect, useState } from 'react';
import { useLocation, Link } from 'react-router-dom';
import { hasAccess } from '../../Utils/Common';
import Spinner from '../../Utils/Spinner';
import GenericApi from '../../Api/Services/GenericApi';

function ListaAccionesForm() {
    const location = useLocation();
    const { from, selected_id_etapa, id_actuacion } = location.state;

    let idActu = id_actuacion != null ? id_actuacion : null;

    const [getTextoTransaccion1, setTextoTransaccion1] = useState("");
    const [getTextoTransaccion2, setTextoTransaccion2] = useState("");
    const [getTextoTransaccion3, setTextoTransaccion3] = useState("");
    const [getTextoTransaccion4, setTextoTransaccion4] = useState("");
    const [getTextoTransaccion5, setTextoTransaccion5] = useState("");
    const [getTextoTransaccion6, setTextoTransaccion6] = useState("");
    const [getTextoTransaccion7, setTextoTransaccion7] = useState("");
    const [getNombreProceso, setNombreProceso] = useState('');
    const [getRtaJefe, setRtaJefe] = useState(false);


    //Funcion principal
    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            if (idActu != null) {
                datosTransacciones(idActu);
            }
            nombreProceso();
        }
        fetchData();
    }, []);


    const nombreProceso = () => {

        GenericApi.getByIdGeneric("nombre-proceso",from.procesoDisciplinarioId).then(
            datos => {
                if (!datos.error) {
                    setNombreProceso(datos.data.attributes.nombre);
                    jefeDependencia();
                }
            }
        )
    }

    // Se consulta si el usuario que se encuentra en sesi√≥n es jefe dependencia
    const jefeDependencia = () => {

        const data = {
            "data": {
                "type": "usuario",
                "attributes": {
                    "": ""
                }
            }
        }
        GenericApi.getByDataGeneric("usuario/get-jefe-dependencia", data).then(
            datos => {
                // console.log(datos);
                if (!datos.error) {
                    setRtaJefe(true);
                } else {
                    setRtaJefe(false);
                }
                window.showSpinner(false);
            }
        )
    }

    const datosTransacciones = (id) => {
        GenericApi.getGeneric('mas_actuaciones/' + id).then(
            datos => {
                if (!datos.error) {
                    setTextoTransaccion1(datos.data.attributes.texto_dejar_en_mis_pendientes);
                    setTextoTransaccion2(datos.data.attributes.texto_enviar_a_alguien_de_mi_dependencia);
                    setTextoTransaccion3(datos.data.attributes.texto_enviar_a_jefe_de_la_dependencia);
                    setTextoTransaccion4(datos.data.attributes.texto_enviar_a_otra_dependencia);
                    setTextoTransaccion5(datos.data.attributes.texto_regresar_proceso_al_ultimo_usuario);
                    setTextoTransaccion6(datos.data.attributes.texto_enviar_a_alguien_de_secretaria_comun_aleatorio);
                    setTextoTransaccion7(datos.data.attributes.texto_enviar_a_alguien_de_secretaria_comun_dirigido);
                }
                else {
                    // console.log("Error en la consulta de la los datos de las transacciones");
                }

            }
        )
    }

    const handleClicArchivoManual = () => {
        try {
            window.showSpinner(true);

            const data = {
                "data": {
                    "type": "mas_actuaciones",
                    "attributes": {
                        "nombre_actuacion": "",
                        "nombre_plantilla": "",
                        "id_etapa": "",
                        "estado": "",
                        "id_etapa_despues_aprobacion": "",
                        "despues_aprobacion_listar_actuacion": "",
                        "nombre_plantilla_manual": "",
                        "fileBase64": "",
                        "fileBase64_manual": ""
                    }
                }
            }

            GenericApi.getByDataGeneric('mas_actuaciones/manual/' + idActu, data).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64FileManual(datos.content_type, datos.base_64, datos.file_name);
                    }
                    else {

                    }
                    window.showSpinner(false);
                }
            )

        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64FileManual(contentType, base64Data, fileName) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    return (

        <>
            {<Spinner />}

            <div className="w2d_block">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb breadcrumb-alt push">
                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                        <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }}><small>Actuaciones</small></Link></li>
                        <li class="breadcrumb-item"> <small>Transacciones</small></li>
                    </ol>
                </nav>
            </div>

            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">{getNombreProceso} :: <strong>Transacciones</strong></h3>
                </div>
                <div className="block-content row">
                    <table className="table table-striped table-borderless table-vcenter">
                        <thead className="thead-light">
                            <tr>
                                <th colSpan="2">
                                    {idActu != null ? (
                                        "En el siguiente boton puedes descargar una guia :"
                                    ) : null}
                                    {idActu != null ? (

                                        <button type='button' title='Descargar documento' className='btn btn-rounded btn-primary ml-3' onClick={() => handleClicArchivoManual()}>
                                            Descargar Manual
                                        </button>

                                    ) : null}

                                    <div className="text-muted mt-2 mb-1">Donde deseas asignar el proceso con radicado {from.radicado} ?</div>
                                </th>
                            </tr>
                        </thead>
                        <tbody>

                            <tr>
                                <td className="text-center">
                                    <i className="fa fa-check text-success fa-2x"></i>
                                </td>
                                <td>
                                    <Link to={'/MisPendientes'} className="font-size-h5 font-w600">Dejar en mis pendientes</Link>
                                    <div className="text-muted mt-2 mb-1">{getTextoTransaccion1}</div>
                                </td>
                            </tr>
                            {(hasAccess('Transacciones', 'EnviarUsuarioDependencia')) ? (
                                <tr>
                                    <td className="text-center">
                                        <i className="fa fa-user text-success fa-2x"></i>
                                    </td>
                                    <td>
                                        <Link to={'/EnviaraAlguienDeMiDependencia'} state={{ from: from, selected_id_etapa: selected_id_etapa }} className="font-size-h5 font-w600" >Enviar a alguien de mi dependencia</Link>
                                        <div className="text-muted mt-2 mb-1">{getTextoTransaccion2}</div>
                                    </td>
                                </tr>
                            ) : null}
                            {(hasAccess('Transacciones', 'EnviarAJefe')) && getRtaJefe == false ? (
                                <tr>
                                    <td className="text-center">
                                        <i className="fa fa-check-double text-success fa-2x"></i>
                                    </td>
                                    <td>
                                        <Link to={`/EnviarAlJefeDeMiDependencia`} state={{ from: from, selected_id_etapa: selected_id_etapa }} className="font-size-h5 font-w600" >Enviar a jefe de la dependencia</Link>
                                        <div className="text-muted mt-2 mb-1">{getTextoTransaccion3}</div>
                                    </td>
                                </tr>
                            ) : null}
                            {(hasAccess('Transacciones', 'EnviarOtraDependencia')) && getRtaJefe == true ? (
                                <tr>
                                    <td className="text-center">
                                        <i className="fa fa-paper-plane text-success fa-2x"></i>
                                    </td>
                                    <td>
                                        <Link to={`/EnviarAotraDependencia`} state={{ from: from, selected_id_etapa: selected_id_etapa }} className="font-size-h5 font-w600" >Enviar a otra dependencia</Link>
                                        <div className="text-muted mt-2 mb-1">{getTextoTransaccion4}</div>
                                    </td>
                                </tr>
                            ) : null}
                            {(hasAccess('Transacciones', 'RegresarUltimoUsuario')) ? (
                                <tr>
                                    <td className="text-center">
                                        <i className="fa fa-undo-alt text-success fa-2x"></i>
                                    </td>
                                    <td>
                                        <Link to={'/EnviarAlAnteriorUsuario'} state={{ from: from, selected_id_etapa: selected_id_etapa }} className="font-size-h5 font-w600" >Regresar proceso al √∫ltimo usuario</Link>
                                        <div className="text-muted mt-2 mb-1">{getTextoTransaccion5}</div>
                                    </td>
                                </tr>
                            ) : null}
                            {(hasAccess('Transacciones', 'EnviarDirigidoSecretariaComun')) ? (
                                <tr>
                                    <td className="text-center">
                                        <i className="fa fa-arrow-right text-success fa-2x"></i>
                                    </td>
                                    <td>
                                        <Link to={'/EnviaraAlguienDeSecretariaComunDirigido'} state={{ from: from, selected_id_etapa: selected_id_etapa }} className="font-size-h5 font-w600" >Enviar a alguien de secretaria com√∫n dirigidamente</Link>
                                        <div className="text-muted mt-2 mb-1">{getTextoTransaccion6}</div>
                                    </td>
                                </tr>
                            ) : null}
                            {(hasAccess('Transacciones', 'EnviarAleatorioSecretariaComun')) ? (
                                <tr>
                                    <td className="text-center">
                                        <i className="fa fa-arrows-alt text-success fa-2x"></i>
                                    </td>
                                    <td>
                                        <Link to={'/EnviaraAlguienDeSecretariaComunAleatorio'} state={{ from: from, selected_id_etapa: selected_id_etapa }} className="font-size-h5 font-w600" >Enviar a alguien de secretaria com√∫n aleatoriamente</Link>
                                        <div className="text-muted mt-2 mb-1">{getTextoTransaccion7}</div>
                                    </td>
                                </tr>
                            ) : null}
                        </tbody>
                    </table>
                </div>
            </div>


        </>
    );
}

export default ListaAccionesForm;
