import React, { useEffect, useState } from 'react';
import Spinner from '../../Utils/Spinner';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { Link } from "react-router-dom";
import GenericApi from '../../Api/Services/GenericApi';
import { useLocation } from 'react-router-dom';
import { getUser } from '../../../components/Utils/Common';

function ActuacionesAprobacionRechazar() {

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [countTextArea, setCountTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getIdDependenciaAtiendaAnulaciones, setIdDependenciaAtiendaAnulaciones] = useState(0);
    const [getNameUsuario, setNameUsuario] = useState("");
    const [getIdActuacion, setIdActuacion] = useState("");
    const [getNombreUsuario, setNombreUsuario] = useState("");
    const location = useLocation()
    const { from, selected_id_etapa, id, nombre, estadoActualActuacion, titulo, valor } = location.state;

    let procesoDisciplinarioId = from.procesoDisciplinarioId;

    console.log("titulo -> ", titulo);
    console.log("uuid actuacion -> ", id);
    console.log("nombre actuacion -> ", nombre);
    console.log("valor actuacion -> ", valor);
    console.log("procesoDisciplinarioId -> ", procesoDisciplinarioId);

    const [getRoutes, setRoutes] = useState({
        ver_detalle: "/ActuacionesVer/" + from.procesoDisciplinarioId + "/" + selected_id_etapa + "/1"
    });

    useEffect(() => {
        async function fetchData() {
            obtenerParametros();
        }
        fetchData();
    }, []);

    function containsSpecialChars(str) {

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }

    const obtenerParametros = () => {

        window.showSpinner(true);

        try {
            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            // Buscamos el parametro
            GenericApi.getByDataGeneric("parametro/parametro-nombre", data).then(
                datos => {

                    window.showSpinner(false);

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        setModalState({ title: "Actuaciones", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }

    const enviarDatos = (valores) => {

        window.showSpinner(true);

        let data = {
            "data": {
                "type": "trazabilidad-actuaciones",
                "attributes": {
                    "uuid_actuacion": id,
                    "id_estado_actuacion": valor == 1 ? 1 : 2,
                    "observacion": valores.informacion,
                    "estado": 1,
                    "envia_correo": 1,
                    "id_proceso_disciplinario": procesoDisciplinarioId
                }
            }
        }

        GenericApi.addGeneric('trazabilidad-actuaciones', data).then(
            datos => {
                if (!datos.error) {
                    if (!datos.error) {
                        window.showSpinner(false);
                        let validacion = (valor == 1 ? 'aprobada' : 'rechazada');
                        let mensaje = 'La actuación ' + nombre + ' ha sido ' + validacion + ' con éxito.';
                        setModalState({ title: "SINPROC No " + from.radicado + " :: Actuaciones Aprobar/Rechazar", message: mensaje, show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.EXITO });
                    } else {
                        setModalState({ title: "SINPROC No " + from.radicado + " :: Actuaciones Aprobar/Rechazar", message: datos.error.toString(), show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            }
        )
    }

    const guardarTrazabilidadActuaciones = () => {
        let data = {
            "data": {
                "type": "trazabilidad-actuaciones",
                "attributes": {
                    "uuid_actuacion": id,
                    "id_estado_actuacion": 2,
                    "observacion": "Rechazada",
                    "estado": 1,
                }
            }
        }

        GenericApi.addGeneric('trazabilidad-actuaciones', data).then(
            datos => {
                if (!datos.error) {
                    cambiarEstadoEnActuaciones();
                    window.showSpinner(false);
                    setModalState({ title: "SINPROC No " + from.radicado + " :: Enviar solicitud de anulación", message: 'El proceso con radicado: ' + from.radicado + ' es enviado por solicitud de anulación al usuario: ' + getNameUsuario, show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.EXITO });
                }
                else {
                    setModalState({ title: "SINPROC No " + from.radicado + " :: Enviar solicitud de anulación", message: datos.error.toString(), show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.ERROR });
                }
            }
        )
    }

    const cambiarEstadoEnActuaciones = () => {
        let data = {
            "data": {
                "type": "actuaciones",
                "attributes": {
                    "id_actuacion": getIdActuacion,
                    "usuario_accion": getNombreUsuario,
                    "id_estado_actuacion": 4,
                    "documento_ruta": null,
                    "estado": 1,
                    "fileBase64": null,
                    "nombre_archivo": null,
                    "ext": null,
                    "peso": null,
                    "id_proceso_disciplinario": from.procesoDisciplinarioId,
                    "id_etapa": selected_id_etapa
                }
            }
        }
        GenericApi.updateGeneric("actuaciones", id, data);
    }

    const obtenerTodosLosDatosDeLaActuacion = () => {
        GenericApi.getGeneric("actuaciones/" + id).then(
            datos => {

                if (!datos.error) {
                    setIdActuacion(datos.data.attributes.id_actuacion);
                } else {

                }
            }
        )
    }

    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            <Formik
                initialValues={{
                    informacion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    setCountTextArea(valores.informacion.length)
                    let errores = {}
                    if (!valores.informacion) {
                        errores.informacion = 'Debe ingresar un valor';
                    }
                    else if (valores.informacion.length <= getMinimoTextArea) {
                        errores.informacion = 'La descripción debe tener almenos ' + getMinimoTextArea + ' caracteres';
                    }
                    if (valores.informacion) {
                        if (containsSpecialChars(valores.informacion))
                            errores.informacion = 'Tiene caracteres inválidos';
                    }

                    return errores;
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`}><small>Ramas del proceso</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }}><small>Actuaciones</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={getRoutes.ver_detalle} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion }}><small>Actuacion {nombre}</small></Link></li>
                                    <li className="breadcrumb-item"> <small>Actuaciones Aprobar/Rechazar</small></li>
                                </ol>
                            </nav>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Actuación :: {titulo} </h3>
                            </div>
                            <div className="block-content">
                                <div className="row">
                                    <div className="col-md-12 mt-2">
                                        <div className="form-group">
                                            <label htmlFor="informacion">Motivo porqué se solicita {valor == 1 ? "aprobación" : "rechazo"} <span className="text-danger mt-2">*</span></label>
                                            <Field as="textarea" className="form-control mt-2" id="informacion" name="informacion" rows="6" placeholder="Información para su solicitud...."
                                                maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                            <div className="text-right">
                                                <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                            </div>
                                            <ErrorMessage name="informacion" component={() => (<span className="text-danger">{errors.informacion}</span>)} />
                                        </div>
                                    </div>
                                </div>
                                <div className="block-content block-content-full text-right">
                                    <button type="submit" className="btn btn-primary" >Aceptar</button>
                                    <Link to={getRoutes.ver_detalle} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion }} className="font-size-h5 font-w600" >
                                        <button type="button" className="btn btn-danger" >Cancelar</button>
                                    </Link>
                                </div>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>


    );
}

export default ActuacionesAprobacionRechazar;
