import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import { Link } from "react-router-dom";
import DatePicker from 'react-datetime';
import 'react-datetime/css/react-datetime.css';
import moment from 'moment';
import 'moment/locale/es';
import { getUser } from '../../../components/Utils/Common';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import DatePerson from "../../DatePerson/DatePerson";
import { hasAccess } from '../../../components/Utils/Common';
import ClasificacionRadicado from './ClasificacionRadicado';


function ClasificacionRadicadoForm() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [listaTipoExpediente, setListaTipoExpediente] = useState({ data: {} });

    const [respuestaTipoExpediente, setRespuestaTipoExpediente] = useState(false);
    const [selectedTipoExpediente, setSelectedTipoExpediente] = useState("");


    const [countTextArea, setCountTextArea] = useState(0);

    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);

    const [listaTipoDerechoPeticion, setListaDerechoPeticion] = useState({ data: {} });
    const [selectedTipoDerechoPeticion, setSelectedDerechoPeticion] = useState("");
    const [respuestaTipoDerechoPeticion, setRespuestaDerechoPeticion] = useState(false);

    const [listaTipoRespuesta, setListaTipoRespuesta] = useState({ data: {} });
    const [selectedTipoRespuesta, setSelectedTipoRespuesta] = useState("");
    const [respuestaTipoRespuesta, setRespuestaTipoRespuesta] = useState(false);

    const [listaTerminosRespuesta, setListaTerminosRespuesta] = useState({ data: {} });
    const [selectedTerminosRespuesta, setSelectedTerminosRespuesta] = useState("");
    const [respuestaTerminosRespuesta, setRespuestaTerminosRespuesta] = useState(false);

    const [listaTiposQueja, setListaTiposQueja] = useState({ data: {} });
    const [selectedTiposQueja, setSelectedTiposQueja] = useState("");
    const [respuestaTiposQueja, setRespuestaTiposQueja] = useState(false);

    const [fechaTermino, setFechaTermino] = useState(null);
    const [resultDiasNoLaborales, setResultDiasNoLaborales] = useState([]);

    const [getNombreUsuario, setNombreUsuario] = useState("");
    const [getNombreRol, setNombreRol] = useState("");
    const [getAnosAtrasInvalidos, setAnosAtrasInvalidos] = useState(0);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;


    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/ClasificacionRadicadoLista/`} state={{ from: from }} />;
    }

    function containsSpecialChars(str) {
        const specialChars = `\`!@#$%^&*()_+\-=\[\]{};':"\\|<>\/?~`;

        const result = specialChars.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }

    useEffect(() => {
        async function fetchData() {

            //window.showSpinner(true);
            setNombreUsuario(getUser().nombre);
            setNombreRol(getUser().roles);

        }
        fetchData();
    }, []);


    const obtenerParametros = () => {
        try {


            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "limite_años_calendario|minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            ParametrosMasApi.getParametroPorNombre(data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('limite_años_calendario')).map(filteredName => (
                                setAnosAtrasInvalidos(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        window.showModal(1);
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }

    const disableCustomDt = current => {
        return !resultDiasNoLaborales.includes(current.format('YYYY-MM-DD'));
    }



    let selectChangeTipoRespuesta = (e) => {
        setSelectedTipoRespuesta(e);
    }

    let selectChangeTipoExpediente = (e) => {
        setCountTextArea(0)
        setSelectedTipoExpediente(e);
    }

    let selectChangeTipoDerechoPeticion = (e) => {
        setCountTextArea(0)
        setSelectedDerechoPeticion(e);
    }

    let selectChangeTerminoRespuesta = (e) => {
        setSelectedTerminosRespuesta(e);
    }

    let selectChangeTiposQueja = (e) => {
        setSelectedTiposQueja(e);
    }


    //Componetes










    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            <Spinner />
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <div className="row">

                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page" to={`/ClasificacionRadicadoLista/`} state={{ from: from }}><small>Clasificacion del radicado Lista</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Nueva clasificacion del radicado</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                { }

                <div className="col-md-12">
                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title"> SINPROC No {radicado} :: <strong>Crear Clasificación del Radicado</strong></h3>
                        </div>

                        <ClasificacionRadicado getIdProcesoDisciplinario={procesoDisciplinarioId} id="cr_componente" name="cr_componente" />

                    </div>
                </div>
            </div>

        </>
    );

}
export default ClasificacionRadicadoForm;
