import React, { useEffect, useState } from 'react';
import Spinner from '../../Utils/Spinner';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { Link } from "react-router-dom";
import GenericApi from '../../Api/Services/GenericApi';
import { useLocation } from 'react-router-dom';
import { getUser } from '../../../components/Utils/Common';

function AgregarUsuarioParaFirmaMecanica() {

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getListaUsuarios, setListaUsuarios] = useState({ data: {} });
    const [getUsuarioSeleccionado, setUsuarioSeleccionado] = useState();
    const [getTipoFirmaSeleccionada, setTipoFirmaSeleccionada] = useState();
    

    const location = useLocation()
    const { from, selected_id_etapa, id, nombre, estadoActualActuacion, titulo, valor, solicitante, tipoActuacion } = location.state;
    
    const [getRoutes, setRoutes] = useState({
        ver_detalle: "/ActuacionesVer/" + from.procesoDisciplinarioId + "/" + selected_id_etapa + "/1"
    });

    useEffect(() => {
        async function fetchData() {
            allUsers();
        }
        fetchData();
    }, []);


    const allUsers = () =>{

        GenericApi.getGeneric('usuario').then(
            datos => {
                if (!datos.error) {
                    setListaUsuarios(datos);
                }
            }
        )
    }

    const enviarDatos = (valores) => {

        let data = {
            "data":{
                "type": "Agregar Usuario",
                "attributes": {
                    "id_actuacion": id,
                    "id_user": getUsuarioSeleccionado,
                    "tipo_firma": getTipoFirmaSeleccionada,
                    "uuid_proceso_disciplinario": from.procesoDisciplinarioId
                }
            }
        }


        GenericApi.addGeneric("actuaciones/agregar-usuario-para-firma-mecanica", data).then(
            datos => {
                if (!datos.error && datos.data) {
                    setModalState({ title: "SINPROC No " + from.radicado + " :: Agregar usuario para firma", message: 'Registro Exitoso', show: true, redirect: "/ActuacionesVer/"+id+"/"+selected_id_etapa+"/1", from: { from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion  }, alert: global.Constants.TIPO_ALERTA.EXITO });
                } else {
                    setModalState({ title: "SINPROC No " + from.radicado + " :: Agregar usuario para firma", message: 'Registro Fallido: '+datos, show: true, redirect: "/ActuacionesVer/"+id+"/"+selected_id_etapa+"/1", from: { from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion  }, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
            }
        )
    
    }

    const selectChangelistarUsuarios = (e) => {
        setUsuarioSeleccionado(e.target.value);
    }


    const selectChangeTipoFirma = (e) => {
        setTipoFirmaSeleccionada(e.target.value);
    }

    const selectUsuario = () => {
        return(
            getListaUsuarios.data.map((user, i) => {
                if (user.attributes.estado == 1) {
                    return(
                        <option key={user.id} value={user.id}>{user.attributes.nombre +" "+ user.attributes.apellido +" ("+ user.attributes.name + ")"}</option>
                    );
                }
            })
        );
    }

    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            <Formik
                initialValues={{

                }}
                enableReinitialize
                validate={(valores) => {
                    
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`}><small>Ramas del proceso</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }}><small>Actuaciones</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={getRoutes.ver_detalle} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion }}><small>Actuacion {nombre}</small></Link></li>
                                    <li className="breadcrumb-item"> <small>{tipoActuacion == 0 ? "Actuaciones" : (tipoActuacion == 1 ? 'Impedimentos' : 'Comisorio')} Aprobar/Rechazar</small></li>
                                </ol>
                            </nav>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">{tipoActuacion == 0 ? "Actuacion" : (tipoActuacion == 1 ? 'Impedimento' : 'Comisorio')} {nombre} :: {titulo} </h3>
                            </div>
                            <div className="block-content">
                                <div className="row">
                                    
                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor="listarUsuarios">Usuario ? <span className="text-danger">*</span></label>
                                            <Field as="select" className="form-control" id="listarUsuarios" name="listarUsuarios" value={getUsuarioSeleccionado} onChange={selectChangelistarUsuarios}>
                                                <option value="">Selecciona</option>
                                                {getListaUsuarios.data.length >= 1 ? selectUsuario() : null}
                                            </Field>
                                            <ErrorMessage name="listarUsuarios" component={() => (<span className="text-danger">{errors.listarUsuarios}</span>)} />
                                        </div>
                                    </div>

                                    <div className="col-md-12">
                                        <div className="form-group">
                                            <label htmlFor="listarTipoFirma">Tipo firma ? <span className="text-danger">*</span></label>
                                            <Field as="select" className="form-control" id="listarTipoFirma" name="listarTipoFirma" value={getTipoFirmaSeleccionada} onChange={selectChangeTipoFirma}>
                                                <option value="">Selecciona</option>
                                                <option value="1">Principal</option>
                                                <option value="2">Firm칩</option>
                                                <option value="3">Elabor칩</option>
                                            </Field>
                                            <ErrorMessage name="listarTipoFirma" component={() => (<span className="text-danger">{errors.listarTipoFirma}</span>)} />
                                        </div>
                                    </div>

                                    <div className="col-md-12
                                    ">
                                        <div className="form-group">
                                            <label htmlFor="listarActuaciones">* El usuario agregado recibir치 una notificaci칩n por correo para que pueda revisar y firmar el documento</label>
                                        </div>
                                    </div>

                                </div>
                                <div className="block-content block-content-full text-right">
                                    <button type="submit" className="btn btn-primary" >Aceptar</button>
                                    <Link to={getRoutes.ver_detalle} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion }} className="font-size-h5 font-w600" >
                                        <button type="button" className="btn btn-danger" >Cancelar</button>
                                    </Link>
                                </div>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>


    );
}

export default AgregarUsuarioParaFirmaMecanica;
