import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
//import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import { Link } from "react-router-dom";
import ParametrosMasApi from '../Api/Services/ParametrosMasApi';
import UsuarioPermitidoApi from '../Api/Services/UsuarioPermitidoApi';
import moment from 'moment';
import { Navigate } from "react-router-dom";
import { useParams } from "react-router";
import Autocomplete from "../Autocomplete/Autocomplete";
import Spinner from '../Utils/Spinner';






function AdministracionUsuarioLista() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [respuesUsuarioPermitido, setRespuesUsuarioPermitido] = useState(false);
    const [usuarioPermitidoLista, setUsuarioPermitidoLista] = useState({ data: [], links: [], meta: [] });

    const [respuestaPermitidasTotal, setRespuestaUsuarioPermitidoTotal] = useState(false);
    const [usuarioPermitidoListaTotal, setUsuarioPermitidoListaTotal] = useState({ data: [], links: [], meta: [] });

    const redirectToRoutes = () => {
        return <Navigate to={`/AdministracionUsuarioLista/`} />;
    }

    useEffect(() => {
        window.showSpinner(true);
        async function fetchData() {

            //window.showSpinner(true);


            ParametrosMasApi.getAllUsuarios().then(
                datos => !datos.error ? (setUsuarioPermitidoListaTotal(datos), setRespuestaUsuarioPermitidoTotal(true),
                    setUsuarioPermitidoLista(datos), setRespuesUsuarioPermitido(true), window.showSpinner(false)) : window.showModal(1)
            )
        }
        fetchData();
    }, []);




    const enviarDatos = (estado, id) => {

        try {


            //actualizamos el estado
            window.showSpinner(true);
            const data = {
                "data": {
                    "type": "mas_entidad_permitida",
                    "attributes": {
                        "estado": estado
                    }
                }
            }


            UsuarioPermitidoApi.updateUsuarioPermitido(data, id).then(
                datos => {

                    if (!datos.error) {
                        ParametrosMasApi.getAllUsuarios().then(
                            datos => !datos.error ? (setUsuarioPermitidoListaTotal(datos), setRespuestaUsuarioPermitidoTotal(true),
                                setUsuarioPermitidoLista(datos), setRespuesUsuarioPermitido(true), window.showSpinner(false)) : window.showModal(1)
                        )
                        //window.showModal(2)
                        //setIsRedirect(true);
                    }
                    else {
                        //console.log("hay error");
                        console.log(datos.error);
                        window.showSpinner(false)
                    }
                    //window.showSpinner(false);
                }
            )

        } catch (error) {
            window.showSpinner(false);
        }
    }

    const listaUsuarioPermitido = () => {

        if (usuarioPermitidoLista.data != null && typeof (usuarioPermitidoLista.data) != 'undefined') {
            return (


                usuarioPermitidoLista.data.map((entidadPerm, i) => {
                    return cargarTabla(entidadPerm, i)
                })
            )
        }
        else {
            return (

                usuarioPermitidoLista.map((entidadPerm, i) => {
                    return cargarTabla(entidadPerm, i)
                })
            )
        }

    }

    const cargarTabla = (entidadPerm) => {
        return (
            <tr key={entidadPerm.id}>
                <td>{entidadPerm.attributes.id_entidad + " " + entidadPerm.attributes.nombre_entidad}</td>
                <td>{moment(entidadPerm.attributes.created_at).format("DD/MM/YYYY")}</td>
                <td>{entidadPerm.attributes.estado == '1' ? 'Activo' : 'Inactivo'}</td>
                <td>
                    {
                        (entidadPerm.attributes.estado == 0) ? (
                            <button type='button' title='Activar' onClick={() => enviarDatos(1, entidadPerm.id)} className='btn btn-sm btn-success'><span className="fas fa-toggle-off"> </span></button>
                        ) : null
                    }

                    {
                        (entidadPerm.attributes.estado == 1) ? (
                            <button type='button' title='Inacivar' onClick={() => enviarDatos(0, entidadPerm.id)} className='btn btn-sm btn-danger'><span className="fas fa-toggle-off"> </span></button>
                        ) : null
                    }
                </td>
            </tr>
        )
    }


    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setUsuarioPermitidoLista(usuarioPermitidoListaTotal);
            }
            else {

                setUsuarioPermitidoLista(usuarioPermitidoListaTotal.data.filter(
                    suggestion => ((suggestion.attributes.id + suggestion.attributes.name
                        + suggestion.attributes.created_at).toLowerCase().includes(event.target.value.toLowerCase()))
                ));
            }
        } catch (error) {
            console.error(error);
        }

    };

    return (
        <>
            {<Spinner />}
           
            {<InfoExitoApi />}
            <Formik>
                <Form>
                    <div className="block block-themed">
                        <div className="block-header">

                            <h3 className="block-title">Usurarios</h3>
                            <Link to={`/AdministracionUsuarioForm/`}>
                                <button type="button" className="btn btn-sm btn-dark mr-1 mb-3"><i class="far fa-plus-square"></i> Agregar Usuario</button>
                            </Link>

                        </div>
                        <div className="block-content">
                            <div className='row'>

                                <div className='col-md-12'>
                                    <div className="form-group ">
                                        <label htmlFor='search'>Buscar: </label>
                                        <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                    </div>
                                </div>
                            </div>
                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>
                                        <th className="text-center">Nombres y Apellidos</th>
                                        <th className="text-center">Dependencia origen</th>
                                        <th className="text-center">Roles</th>
                                        <th className="text-center">Estado</th>
                                        
                                    </tr>
                                </thead>
                                <tbody>
                                    {respuesUsuarioPermitido ? listaUsuarioPermitido() : null}
                                </tbody>
                            </table>
                        </div>


                    </div>
                </Form>
            </Formik>
        </>
    )

}

export default AdministracionUsuarioLista;
