import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';


function ModalCoincidenciasUsuarios(props) {

    const [getCoincidenciasListaSearch, setCoincidenciasListaSearch] = useState([]);
    const [getCoincidenciasListaTotal, setCoincidenciasListaTotal] = useState([]);

    useEffect(() => {

        setCoincidenciasListaSearch(props.usuarios);
        setCoincidenciasListaTotal(props.usuarios);
    })

    const handleSearch = (event) => {

        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setCoincidenciasListaSearch(getCoincidenciasListaTotal);
            }
            else {

                setCoincidenciasListaSearch(getCoincidenciasListaTotal.data.filter(
                    suggestion => ((suggestion.attributes.primer_apellido
                        + suggestion.attributes.segundo_apellido + suggestion.attributes.primer_nombre +
                        suggestion.attributes.segundo_nombre + suggestion.attributes.nombre_entidad
                        + suggestion.attributes.created_at + suggestion.attributes.created_user
                    ).toLowerCase().includes(event.target.value.toLowerCase()))
                ));
            }
        } catch (error) {
            console.error(error);
        }

    };

    const listarCoincidencias = () => {


        if (getCoincidenciasListaSearch != null && typeof (getCoincidenciasListaSearch) != 'undefined') {
            return (

                getCoincidenciasListaSearch.map((coincidencia, i) => {
                    return (
                        cuerpoTabla(coincidencia)
                    )
                })
            )
        }
        else {
            return (

                getCoincidenciasListaSearch.map((coincidencia, i) => {
                    return (
                        cuerpoTabla(coincidencia)
                    )
                })
            )
        }

    }

    const seleccionarUsuario = (e) => {
        props.parentCallback(e);
    }

    const cuerpoTabla = (coincidencia) => {
        return (

            <tr key={coincidencia.name}>
                <td>{coincidencia.nombre}</td>
                <td>{coincidencia.apellido}</td>
                <td>{coincidencia.email}</td>
                <td>{coincidencia.name}</td>

                <td> <button type='button' data-dismiss="modal" className='btn btn-sm btn-primary' onClick={() => seleccionarUsuario(coincidencia)}>Asignar</button></td>
            </tr>
        )
    }


    return (
        <>
            <Formik
                initialValues={{
                    informacion: '',
                }}
                enableReinitialize
                validate={(valores) => {

                    let errores = {}

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {

                    //enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="modal fade" id="modal-block-popout-coincidencias-usuario" tabIndex="-1" role="dialog" aria-labelledby="modal-block-popout" aria-hidden="true">
                            <div className="modal-dialog modal-lg" role="document" >
                                <div className="modal-content" >
                                    <div className="block block-themed block-transparent mb-0">
                                        <div className="block-header bg-primary-dark">
                                            <h3 className="block-title">Seleccionar usuario</h3>

                                            <div className="block-options">
                                                <button type="button" className="btn-block-option" data-dismiss="modal" aria-label="Close">
                                                    <i className="fa fa-fw fa-times"></i>
                                                </button>
                                            </div>
                                        </div>
                                        <div className="block-content">
                                            <span>Se han encontrado las siguiente coincidencias, seleccione el usuario con el cual desea crear el registro</span>
                                            <div className='row'>

                                                <div className='col-md-12'>
                                                    <div className="form-group ">
                                                        <label htmlFor='search'>Buscar: </label>
                                                        <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                                    </div>
                                                </div>
                                            </div>
                                            <div className="row" >
                                                <div className="col-md-12" style={{ 'height': '300px', 'overflow': 'scroll', 'display': 'block' }}>
                                                    <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                        <thead>
                                                            <tr>
                                                                <th>Nombre</th>
                                                                <th>Apellido</th>
                                                                <th>Email</th>
                                                                <th>Usuario</th>
                                                                <th>Acciones</th>
                                                            </tr>
                                                        </thead>
                                                        <tbody >
                                                            {listarCoincidencias()}
                                                        </tbody>
                                                    </table>
                                                </div>


                                            </div>


                                        </div>

                                    </div>
                                </div>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );
}

export default ModalCoincidenciasUsuarios;
