import React, { useEffect, useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useLocation } from 'react-router-dom'
import LogApi from '../Api/Services/LogApi';
import moment from 'moment';
import { Link } from "react-router-dom";
import DataTable from 'react-data-table-component';
import GenericApi from '../Api/Services/GenericApi';
import { getUser } from '../../components/Utils/Common';

function LogProcesosAsignados() {

    const [errorApi, setErrorApi] = useState('');
    const [getSeach, setSeach] = useState('');

    const [getRespuestaLog, setRespuestaLog] = useState(false);
    const [getLogLista, setLogLista] = useState({ data: [], links: [], meta: [] });
    const paganationPerPages = process.env.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(process.env.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [getNombreUsuario, setNombreUsuario] = useState("");


    const columns = [

        {
            name: 'proceso',

            cell: log => <div>{log.attributes.funcionario_actual.name}<br />
                {log.attributes.dep_funcionario_actual.nombre}</div>,
            sortable: true,
            selector: log => log.attributes.funcionario_registra.name,
            width: "20%"
        },

        {
            name: 'Funcionario Actual',

            cell: log => <div>{log.attributes.funcionario_actual.name}<br />
                {log.attributes.dep_funcionario_actual.nombre}</div>,
            sortable: true,
            selector: log => log.attributes.funcionario_registra.name,
            width: "20%"
        },

        {
            name: 'Etapa',
            selector: log => log.attributes.etapa.nombre,
            sortable: true,
            width: "20%"
        },

        {
            name: 'Fecha',
            cell: log => log.attributes.created_at,
            selector: log => log.attributes.created_at,
            sortable: true,
            width: "10%"
        },
        
    ];


    useEffect(() => {
        async function fetchData() {

            setNombreUsuario(getUser().nombre);

            console.log("NOMBRE DE USUARIO: "+getUser().nombre);
            cargarProcesosAsignados(1, paganationPerPages, "1");

        }
        fetchData();
    }, []);


    const cargarProcesosAsignados = (page, perPage, estado) => {

        const data = {
            "data": {
                "type": "cierre_etapa",
                "attributes": {
                    "id_proceso_disciplinario": "id_proceso_disciplinario",
                    "id_etapa": "1",
                    "created_user": getUser().nombre,
                    'per_page': perPage,
                    'current_page': page
                }
            }
        }

        GenericApi.getByDataGeneric('cierre-etapa/procesos-diciplinario-asignados/', data).then(
            datos => {
                if (!datos.error) {
                    console.log(datos);
                    setLogLista(datos);
                    window.showSpinner(false);
                }
                else {
                    window.showSpinner(false);
                    window.showModal(1);
                }
            }
        )
    }


    return (
        <>
            <div className="block block-themed">
                <div className="block-header">
                    <h3 className="block-title">Procesos Gestionados </h3>
                </div>
                <div className="block-content">

                    <div className='row'>

                        <div className='col-md-3'>
                            <div className="form-group ">
                                
                            </div>
                        </div>


                    </div>

                    <div className='col-md-12'>

                        <div className='col-md-12 mt-2 mb-2'>
                            
                        </div>
                        
                    </div>
                </div>
            </div>
        </>
    );
}

export default LogProcesosAsignados;
