import React, { Component } from 'react';

class UsuarioFormEjemplo extends Component {

    constructor() {
        super();
        this.state = {
            nombre: '',
            apellido: '',
            registrado: '',
            cargo: 'Analista'
        };
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e) {
        e.preventDefault();
        this.props.onAddUsuario(this.state);
        this.setState({
            nombre: '',
            apellido: '',
            registrado: '',
            cargo: 'Analista'
        });
    }

    handleInputChange(e) {
        const {value, name} = e.target;
        console.log(value, name);
        this.setState({
            [name]: value
        });
    }

    render() {
        return(        
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div className="block block-rounded block-bordered">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">Registro Usuario</h3>
                            <div className="block-options">
                                <button type="button" className="btn-block-option">
                                    <i className="si si-settings"></i>
                                </button>
                            </div>
                        </div>
                        <div className="block-content">
                            <div className="row justify-content-center py-sm-3 py-md-5">
                                <div className="col-sm-10 col-md-8">
                                    <div className="form-group">
                                        <label>Nombre</label>
                                        <input type="text" name="nombre" onChange={this.handleInputChange} className="form-control" placeholder="Nombre" />
                                    </div>
                                    <div className="form-group">
                                        <label>Apellido</label>
                                        <input type="text" name="apellido" onChange={this.handleInputChange} className="form-control" placeholder="Apellido" />
                                    </div>
                                    <div className="form-group">
                                        <label>Cargo</label>
                                        <select name='cargo' className='form-control' onChange={this.handleInputChange}>
                                            <option> Analista </option>
                                            <option> Programador </option>
                                            <option> QA </option>
                                        </select>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="block-content block-content-full block-content-sm bg-body-light text-right">
                            <button type="submit" className="btn btn-sm btn-success">
                                <i className="fa fa-check"></i> Registrar
                            </button>
                        </div>
                    </div>
                </form>
            </div>
        )
       }

}

export default UsuarioFormEjemplo;
