import { createSchema, addTranslations, setLanguageByName, tr } from 'react-hook-form-auto'

export default function GetFaseModel(ddlData) {

    const model = createSchema('FaseModel', {
        nombre: {
            type: 'string',
            required: true,
            minLength: 2,
            maxLength: 255,
        },
        id_etapa: {
            type: 'select',
            options: ddlData['mas-etapa'],
            required: true,
        },
        estado: {
            type: 'select',
            required: true,
            options: [
                { value: '1', label: 'Activo' },
                { value: '0', label: 'Inactivo' }
            ],
        }
    });
    return model;
}

setLanguageByName('es')
addTranslations({
    models: {
        FaseModel: {
            nombre: 'Nombre',
            id_etapa: {
                _field: 'Etapa',
                _default: "Seleccione",
            },
            estado: {
                _field: 'Estado',
                _default: "Seleccione",
            },
        },
    },
})
