import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { Field, Form, Formik } from 'formik';
import Spinner from '../Utils/Spinner';
import moment from 'moment';
import { useLocation } from 'react-router-dom';
import { hasAccess } from '../../components/Utils/Common';
import '../Utils/Constants';
import GenericApi from '../Api/Services/GenericApi';
import { getUser } from '../../components/Utils/Common';
import ModalCierreEtapaInfo from '../Utils/Modals/ModalCierreEtapaInfo';
import ModalGen from '../Utils/Modals/ModalGeneric';

function RamasProceso() {

    const [getRespuestaAntecedente, setRespuestaAntecedente] = useState(false);
    const [getRtaJefe, setRtaJefe] = useState(false);
    const [getListaFasesHabilitadas, setListaFasesHabilitadas] = useState({ data: [] });
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getFase, setFase] = useState(false);
    const [getEtapa, setEtapa] = useState();
    const [getColorText, setColorText] = useState("");

    const location = useLocation();
    const { from } = location.state;
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;

    useEffect(() => {
        async function fetchData() {

            window.showSpinner(true);

            // VALIDA SI ES JEFE
            jefeDependencia();
            listaFasesHabilitadas();
            etapaActual();
        }
        fetchData();
    }, []);


    /**
     * Se consulta si el usuario que se encuentra en sesión es jefe de la dependencia
     */
    const jefeDependencia = () => {

        const data = {
            "data": {
                "type": "usuario",
                "attributes": {
                    "": ""
                }
            }
        }

        GenericApi.getByDataGeneric("usuario/get-jefe-dependencia", data).then(

            datos => {
                if (!datos.error) {
                    setRtaJefe(true)
                }
                else {
                    setRtaJefe(false);
                }
            }
        )
    }

    const etapaActual = () => {

        GenericApi.getGeneric("proceso-diciplinario/" + procesoDisciplinarioId).then(

            datos => {

                if (!datos.error) {

                    from.idEtapa = datos.data.attributes.etapa.id;
                    setEtapa(datos.data.attributes.etapa.id);
                }
                else {

                }
            }
        )
    }


    const listaFasesHabilitadas = () => {

        GenericApi.getGeneric("proceso-diciplinario/get-fases-registradas/" + procesoDisciplinarioId).then(

            datos => {

                if (!datos.error) {
                    setListaFasesHabilitadas(datos);
                    setFase(true);
                    setRespuestaAntecedente(true);
                    window.showSpinner(false);
                }
                else {

                }
            }
        )
    }

    //Funciones
    const handleClicSoporteRadicado = (id_etapa, id_fase, es_soporte) => {
        from.id_etapa = id_etapa;
        from.id_fase = id_fase;
        changeValueEsFuncion(es_soporte);
    };

    function changeValueEsFuncion(es_funcion) {
        from.es_soporte = es_funcion;
    }

    const cerrarEtapa = (datos) => {

        window.showSpinner(true);

        let data

        data = {
            "data": {
                "type": "cerrar_etapa",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": datos.inputEtapa,
                    "descripcion": "Cierre de etapa",
                    "created_user": getUser().nombre,
                }
            }
        }


        GenericApi.addGeneric('cierre-etapa', data).then(
            datos => {

                if (!datos.error) {
                    setModalState({ title: "Cierre de etapa", message: "El proceso disciplinario se asigna a: " + datos.data.attributes.funcionario_asignado.nombre + " " + datos.data.attributes.funcionario_asignado.apellido, show: true, redirect: '/MisPendientes', from: { from }, alert: global.Constants.TIPO_ALERTA.EXITO });
                }
                else {
                    setModalState({ title: "Cierre de etapa", message: datos.error.toString(), show: true, redirect: '/RamasProceso', from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });

                }
                window.showSpinner(false);
            }
        )
    }

    const componentAntecedente = () => {

        return (
            <>

                <div className="col-lg-12 push">
                    <div className="alert alert-info" role="alert">
                        <h3 className="text-center">(RADICADO) :: {radicado}</h3>
                        <span><strong>Antecedente: </strong></span><br />
                        <span title={(from.antecedente) ? from.antecedente : ""}>{from.antecedente ? from.antecedente.substring(0, 300) : ""}...</span><br />

                        <br />
                        <div className="row">
                            <div className="col-lg-6"><span><strong>Registrado Por: </strong>{from.registradoPor ? from.registradoPor : "Sin Información"}</span></div>
                            <hr />
                            <div className="col-lg-6"><span><strong>Dependencia registro: </strong> {from.dependendencia ? from.dependendencia : "Sin Información"}</span></div>
                            <hr />
                        </div>

                        <div className="row">

                            <div className="col-lg-4"><span><strong>Vigencia: </strong>{vigencia}</span></div>
                            <div className="col-lg-4"><span><strong>Fecha de registro: </strong> {moment(from.fechaRegistro, 'DD/MM/YYYY, h:mm:ss a').format("DD/MM/YYYY, h:mm:ss a")}</span></div>
                            <div className="col-lg-4"><span><strong>Fecha de ingreso: </strong> {moment(from.fechaIngreso, 'YYYY/MM/DD').format("DD/MM/YYYY")}</span></div>


                        </div>

                    </div>
                </div>
            </>
        )
    }


    const cargarFase = (id_etapa, id_fase, es_soporte, redirect) => {

        for (var i = 0; i < getListaFasesHabilitadas.data.length; i++) {

            if (getListaFasesHabilitadas.data[i].id == id_fase
                && (getListaFasesHabilitadas.data[i].attributes.visible
                    || (id_etapa >= 3 && id_etapa == from.idEtapa) //Validación para fases de Evaluación en PD en adelante
                )) {

                if (getListaFasesHabilitadas.data[i].attributes.estado) {
                    return (
                        <>
                            <div className='w2d-form-group'>
                                <Link to={redirect} state={{ from: from, selected_id_etapa: id_etapa }}>
                                    <button type="button" className="btn btn-sm btn-secundary w2d_btn-large mr-1 mb-3 text-left" onClick={() => changeValueEsFuncion(false)}>
                                        <i className="far fa-dot-circle txt-success"></i> {getListaFasesHabilitadas.data[i].attributes.nombre}</button>
                                </Link>

                                {getListaFasesHabilitadas.data[i].attributes.etapa == global.Constants.ETAPAS.CAPTURA_REPARTO ? (
                                    <Link to={`/SoporteradicadoForm/`} state={{ from: from }}>
                                        <button type="button" className="btn btn-sm btn-success mr-1 mb-3" onClick={() => handleClicSoporteRadicado(id_etapa, id_fase, es_soporte)}><i class="far fa-folder-open"></i></button>
                                    </Link>
                                ) : null
                                }
                            </div>
                        </>
                    )
                }

                else {
                    return (
                        <>
                            <div className='w2d-form-group'>
                                <Link to={redirect} state={{ from: from, selected_id_etapa: id_etapa }}>
                                    <button type="button" className={global.Constants.BOTONES_RAMAS.PRIMARY} onClick={() => changeValueEsFuncion(false)}><i className="fas fa-clipboard-list"></i> {getListaFasesHabilitadas.data[i].attributes.nombre}</button>
                                </Link>

                                {getListaFasesHabilitadas.data[i].attributes.etapa == global.Constants.ETAPAS.CAPTURA_REPARTO ? (
                                    <Link to={`/SoporteradicadoForm/`} state={{ from: from }}>
                                        <button type="button" className="btn btn-sm btn-primary mr-1 mb-3" onClick={() => handleClicSoporteRadicado(id_etapa, id_fase, es_soporte)}><i className="fas fa-folder-open"></i></button>
                                    </Link>
                                ) : null
                                }

                            </div>
                        </>
                    )
                }
            }

        }

    }

    const colorFase = (estado_fase) => {

        if (estado_fase === 1)
            return (global.Constants.BOTONES_RAMAS.PRIMARY)
        else if (estado_fase === 2)
            return (global.Constants.BOTONES_RAMAS.WARNING)
        else
            return (global.Constants.BOTONES_RAMAS.SUCCESS)
    }


    const colorFase2 = (estado_fase) => {

        if (estado_fase === 1)
            return (global.Constants.BOTONES_RAMAS.PRIMARY)
        else if (estado_fase === 2)
            return (global.Constants.BOTONES_RAMAS.WARNING)
        else
            return (global.Constants.BOTONES_RAMAS.SUCCESS)
    }




    const cargarSoportesRadicado = (id_etapa, id_fase, es_soporte, redirect) => {

        for (var i = 0; i < getListaFasesHabilitadas.data.length; i++) {

            if (getListaFasesHabilitadas.data[i].id == id_fase && getListaFasesHabilitadas.data[i].attributes.estado) {
                return (
                    <div className='w2d-form-group'>
                        <Link to={redirect} state={{ from: from }}>
                            <button type="button" className={global.Constants.BOTONES_RAMAS.SUCCESS} onClick={() => handleClicSoporteRadicado(id_etapa, id_fase, es_soporte)}><i className="fas fa-archive"></i> {getListaFasesHabilitadas.data[i].attributes.nombre}</button>
                        </Link>
                    </div>
                )
            }
            else if (getListaFasesHabilitadas.data[i].id == id_fase && !getListaFasesHabilitadas.data[i].attributes.estado) {
                return (
                    <div className='w2d-form-group'>
                        <Link to={redirect} state={{ from: from }}>
                            <button type="button" className={global.Constants.BOTONES_RAMAS.SUCCESS} onClick={() => handleClicSoporteRadicado(id_etapa, id_fase, es_soporte)}><i className="fas fa-archive"></i> {getListaFasesHabilitadas.data[i].attributes.nombre}</button>
                        </Link>
                    </div>
                )
            }
        }
    }


    const cargarCierreEtapa = (id_etapa, id_fase_lista_para_cierre, id_fase_cerrada) => {

        for (var i = 0; i < getListaFasesHabilitadas.data.length; i++) {

            // valida cierre de captura y reparto
            if (getListaFasesHabilitadas.data[i].id == id_fase_lista_para_cierre) {

                console.log("ID_ETAPA: " + id_etapa + " ID_FASE_LISTA_PARA_CIERRE: " + id_fase_lista_para_cierre + " ID_FASE_CERRADA: " + id_fase_cerrada)
                return (
                    <div className="form-group">

                        <Formik
                            initialValues={{
                                inputCierreEtapa: '',
                                inputEtapa: id_etapa,
                            }}
                            enableReinitialize
                            validate={(valores) => {

                                let errores = {}
                                if (!valores.inputCierreEtapa) {
                                    errores.inputCierreEtapa = 'Debe seleccionar la opción cierre de etapa'
                                }
                                return errores
                            }}

                            onSubmit={(valores, { resetForm }) => {
                                cerrarEtapa(valores);
                            }}
                        >

                            <Form>
                                <div className="col-md-12">
                                    <Field type="checkbox" className="form-check-input" id="inputCierreEtapa" name="inputCierreEtapa" />
                                    <label className="form-check-label" htmlFor="example-checkbox-inline1">¿Con la información actual se puede dar cierre a esta etapa?</label>
                                </div>

                                <div className="col-md-12">
                                    <>
                                        {
                                            (hasAccess('CR_CierreEtapa', 'Crear')) ? (
                                                <div className="block-content block-content-full text-center">
                                                    <button type="submit" className="btn btn-danger"> Cerrar Etapa</button>
                                                </div>
                                            ) : null
                                        }
                                    </>
                                </div>
                            </Form>
                        </Formik>
                    </div>
                )
            }

            // valida cierre de captura y reparto
            if (getListaFasesHabilitadas.data[i].id == id_fase_cerrada && getListaFasesHabilitadas.data[i].attributes.estado) {

                return (
                    <div className="col-md-12 text-center">
                        <button type='button' title='Consultar cierre de etapa' className='btn btn-sm'
                            data-toggle="modal" data-target={'#modal-consultar-detalle-' + id_etapa}> <i className="fas fa-clipboard-list fa-2x text-info "></i></button>
                    </div>
                )
            }
        }
    }



    return (
        <>
            {<Spinner />}
            {<ModalCierreEtapaInfo object={1} />}
            {<ModalGen data={getModalState} />}

            <div className="row">

                <div className="col-md-12">
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">

                                <li className="breadcrumb-item">
                                    <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li className="breadcrumb-item"><small>Ramas del proceso</small></li>
                            </ol>
                        </nav>

                    </div>
                </div>
                {getRespuestaAntecedente ? componentAntecedente() : null}

                    <div className='col-md-4 offset-md-4'>
                        <Link to="/Buscador" state={{ from: from }} >
                            <button type="button" className="btn btn-primary w-100" ><i class="fas fa-search"></i> Buscador Expediente <i class="fas fa-search"></i></button>
                        </Link>
                    </div>

                    <div className="col-md-12 w2d-enter">

                        <div id="horizontal-navigation-hover-normal" class="d-none d-lg-block mt-2 mt-lg-0">
                            <ul class="nav-main nav-main-horizontal nav-main-hover">

                                <li class="nav-main-item">
                                    <a class="nav-main-link nav-main-link-submenu" data-toggle="submenu" aria-haspopup="true" aria-expanded="false" href="#">
                                        <span class="nav-main-link-name"><i class="fas fa-tools"></i> Herramientas</span>
                                    </a>
                                    <ul class="nav-main-submenu">
                                        <li class="nav-main-item">
                                            <Link className="nav-main-link" to="/ValidarClasificacionRadicadoForm" state={{ from: from }}>
                                                <span class="nav-main-link-name">Reclasificación del expediente</span>
                                            </Link>
                                        </li>
                                        <li class="nav-main-item">
                                            <Link className="nav-main-link" to="/TipoConductaProcesoLista" state={{ from: from }}>
                                                <span class="nav-main-link-name">Tipo de conducta</span>
                                            </Link>
                                        </li>
                                        <li class="nav-main-item">
                                            <Link className="nav-main-link" to="/Transacciones" state={{ from: from , selected_id_etapa: getEtapa}}>
                                                <span class="nav-main-link-name">Transacciones</span>
                                            </Link>
                                        </li>
                                    </ul>
                                </li>
                            </ul>
                        </div>
                    </div>
        

                {/*CAPTURA Y REPARTO*/}
                <div className="col-lg-4 col-md-6 col-sm-6">
                    <div className={getEtapa == global.Constants.ETAPAS.CAPTURA_REPARTO ? "block block-themed2" : "block block-themed"}>

                        <div className= {getEtapa == global.Constants.ETAPAS.CAPTURA_REPARTO ? "block-header2 block-header-default" : "block-header block-header-default"}>
                                <h3 className="block-title" style={{ color: 'white' }}>Captura y Reparto</h3>
                            <Link to={`/SoporteRadicadoLista/`} state={{ from: from }}>
                                <button type="button" className="btn btn-sm" style={{ color: 'white' }} onClick={() => handleClicSoporteRadicado(1, 1, true)}><i className="fas fa-folder-open fa-2x"></i></button>
                            </Link>
                        </div>

                        <>
                            {
                                (hasAccess('CapturaYReparto', 'Consultar')) ? (
                                    <div className="block-content">

                                        <ul className="nav-items push">

                                            <li>{getFase ? cargarFase(global.Constants.ETAPAS.CAPTURA_REPARTO, global.Constants.FASES.ANTECEDENTES, true, '/AntecedentesLista/') : null}</li>
                                            <li>{getFase ? cargarFase(global.Constants.ETAPAS.CAPTURA_REPARTO, global.Constants.FASES.INTERESADO, true, '/DatosInteresadoLista/') : null}</li>
