                                            <i className="fa fa-fw fa-times"></i>
                                        </button>
                                    </div>
                                </div>
                                <div className="block-content">
                                    <p>¿DESEA MODIFICAR EL CAMBIO DE ETAPA A { getListaEtapasBoolean ? getEtapaNombreSiguiente : null } ?</p>
                                </div>
                                <div className="block-content block-content-full text-right bg-light">
                                    <button type="button" className="btn btn-rounded btn-primary" data-dismiss="modal" onClick={() => onCambiarEtapa()}>{global.Constants.BOTON_NOMBRE.ACTUALIZAR}</button>
                                    <button type="button" className="btn btn-rounded btn-outline-primary" data-dismiss="modal" onClick={() => (setEtapaNombreSiguiente(getEtapaNombreActual), setIdEtapaSiguiente(getIdEtapaActual))}>{global.Constants.BOTON_NOMBRE.CANCELAR}</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </>
        );
    }

    const showModalCambioEtapaActuacion = () => {
        window.showModalEtapaConfirmacionActuaciones(true);
    }

    const obtenerEtapa = (idEtapa) => {
        if(idEtapa){
            setIdEtapaSiguiente(idEtapa);
            setEtapaNombreSiguiente(getListaEtapas.data.find(dato => idEtapa === dato.id).attributes.nombre);
            showModalCambioEtapaActuacion()
        }
    }

    const listarActuacionesActivas = () => {
        return (
            getListaActuaciones.map((actuacion, index) => {
                if(actuacion.id_actuacion !== id){
                    return (
                        <tr key={(index)}>
                            <td>
                                { actuacion.nombre_actuacion }
                            </td>
                            <td>
                                { actuacion.nombre_etapa }
                            </td>
                            <td>
                                <small><strong>USUARIO:</strong> { actuacion.nombre_usuario + " " + actuacion.apellido_usuario }</small><br />
                                <small> { actuacion.fecha_creacion }</small><br />
                            </td>
                            <td>
                                <div className="custom-control custom-switch custom-control-lg mb-2">
                                    {
                                        <>
                                            <input defaultChecked={actuacion.estado_inactivo == '1'} type="checkbox" onChange={e => agregarCheckInactivos(e.target.checked, index)} className="custom-control-input" id={actuacion.id_actuacion} name={actuacion.id_actuacion} />
                                            <label className="custom-control-label" htmlFor={actuacion.id_actuacion}></label>
                                        </>
                                    }
                                </div>
                            </td>
                        </tr>
                    )
                }
            })
        )
    }

    return (
        <>
            { componentEtapaAlertConfirmacion() }
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            {<InfoErrorApi error={errorApi} />}
            {getRtaInfoTrue ? <InfoConfirmarAccion mensaje={'¿Esta seguro que desea subir este archivo?'} function={e => subirArchivo(e, getValidarAccion == 0 ? getValidarAccion : 1)} /> : null}
            {<InfoExitoApi />}
            {getInfoModal ? <ModalActuaciones accionActuacion={getMessageModal} /> : null}
            {<ModalItemsVer data={getModalStateItems} />}
            <Formik
                initialValues={{
                    descripcion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    //enviarDatos(valores);
                }}
            >
                {({ errors }) => (
                    <Form>  
                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from, disable: disable }}><small>Ramas del proceso</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa, disable: disable }}><small>Actuaciones</small></Link></li>
                                    <li className="breadcrumb-item"> <small>Ver detalle {tipoActuacion == "0" ? "Actuación" : (tipoActuacion == "1" ? 'Impedimento' : 'Comisorio')}</small></li>
                                </ol>
                            </nav>
                        </div>


                        <div className="row">
                            {
                                (
                                    getEtapaNombreSiguiente != null || 
                                    getExcluyente === '1' || 
                                    getCierraProceso === '1' || 
                                    getDespuesAprobacionListarActuacion === '1'
                                ) && 
                                (
                                    getUltimoEstado === global.Constants.ESTADOS_ACTUACION.APROBADA ||
                                    getUltimoEstado === global.Constants.ESTADOS_ACTUACION.PENDIENTE_APROBACION ||
                                    getUltimoEstado === global.Constants.ESTADOS_ACTUACION.ACTUALIZACION_DOCUMENTO ||
                                    getUltimoEstado === global.Constants.ESTADOS_ACTUACION.DOCUMENTO_FIRMADO
                                )
                            ?
                            (                               
                                <div className="col-md-12">
                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                        <h3 className="alert-heading font-size-h4 my-2">INFORMACIÓN</h3>
                                        <ul>
                                            {
                                                getEtapaNombreSiguiente != null 
                                                ?
                                                (                                                                    
                                                    <li>
                                                        UNA VEZ { tipoActuacion == "0" ? "LA" : 'EL' } <b>{ tipoActuacion == "0" ? "ACTUACIÓN" : (tipoActuacion == "1" ? 'IMPEDIMENTO' : 'COMISORIO') }</b> SEA { tipoActuacion == "0" ? "APROBADA" : 'APROBADO'}, SE CAMBIARÁ A LA ETAPA <b>{ getEtapaNombreSiguiente }</b>.
                                                    </li>
                                                )
                                                : null
                                            }
                                            {
                                                getCierraProceso === '1'
                                                ?
                                                (
                                                    <li>
                                                        { tipoActuacion == "0" ? "LA" : 'EL' } <b>{ tipoActuacion == "0" ? "ACTUACIÓN" : (tipoActuacion == "1" ? 'IMPEDIMENTO' : 'COMISORIO') }</b> DARÁ CIERRE AL PROCESO.
                                                    </li>
                                                )
                                                : null
                                            }
                                            {
                                                getExcluyente === '1'
                                                ?
                                                (                                                                    
                                                    <li>
                                                        { tipoActuacion == "0" ? "LA" : 'EL' } <b>{ tipoActuacion == "0" ? "ACTUACIÓN" : (tipoActuacion == "1" ? 'IMPEDIMENTO' : 'COMISORIO') }</b> ES EXCLUYENTE Y SEGUIRA EL PROCESO HASTA QUE SE APRUEBE.
                                                    </li>
                                                )
                                                : null
                                            }
                                            {
                                                getDespuesAprobacionListarActuacion === '1'
                                                ?
                                                (
                                                    <li>
                                                        { tipoActuacion == "0" ? "LA" : 'EL' } <b>{ tipoActuacion == "0" ? "ACTUACIÓN" : (tipoActuacion == "1" ? 'IMPEDIMENTO' : 'COMISORIO') }</b> ANULA OTRAS ACTUACIONES.
                                                    </li>
                                                )
                                                : null
                                            }
                                        </ul>
                                    </div>
                                </div>
                            )
                            :
                            null
                            }
                        </div>

                        
                        <div className='row'>
                                            {/* 
                                                Barra de navegación de acciones
                                            */}
                                            <div>
                                                <div className='col-md-12'>
                                                    <b style={{ fontSize: '18px' }}>{tipoActuacion == "0" ? "NOMBRE ACTUACIÓN" : (tipoActuacion == "1" ? 'NOMBRE IMPEDIMENTO' : 'NOMBRE COMISORIO')}: </b>{nombre.toUpperCase()}
                                                </div>
                                            </div>
                                            <div className="dropdown" style={{ marginLeft: 'auto' }}>
                                                <button type="button" className="btn btn-rounded btn-primary" id="page-header-user-dropdown" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                                    <span className="d-none d-sm-inline-block" data-toggle="tooltip" data-placement="bottom" title="" data-original-title="Opciones">ACCIONES</span>
                                                    <i className="fa fa-fw fa-angle-down ml-1 d-none d-sm-inline-block" />
                                                </button>

                                                {/*
                                                    Barra de opciones
                                                */}
                                                <div className="dropdown-menu dropdown-menu-right p-0" aria-labelledby="page-header-user-dropdown">

                                                    {/* 
                                                        Opciones
                                                    */}
                                                    {getActuacionEstaAprobadaSioNo && getMuestraBotonFechaParaSemaforo && getEstadoActuacion ?
                                                        <div className='p-1'>
                                                            <a className="dropdown-item">
                                                                <Link className="font-size-h5 font-w600" to={"/SeleccionDeFechaParaSemaforo/"} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion, actuacionIdMaestra: actuacionIdMaestra }}>
                                                                    SELECCIÓN DE FECHA PARA SEMÁFORO
                                                                </Link>
                                                            </a>
                                                        </div>
                                                        : null}

                                                    {tipoActuacion == "1" && (hasAccess('SeleccionarDependenciaDuenaDelProceso', 'Gestionar')) && getEstadoActuacion ?
                                                        <div className='p-1'>
                                                            <a className="dropdown-item">
                                                                <Link className="font-size-h5 font-w600" to={"/SeleccionarDuenoProceso/"} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion, actuacionIdMaestra: actuacionIdMaestra }}>
                                                                    ASIGNAR NUEVA DEPENDENCIA DUEÑA DEL PROCESO
                                                                </Link>
                                                            </a>
                                                        </div>
                                                        : null}
                                                        {
                                                            getUltimoEstado === global.Constants.ESTADOS_ACTUACION.APROBADA ||
                                                            getUltimoEstado === global.Constants.ESTADOS_ACTUACION.PENDIENTE_APROBACION ||
                                                            getUltimoEstado === global.Constants.ESTADOS_ACTUACION.ACTUALIZACION_DOCUMENTO ||
                                                            getUltimoEstado === global.Constants.ESTADOS_ACTUACION.DOCUMENTO_FIRMADO
                                                            ?
                                                                <div className='p-1'>
                                                                    <a className="dropdown-item">
                                                                        <Link className="font-size-h5 font-w600" to="/Transacciones" state={{ from: from, selected_id_etapa: selected_id_etapa, id_actuacion: getidMasActuacion, actuacionIdMaestra: actuacionIdMaestra }}>
                                                                            TRANSACCIONES
                                                                        </Link>
                                                                    </a>
                                                                </div>
                                                            :
                                                            null
                                                        }

                                                    {/* Si no es un comisorio y es es diferente de Pendiente de aprobacion */}
                                                    {/* {(estadoActualActuacion == "Pendiente aprobación" || estadoActualActuacion == "Solicitud inactivación rechazada") && */}
                                                    {
                                                        (
                                                            estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.APROBADA_PDF_DEFINITIVO || 
                                                            estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION_RECHAZADA ||                                                            
                                                            estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION_ACEPTADA
                                                        ) && getEstadoActuacion 
                                                        ?
                                                            <div className='p-1'>
                                                                <a className="dropdown-item">
                                                                    <Link className="font-size-h5 font-w600" to={`/ActuacionesSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, actuacionIdMaestra: actuacionIdMaestra, activacion: false }}>
                                                                        SOLICITAR INACTIVACIÓN
                                                                    </Link>
                                                                </a>
                                                            </div>
                                                        : 
                                                            null
                                                    }
                                                    {
                                                        estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION_ACEPTADA ||                                                            
                                                        estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION_RECHAZADA ||
                                                        estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.ACTUACION_INACTIVADA
                                                        ?
                                                            <div className='p-1'>
                                                                <a className="dropdown-item">
                                                                    <Link className="font-size-h5 font-w600" to={`/ActuacionesSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, actuacionIdMaestra: actuacionIdMaestra, activacion: true }}>
                                                                        SOLICITAR ACTIVACIÓN
                                                                    </Link>
                                                                </a>
                                                            </div>
                                                        : 
                                                            null
                                                    }

                                                    {
                                                        getRespuestaCampos && getEstadoActuacion ?
                                                            <div className='p-1'>
                                                                <a className="dropdown-item">
                                                                    <p className="font-size-h5 font-w600"
                                                                        style={{
                                                                            marginBottom: '0px',
                                                                            color: '#0665d0',
                                                                            textDecoration: 'none',
                                                                            backgroundColor: 'transparent',
                                                                            cursor: 'pointer',
                                                                        }}
                                                                        onClick={verCamposAdicionales}
                                                                    >
                                                                        VER CAMPOS ADICIONALES
                                                                    </p>
                                                                </a>
                                                            </div>
                                                        : 
                                                            null
                                                    }
                                                </div>
                                            </div>
                                            <div className='mb-2'>
                                                {
                                                    (
                                                        getUltimoEstado === global.Constants.ESTADOS_ACTUACION.APROBADA ||
                                                        getUltimoEstado === global.Constants.ESTADOS_ACTUACION.PENDIENTE_APROBACION ||
                                                        getUltimoEstado === global.Constants.ESTADOS_ACTUACION.ACTUALIZACION_DOCUMENTO ||
                                                        getUltimoEstado === global.Constants.ESTADOS_ACTUACION.DOCUMENTO_FIRMADO
                                                    )
                                                    ?
                                                        <div className="dropdown">
                                                            <button type="button" className="btn btn-rounded btn-primary" id="page-header-user-dropdown" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                                                <span className="d-none d-sm-inline-block" data-toggle="tooltip" data-placement="bottom" title="" data-original-title="Opciones"><i className='fa fa-eye'></i></span>
                                                                <i className="fa fa-fw fa-angle-down ml-1 d-none d-sm-inline-block" />
                                                            </button>
                                                            {/*
                                                                Barra de opciones
                                                            */}
                                                            <div className="dropdown-menu dropdown-menu-right p-0" aria-labelledby="page-header-user-dropdown">
                                                                {
                                                                    getEtapaVisibilidadLista.data.map((etapa, i) => {
                                                                        if (true) {
                                                                            return (
                                                                                <div className='p-1' onClick={() => (onCambiarVisibilidad(etapa.id))}>
                                                                                    <a className="dropdown-item" >
                                                                                        { (getIdEtapaVisibilidadActual == etapa.id || getIdEstadoVisibilidad == etapa.id) ? <i className='fa fa-check'></i> : null } { etapa.nombre }
                                                                                    </a>
                                                                                </div>
                                                                            )
                                                                        }
                                                                    })
                                                                }
        
                                                            </div>
                                                        </div>
                                                    :
                                                        null
                                                }
                                            </div>
                                            <div className='mb-2'>
                                            <div class="custom-control custom-switch mb-1" style={{ paddingTop: '6px', paddingRight: '10px' }}>
                                                    {/* <input defaultChecked={false} type="checkbox" className="custom-control-input" id="incluir_reporte" name="incluir_reporte" />                                                     */}
                                                    <input type="checkbox" class="custom-control-input" id="incluir_reporte" name="incluir_reporte" checked={getIncluirReporte} onChange={e => onCambiarIncluirReporte(e.target.checked)} />
                                                    <label className="custom-control-label" for="incluir_reporte">INCLUIR EN REPORTE</label>
                                                </div>
                                            </div>
                                            <div className='mb-2'>
                                                <Link to={`/ActuacionesLista/`} title='REGRESAR A LA LISTA DE ACTUACIONES' state={{ from: from, selected_id_etapa: selected_id_etapa, disable: disable }}>
                                                    <button type="button" className="btn btn-rounded btn-primary"><i className="fas fa-backward"></i></button>
                                                </Link>
                                            </div>
                                        </div>

                        <div className="block block-themed">
                            <li className="nav-main-item open">
                                <a className="nav-main-link nav-main-link-submenu acordeon-pantalla" data-toggle="submenu" aria-haspopup="true" aria-expanded="false" href="#">
                                    <div className={(disable ? ("block-header bg-dark") : "block-header")}>
                                        <h3 className="block-title">{getNombreProceso.toUpperCase()} :: <strong>{tipoActuacion == "0" ? "ACTUACIÓN" : (tipoActuacion == "1" ? 'IMPEDIMENTO' : 'COMISORIO')} DETALLE</strong></h3>
                                    </div>
                                </a>
                                <ul className="nav-main-submenu">
                                    <div className="block-content">
                                        <div className="row">
                                        {
                                            disable != true ? (
                                                (estadoActualActuacion !== global.Constants.ESTADOS_ACTUACION.APROBADA ||
                                                    (getArchivosListaSearch.data.length == 2 || getArchivosListaSearch.data.length > 2)) ? null :
                                                    <div className='col-md-12'>
                                                        <table className="table table-striped table-vcenter js-dataTable-full">
                                                            <thead>
                                                                <tr>
                                                                    {inputListArchivos.map((x, i) => {
                                                                        return (
                                                                            <div className="col-md-12" key={i}>
                                                                                <div className="form-group" style={{
                                                                                    display: 'flex',
                                                                                    justifyContent: 'flex-end'
                                                                                }}>
                                                                                    <div className='row'>
                                                                                        <div className='col-md-12'>
                                                                                            <div className='col-md-12' style={{
                                                                                                textAlign: 'center',
                                                                                                marginLeft: '14%'
                                                                                            }}>
                                                                                                <label>CARGAR ARCHIVO DEFINITIVO<span className="text-danger">*</span></label>
                                                                                            </div>
                                                                                        </div>
                                                                                        <div className='col-md-12' style={{
                                                                                            display: 'flex',
                                                                                            justifyContent: 'flex-end'
                                                                                        }}>
                                                                                            <div className='col-md-6'>
                                                                                                <label className="custom-file-label" htmlFor="example-file-input-custom" name="input-file" data-toggle="custom-file-input">{x.archivo.name}</label>
                                                                                                <input className="custom-file-input" data-toggle="custom-file-input" type="file" accept='.pdf' name="archivo" onChange={e => handleInputChangeArchivos(e, i, 0, 0, 0, "Documento definitivo", 2)} required />
                                                                                                <label>PESO DEL ARCHIVO: {formatBytes(x.archivo.size)}</label>
                                                                                            </div>
                                                                                        </div>
                                                                                    </div>
                                                                                </div>
                                                                            </div>
                                                                        );
                                                                    })}
                                                                </tr>
                                                            </thead>
                                                        </table>
                                                    </div>
                                            ) : null
                                        }
                                        </div>
                                        {
                                            getEtapaSiguiente && 
                                            (
                                                estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.PENDIENTE_APROBACION ||
                                                estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.ACTUALIZACION_DOCUMENTO
                                            )
                                            ?
                                            (
                                                <>
                                                    <div className="content-heading"></div>
                                                        <div className='row'>
                                                            <div className="col-md-12">
                                                                <div className="form-group">
                                                                    <label htmlFor="cambiarEtapa">ETAPA DESPUÉS DE LA APROBACIÓN</label>
                                                                    <select as="select" className="form-control" id="cambiarEtapa" name="cambiarEtapa" value={getIdEtapaSiguiente} onChange={e => (obtenerEtapa(e.target.value))}>
                                                                        <option value="">Seleccione un valor</option>
                                                                        { selectEtapaCambiar() }
                                                                    </select>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    <div className="content-heading"></div>
                                                </>
                                            )
                                            :
                                            null
                                        }
                                        {
                                            getDespuesAprobacionListarActuacion === '1' && 
                                            (
                                                estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.PENDIENTE_APROBACION ||
                                                estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.ACTUALIZACION_DOCUMENTO
                                            )
                                            ?
                                            (
                                                <>
                                                    <label htmlFor="cambiarEtapa">ACTUACIONES A INACTIVAR</label>
                                                    {
                                                        getListaActuaciones.length > 0
                                                        ?
                                                            <>
                                                                <div className='row'>
                                                                    <div className="col-md-12">
                                                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                                            <thead>
                                                                                <tr>
                                                                                    <th width="25%">ACTUACIÓN</th>
                                                                                    <th width="25%">ETAPA</th>
                                                                                    <th width="30%">DETALLE</th>
                                                                                    <th width="20%">INACTIVAR</th>
                                                                                </tr>
                                                                            </thead>
                                                                            <tbody >
                                                                                { getListaActuaciones ? listarActuacionesActivas() : null}
                                                                            </tbody>
                                                                        </table>
                                                                    </div>
                                                                </div>
                                                                <div className="block-content block-content-full text-right">
                                                                    <button type="button" className="btn btn-rounded btn-primary" onClick={() => onEstablecerListaInactivos()}>Guardar Lista</button>
                                                                </div>
                                                            </>
                                                        :
                                                            <>
                                                                <p>NO HAY ACTUACIONES A INACTIVAR</p>
                                                            </>
                                                    }
                                                    
                                                    <div className="content-heading"></div>
                                                </>
                                            )
                                            :
                                            null
                                        }
                                        <div className='row'>
                                            <div className="col-md-12">
                                                <b style={{ fontSize: '18px' }}>ARCHIVOS {tipoActuacion == "0" ? "ACTUACIÓN:" : (tipoActuacion == "1" ? 'IMPEDIMENTO:' : 'COMISORIO:')}</b>
                                            </div>
                                        </div>
                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full table-responsive-md">
                                            <thead>
                                                <tr>
                                                    <th>TIPO</th>
                                                    <th>NOMBRE DOCUMENTO</th>
                                                    <th>DESCARGAR</th>
                                                    <th>ACTUALIZAR</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {getRtaInfoArchivoActuaciones ? infoArchivos() : null}
                                            </tbody>
                                        </table>
                                        <strong>
                                            {
                                                getEstadoActuacion 
                                                ?
                                                    <span className='txt-blue-primary'>ESTADO: </span>
                                                :
                                                    <span className='txt-blue-primary'>ESTADO <strong>{tipoActuacion == "0" ? "ACTUACIÓN" : (tipoActuacion == "1" ? 'IMPEDIMENTO' : 'COMISORIO')} </strong> INACTIVA </span>
                                            }
                                            {
                                                getActuacionEstaAprobadaSioNo && !getActuacionPendienteDeFirmas && getEstadoActuacion 
                                                ?
                                                    (" APROBADO Y FIRMADO") 
                                                : 
                                                    null
                                            }
                                            {
                                                !getActuacionEstaAprobadaSioNo && getActuacionPendienteDeFirmas && getEstadoActuacion 
                                                ?
                                                    (" PENDIENTE DE APROBACIÓN Y FIRMAS") 
                                                : 
                                                    null
                                            }
                                            {
                                                !getActuacionEstaAprobadaSioNo && !getActuacionPendienteDeFirmas && getEstadoActuacion 
                                                ?
                                                    (
                                                        estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.PENDIENTE_APROBACION 
                                                        ?
                                                            (" PENDIENTE DE APROBACIÓN")
                                                        : 
                                                            (" PENDIENTE DE APROBACIÓN Y FIRMADO") 
                                                    )
                                                    : 
                                                    null
                                                }
                                        </strong>
                                        {
                                            console.log("Daticos",
                                                hasAccess('SolicitudDeAprobacion', 'Gestionar'),
                                                getEstadoActuacion,
                                                getActuacionEstaAprobadaSioNo == false,
                                                tipoActuacion != "1",
                                                !getActuacionPendienteDeFirmas,
                                                !getValidarUsuariosPendienteFirma,
                                                (
                                                    estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.PENDIENTE_APROBACION || 
                                                    estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION_RECHAZADA
                                                ), 
                                                (
                                                    getEtapaSiguiente &&
                                                    getEtapaNombreSiguiente != null
                                                )
                                            )
                                        }
                                        {
                                            hasAccess('SolicitudDeAprobacion', 'Gestionar') &&
                                            getEstadoActuacion &&
                                            getActuacionEstaAprobadaSioNo == false &&
                                            tipoActuacion != "1" &&
                                            !getActuacionPendienteDeFirmas &&
                                            !getValidarUsuariosPendienteFirma &&
                                            (
                                                estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.PENDIENTE_APROBACION || 
                                                estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION_RECHAZADA
                                            ) && 
                                            (
                                                (
                                                    getEtapaSiguiente &&
                                                    getEtapaNombreSiguiente != null
                                                ) ||
                                                (
                                                    !getEtapaSiguiente
                                                )
                                            )
                                            ?
                                                <div>
                                                    <table>
                                                        <thead>
                                                            <tr>
                                                                <th style={{ width: '30%' }}>APROBAR Y/O RECHAZAR {tipoActuacion == "0" ? "ACTUACIÓN" : 'COMISORIO'}: </th>
                                                                <th style={{ paddingLeft: '50px' }}>
                                                                    <div className="block-content block-content-full text-left">
                                                                        <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Aprobar actuación", valor: 1, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion, actuacionIdMaestra: actuacionIdMaestra }}>
                                                                            <button type="button" className="btn btn-rounded btn-primary">APROBAR</button>
                                                                        </Link>
                                                                        <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Rechazar actuación", valor: 2, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion, actuacionIdMaestra: actuacionIdMaestra }}>
                                                                            <button type="button" className="btn btn-rounded btn-outline-primary">RECHAZAR</button>
                                                                        </Link>
                                                                    </div>
                                                                </th>
                                                            </tr>
                                                        </thead>
                                                    </table>
                                                </div>
                                            : 
                                            null
                                        }
                                        {
                                            (hasAccess('AprobarYRechazarUnImpedimento', 'Gestionar')) &&
                                                getEstadoActuacion &&
                                                getActuacionEstaAprobadaSioNo == false &&
                                                tipoActuacion == "1" &&
                                                !getActuacionPendienteDeFirmas &&
                                                !getValidarUsuariosPendienteFirma &&
                                                (estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.PENDIENTE_APROBACION ||
                                                    estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION_RECHAZADA ) ?
                                                <div>
                                                    <table>
                                                        <thead>
                                                            <tr>
                                                                <th style={{ width: '30%' }}>APROBAR Y/O RECHAZAR IMPEDIMENTO: </th>
                                                                <th style={{ paddingLeft: '50px' }}>
                                                                    <div className="block-content block-content-full text-left">
                                                                        <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Aprobar actuación", valor: 1, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion, actuacionIdMaestra: actuacionIdMaestra }}>
                                                                            <button type="button" className="btn btn-rounded btn-primary mb-2">APROBAR</button>
                                                                        </Link>
                                                                        <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Rechazar actuación", valor: 2, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion, actuacionIdMaestra: actuacionIdMaestra }}>
                                                                            <button type="button" className="btn btn-rounded btn-outline-primary mb-2">RECHAZAR</button>
                                                                        </Link>
                                                                    </div>
                                                                </th>
                                                            </tr>
                                                        </thead>
                                                    </table>
                                                </div>
                                                : null
                                        }
                                        {
                                                hasAccess('SolicitudDeInactivacion', 'Gestionar') &&
                                                getEstadoActuacion &&
                                                (
                                                    (
                                                        getUltimoEstado === global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION || 
                                                        estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION
                                                    ) ||
                                                    (
                                                        getUltimoEstado === global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION || 
                                                        estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION
                                                    ) 
                                                ) &&
                                                disable != true 
                                                ? 
                                                    <div className='mt-4'>
                                                        <label className='mb-3'><b>CONFIRMAR { 
                                                        (
                                                            getUltimoEstado === global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION || 
                                                            estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION
                                                        ) ? 'ACTIVACIÓN' : 'INACTIVACIÓN'}: </b></label>
                                                        <Link to={`/ActuacionesAceptaRechazaSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, boton: "Acepta", solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion, actuacionIdMaestra: actuacionIdMaestra, 
                                                        activacion: (
                                                            getUltimoEstado === global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION || 
                                                            estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION
                                                        ) ? true : false }}>
                                                            <button type="button" className="btn btn-rounded btn-primary ml-2">ACEPTAR</button>
                                                        </Link>
                                                        <Link to={`/ActuacionesAceptaRechazaSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, boton: "Rechaza", solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion, actuacionIdMaestra: actuacionIdMaestra,
                                                        activacion: (
                                                            getUltimoEstado === global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION || 
                                                            estadoActualActuacion === global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION
                                                        ) ? true : false }}>
                                                            <button type="button" className="btn btn-rounded btn-outline-primary">RECHAZAR</button>
                                                        </Link>
                                                    </div>
                                                : 
                                                    null
                                        }
                                    </div>
                                </ul>
                            </li>
                        </div >
                        <div className="block block-themed">
                            <li className="nav-main-item">
                                <a className="nav-main-link nav-main-link-submenu acordeon-pantalla" data-toggle="submenu" aria-haspopup="true" aria-expanded="false" href="#">
                                    <div className={(disable ? ("block-header bg-dark") : "block-header")}>
                                        <h3 className="block-title"><strong>USUARIOS PARA FIRMA MECÁNICA</strong></h3>
                                    </div>
                                </a>
                                <ul className="nav-main-submenu">
                                    <div className="block-content">
                                        {
                                            getEstadoActuacion &&
                                            estadoActualActuacion !== global.Constants.ESTADOS_ACTUACION.RECHAZADA &&
                                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.REMITIDA &&
                                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.APROBADA &&
                                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.APROBADA_PDF_DEFINITIVO &&
                                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION_ACEPTADA &&
                                            !getActuacionEstaAprobadaSioNo 
                                            ? 
                                            (
                                                (hasAccess('AgregarUsuarioParaFirmaMecanica', 'Gestionar')) ? (
                                                    <Link to="/AgregarUsuarioParaFirmaMecanica" state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Agregar usuario para firma mecánica", valor: 1, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion, actuacionIdMaestra: actuacionIdMaestra }}>
                                                        <button type="button" className="btn btn-rounded btn-primary mb-4">AGREGAR USUARIO PARA FIRMA MECÁNICA</button>
                                                    </Link>
                                                ) : null
                                            ) 
                                            : 
                                            null
                                        }
                                        <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                            <thead>
                                                <tr>
                                                    <th>USUARIO</th>
                                                    <th>TIPO FIRMA</th>
                                                    <th>ESTADO</th>
                                                    <th>ACCIONES</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {getFirmasMecanicas ? ListaFirmasMecanicas() : null}
                                            </tbody>
                                        </table>
                                    </div>
                                </ul>
                            </li>
                        </div>
                        {
                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.APROBADA &&
                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.APROBADA_PDF_DEFINITIVO &&
                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION &&
                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION_ACEPTADA &&
                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION_RECHAZADA &&
                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.DOCUMENTO_FIRMADO &&
                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.ACTUACION_INACTIVADA &&
                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION &&
                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION_ACEPTADA &&
                            getUltimoEstado !== global.Constants.ESTADOS_ACTUACION.SOLICITUD_ACTIVACION_RECHAZADA
                            ?
                                <div className="block block-themed">
                                    <li className="nav-main-item">
                                        <a className="nav-main-link nav-main-link-submenu acordeon-pantalla" data-toggle="submenu" aria-haspopup="true" aria-expanded="false" href="#">
                                            <div className={(disable ? ("block-header bg-dark") : "block-header")}>
                                            <h3 className="block-title"><strong>HISTORIAL DE APROBACIONES {tipoActuacion == "0" ? "DE LA ACTUACIÓN" : (tipoActuacion == "1" ? 'DEL IMPEDIMENTO' : 'DEL COMISORIO')}</strong></h3>
                                            </div>
                                        </a>
                                        <ul className="nav-main-submenu">
                                            <div className="block-content">
                                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full table-responsive-md">
                                                    <thead>
                                                        <tr>
                                                            <th style={{ width: '13%' }}>FECHA</th>
                                                            <th>USUARIO</th>
                                                            <th>USUARIO APROBO/RECHAZO</th>
                                                            <th>ESTADO</th>
                                                            <th>OBSERVACIONES</th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>
                                                        {getRtaInfoTrazabilidadActuaciones ? infoTrazabilidad() : null}
                                                    </tbody>
                                                </table>
                                            </div>
                                        </ul>
                                    </li>
                                </div>
                            :
                                null
                        }
                        <div className="block block-themed">
                            <li className="nav-main-item">
                                <a className="nav-main-link nav-main-link-submenu acordeon-pantalla" data-toggle="submenu" aria-haspopup="true" aria-expanded="false" href="#">
                                    <div className={(disable ? ("block-header bg-dark") : "block-header")}>
                                        <h3 className="block-title"><strong>SEMÁFOROS {tipoActuacion == "0" ? "DE LA ACTUACIÓN" : (tipoActuacion == "1" ? 'DEL IMPEDIMENTO' : 'DEL COMISORIO')}</strong></h3>
                                    </div>
                                </a>
                                <ul className="nav-main-submenu">                                    
                                    <div className="block-content">
                                                {
                                                    getEstadoActuacion && 
                                                    (
                                                        estadoActualActuacion !== global.Constants.ESTADOS_ACTUACION.RECHAZADA  && 
                                                        estadoActualActuacion !== global.Constants.ESTADOS_ACTUACION.APROBADA && 
                                                        estadoActualActuacion !== global.Constants.ESTADOS_ACTUACION.APROBADA_PDF_DEFINITIVO && 
                                                        estadoActualActuacion !== global.Constants.ESTADOS_ACTUACION.SOLICITUD_INACTIVACION_RECHAZADA
                                                    ) 
                                                    ?
                                                        <div className='row' style={{ paddingBottom: '20px' }}>
                                                            <Link to="/AgregarSemaforo/Add" state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, actuacionIdMaestra: actuacionIdMaestra }} style={{ marginLeft: 'auto' }}>
                                                                <button type="button" className="btn btn-rounded btn-primary">AGREGAR</button>
                                                            </Link>
                                                        </div>
                                                    : 
                                                    null
                                                }

                                        <div className='row'>
                                            <div className='col-md-12'>
                                                <DataTable id="tablaSemaforoActuacion" className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                                    columns={columns}
                                                    data={getListaSemaforos.data.filter((suggestion) => {
                                                        return suggestion;
                                                    })}
                                                    perPage={perPage}
                                                    page={pageActual}
                                                    pagination
                                                    noDataComponent="Sin datos"
                                                    paginationTotalRows={getListaSemaforos.data.length}
                                                    onChangePage={handlePageChange}
                                                    onChangeRowsPerPage={handlePerRowsChange}
                                                    defaultSortFieldId="Nombre"
                                                    striped
                                                    paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                                    defaultSortAsc={false}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </ul>
                            </li>
                        </div>
                    </Form >
                )
                }
            </Formik >
        </>
    );
}

export default ActuacionesVer;
