import { Link } from "react-router-dom";
import { useLocation } from 'react-router-dom'
import AntecedentesLista from "../RamasProceso/Antecedentes/AntecedentesLista";

function ListaBotones(props) {

    const location = useLocation()
    const { from } = location.state

    const registrar = "/"+props.registrar+"/";
    const lista_activos = "/"+props.registrar+"/";
    const lista_inactivos = "/"+props.registrar+"/";
    const adjunta_documentos = "/"+props.registrar+"/";

    return (
        <>
            <div className='col-md-9 text-right'>
                <Link to={registrar} state={{ from: from }}>
                    <button type="button" title='Agregar Antecedente' className="btn btn-primary"> <i className="fas fa-plus"></i> </button>
                </Link>

                <Link to={`/AntencentesLista/`}>
                    { from.estadoActividad = true}
                    <button type="button" title='Antecedentes activos' className="btn btn-primary"> <i className="fas fa-list"></i> </button>
                </Link>

                <Link to={`/AntencentesForm/`} state={{ from: from }}>
                    <button type="button" title='Adjuntar documento(s)' className="btn btn-primary"> <i className="fas fa-paperclip"></i> </button>
                </Link>

                <Link to={`/AntencentesForm/`} state={{ from: from }}>
                    <button type="button" title='Repositorio de documentos' className="btn btn-primary"> <i className="fas fa-folder"></i> </button>
                </Link>
                
                <Link to={`/AntencentesForm/`} state={{ from: from }}>
                    <button type="button" title='Historial' className="btn btn-primary"> <i className="fas fa-history"></i> </button>
                </Link>
                
                <button type="button" title='Antecedentes inactivos' className="btn btn-danger"> <i className="fas fa-list"></i> </button>
                
                <Link to={`/RamasProceso/`} title='Regresar a ramas del proceso' state={{ from: from }}>
                    <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                </Link>
            </div>
        </>
    );
}

export default ListaBotones;
