import React, { useEffect, useState, } from 'react';
import { ErrorMessage, Form, Field, Formik } from 'formik';
import { Link } from 'react-router-dom';
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import Spinner from '../Utils/Spinner';
import PendientesApi from "./../Api/Services/PendientesApi";
import { getUser } from '../../components/Utils/Common';
import { ParametroModel } from '../Models/ParametroModel';
import ModalRemitirExpediente from '../Utils/Modals/ModalRemitirExpediente';
import { hasAccess } from '../../components/Utils/Common';
import DataTable from 'react-data-table-component';
import GenericApi from './../Api/Services/GenericApi';
function TrasladoProcesos() {

    const [errorApi, setErrorApi] = useState('');
    const [formIntialValuesTraslado, setFormIntialValuesTraslado] = useState();
    const [getListaUsuarios, setListaUsuarios] = useState({ data: {} });
    const [getRespuestaUsuarios, setRespuestaUsuarios] = useState(false);

    const columns = [

    ];

    useEffect(() => {
        async function fetchData() {

        }
        fetchData();
    }, []);

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;
        if (formIntialValuesTraslado) {

            data = {

                "data": {
                    "type": "user",
                    "attributes": {
                        "nombre": formIntialValuesTraslado ? (formIntialValuesTraslado.nombre ? formIntialValuesTraslado.nombre : '') : '',
                        "apellido": formIntialValuesTraslado ? (formIntialValuesTraslado.apellido ? formIntialValuesTraslado.apellido : '') : ''

                    }
                }
            }
        }




        console.log(JSON.stringify(data));

        GenericApi.getByDataGeneric('usuario/usuario-filter', data).then(
            datos => {

                if (!datos.error) {
                    setListaUsuarios(datos);
                    setRespuestaUsuarios(true);
                }
                else {
                    setErrorApi(datos.error.toString())
                    console.log(datos.error);
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )
    }


    const listarCoincidencias = () => {


        if (getListaUsuarios != null && typeof (getListaUsuarios) != 'undefined') {
            return (

                getListaUsuarios.data.map((usuario, i) => {
                    return (

                        <tr key={usuario.id}>
                            <td>{usuario.attributes.nombre}</td>
                            <td>{usuario.attributes.apellido}</td>
                            <td>{usuario.attributes.email}</td>
                            <td>{usuario.attributes.dependencia.nombre}</td>

                            <td>  <Link to={`/CasosActivos/${usuario.attributes.name}`}>
                                <button type='button' data-dismiss="modal" className='btn btn-sm btn-primary' >Ver casos activos</button>
                            </Link></td>
                        </tr >
                    )
                })
            )
        }


    }

    return (
        <>
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik
                initialValues={{
                    nombre: '',
                    apellido: '',
                }}
                enableReinitialize
                validate={(valores) => {
                    setFormIntialValuesTraslado(valores);
                    let errores = {}

                    if (!valores.nombre && !valores.apellido) {
                        errores.nombre = 'Debe ingresar nombre o apellido'
                    }

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {

                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>


                        <div className="block block-rounded block-bordered">

                            <div className="block-content block-content-full">
                                <div className="row">
                                    <div className="col-md-12 text-center mb-3">
                                        <label>PASO 1. Busca el usuario para cargar sus procesos y trasladarlos:</label>
                                    </div>
                                </div>
                                <div className="row">
                                    <ErrorMessage name="nombre" component={() => (<span className="text-danger">{errors.nombre}</span>)} />
                                </div>
                                <div className="row">

                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor='nombre'>Nombre </label>
                                            <Field type="text" id="nombre" name="nombre" className="form-control" placeholder="Nombre" />

                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="form-group">
                                            <label htmlFor='apellido'>Apellido </label>
                                            <Field type="text" id="apellido" name="apellido" className="form-control" placeholder="Apellido" />
                                        </div>
                                    </div>


                                </div>
                                <div className="block-content block-content-full text-right">
                                    <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Buscar</button>
                                </div>

                                <div className="block-content block-content-full text-center">
                                    <div className="row">
                                        <div className="col-md-12">
                                            {
                                                (getListaUsuarios.data.length > 0) ? (
                                                    <div>
                                                        <hr />
                                                        <span>Se han encontrado las siguiente coincidencias, seleccione el usuario para ver sus casos activos.</span>

                                                        <div className="row mt-2" >

                                                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                                <thead>
                                                                    <tr>
                                                                        <th>Nombre</th>
                                                                        <th>Apellido</th>
                                                                        <th>Correo</th>
                                                                        <th>Dependencia</th>
                                                                        <th>Acciones</th>
                                                                    </tr>
                                                                </thead>
                                                                <tbody >
                                                                    {getRespuestaUsuarios ? listarCoincidencias() : null}
                                                                </tbody>
                                                            </table>

                                                        </div>


                                                    </div>
                                                ) : null
                                            }
                                        </div>
                                    </div>
                                </div>
                            </div>




                        </div>
                    </Form>
                )}
            </Formik>
        </>
    )

}

export default TrasladoProcesos;
