import React, { useEffect, useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useLocation } from 'react-router-dom'
import LogApi from '../Api/Services/LogApi';
import moment from 'moment';
import { Link } from "react-router-dom";
import DataTable from 'react-data-table-component';

function LogEtapaLista() {


    const [errorApi, setErrorApi] = useState('');

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId; 

    const [getRespuestaLog, setRespuestaLog] = useState(false);
    const [getLogLista, setLogLista] = useState({ data: [], links: [], meta: [] });

    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);


    const columns = [

        {
            name: 'Etapa',
            selector: log => log.attributes.etapa.nombre,
            sortable: true,
            width: "20%"
        },

        {
            name: 'Fecha',
            selector: log => log.attributes.created_at,
            sortable: true,
            width: "20%"
        },

        {
            name: 'Funcionario Registra',

            cell: log => <div>{log.attributes.funcionario_registra.name}<br />
                {log.attributes.dependencia_origen.nombre}</div>,
            sortable: true,
            width: "20%"
        },

        {
            name: 'Funcionario Actual',

            cell: log => <div>{log.attributes.funcionario_actual.name}<br />
                {log.attributes.dep_funcionario_actual.nombre}</div>,
            sortable: true,
            width: "20%"
        },

        {
            name: 'Descripción',
            selector: log => log.attributes.descripcion,
            sortable: true,
            width: "20%"
        },
        {
            name: 'Estado Etapa',
            selector: log => log.attributes.estado_etapa.nombre,
            sortable: true,
            width: "15%",
        }  
    ];


    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const [getLista, setLista] = useState({ data: [], links: [], meta: [] });
    const [getListaTotal, setListaTotal] = useState({ data: [], links: [], meta: [] });

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setLista(getListaTotal);
            }
            else {
                var filteredData = {
                    data: getListaTotal.data.filter(
                        suggestion => ((suggestion.attributes.descripcion
                            + suggestion.attributes.nombre_dependencia + suggestion.attributes.etapa.nombre +
                            suggestion.attributes.estado + suggestion.attributes.fecha_registro).toLowerCase().includes(event.target.value.toLowerCase()))
                    )
                };
                setLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }

    };



    useEffect(() => {
        async function fetchData() {

            cargarLogProcesoDisciplinario(1, 10, "1")

        }
        fetchData();
    }, []);

    const cargarLogProcesoDisciplinario = (page, perPage, estado) => {

        const data = {
            "data": {
                "type": "log_etapa",
                "attributes": {
                    "descripcion": "descripcion",
                    "id_etapa": "fecha_registro",
                    "id_fase": "id_dependencia",
                    "id_proceso_disciplinario": "id_proceso_disciplinario",
                    "id_tipo_log": "id_tipo_log",
                    "id_estado": "id_estado",
                    "created_user": "created_user",
                    "id_dependencia_origen": "id_dependencia_origen",
                    'per_page': perPage,
                    'current_page': page
                }
            }
        }

        LogApi.getLogEtapaProcesoDisciplinario(data, procesoDisciplinarioId).then(
            datos => !datos.error ? (setLogLista(datos), setRespuestaLog(true)) : window.showModal(1)
        )
    }

    const listaLog = () => {

        if (getLogLista.data != null && typeof (getLogLista.data) != 'undefined') {
            return (

                getLogLista.data.map((log, i) => {
                    return (
                        cargarLog(log, i)
                    )
                })
            )
        }
        else {
            return (

                getLogLista.map((log, i) => {
                    return (
                        cargarLog(log, i)
                    )
                })
            )
        }
    }

    const cargarLog = (log, i) => {

        return (
            <>

                <tr>
                    <td>
                        {(log.attributes.etapa ? log.attributes.etapa.nombre : "")}
                    </td>
                    <td>
                        {log.attributes.created_at}
                    </td>
                    <td>
                        {(log.attributes.funcionario_registra ? log.attributes.funcionario_registra.name : "")}<br/>
                        {(log.attributes.dependencia_origen ? log.attributes.dependencia_origen.nombre : "")}
                    </td>

                    <td>
                        {(log.attributes.funcionario_actual ? log.attributes.funcionario_actual.name : "")}<br/>
                        {(log.attributes.dep_funcionario_actual ? log.attributes.dep_funcionario_actual.nombre : "")}
                    </td>

                    <td>
                        {log.attributes.descripcion}
                    </td>

                    <td>
                        {(log.attributes.estado_etapa ? log.attributes.estado_etapa.nombre : "")}
                    </td>
                </tr>

                
                               
            </>
        )
    }


    return (
        <>

            <div className="block block-themed">
                    <div className="block-header">
                        <h3 className="block-title">Log Proceso disciplinario :: </h3>
                    </div>
                    <div className="block-content">

                    <div className='row'>

                        <div className='col-md-3'>
                            <div className="form-group ">
                                
                            </div>
                        </div>

                    </div>

                        <div className='col-md-12'>

                        <div className='col-md-12 mt-2 mb-2'>
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={getAntecedentesLista.data}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationServer
                                paginationTotalRows={getAntecedentesLista.meta ? getAntecedentesLista.meta.total : 1}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                icon
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                defaultSortAsc={false}
                            />
                        </div>



                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                <thead>
                                    <tr>

                                        <th>Etapa</th>
                                        <th>Fecha</th>
                                        <th>Funcionario Registra</th>
                                        <th>Funcionario Actual</th>
                                        <th>Descripción</th>
                                        <th>Estado del trámite</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {getRespuestaLog ? listaLog() : null}

                                </tbody>
                            </table>
                        </div>
                    </div>
            </div>

        </>
    );
}

export default LogEtapaLista;
