import React, { useEffect, useState } from 'react';
import Spinner from '../../Utils/Spinner';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { Link } from "react-router-dom";
import GenericApi from '../../Api/Services/GenericApi';
import { useLocation } from 'react-router-dom';
import { getUser } from '../../../components/Utils/Common';

function ActuacionesAprobacionRechazar() {

    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [countTextArea, setCountTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getIdDependenciaAtiendaAnulaciones, setIdDependenciaAtiendaAnulaciones] = useState(0);
    const [getNameUsuario, setNameUsuario] = useState("");
    const [getIdActuacion, setIdActuacion] = useState("");
    const [getNombreUsuario, setNombreUsuario] = useState("");
    const [getIdDependenciaSolicitante, setIdDependenciaSolicitante] = useState("");
    const [getUsuarioNombre, setUsuarioNombre] = useState("");
    const [getUsuarioApellido, setUsuarioApellido] = useState("");
    const [getUsuarioName, setUsuarioName] = useState("");
    const [getUsuarioDependenciaNombre, setUsuarioDependenciaNombre] = useState("");
    const [getActuacionCierraProceso, setActuacionCierraProceso] = useState("");
    const [getid_funcionario_asignado, setid_funcionario_asignado] = useState("");

    const location = useLocation()
    const { getActuacionConFirmas , from, selected_id_etapa, id, nombre, estadoActualActuacion, titulo, valor, solicitante, tipoActuacion } = location.state;

    let procesoDisciplinarioId = from.procesoDisciplinarioId;

    const [getRoutes, setRoutes] = useState({
        ver_detalle: "/ActuacionesVer/" + from.procesoDisciplinarioId + "/" + selected_id_etapa + "/1"
    });

    useEffect(() => {
        async function fetchData() {
            obtenerParametros();
            obtenerDatosUsuarioSolicitante();
            obtenerDatos();

            GenericApi.getGeneric('usuario/get-usuario-por-name/' + getUser().nombre).then(
                datos => {
                    if (!datos.error) {
                        setid_funcionario_asignado(datos.data[0].id);
                    } else {

                    }
                }
            )

        }
        fetchData();
    }, []);


    const obtenerDatosUsuarioSolicitante = () => {

        // Buscamos el parametro
        GenericApi.getGeneric("usuario/get-usuario-por-name/" + solicitante).then(
            datos => {
                if (!datos.error) {
                    if (datos["data"].length > 0) {
                        setIdDependenciaSolicitante(datos.data[0].attributes.id_dependencia);
                        datosDelUsuarioARemitir(datos.data[0].attributes.name);
                    }
                } else {

                }
            }
        )
    }

    const datosDelUsuarioARemitir = (name) => {

        GenericApi.getGeneric('usuario/get-usuario-por-name/' + name).then(
            datos => {
                if (!datos.error) {
                    console.log(datos);
                    setUsuarioNombre(datos.data[0].attributes.nombre);
                    setUsuarioApellido(datos.data[0].attributes.apellido);
                    setUsuarioName(datos.data[0].attributes.name);
                    setUsuarioDependenciaNombre(datos.data[0].attributes.dependencia.nombre);
                } else {

                }
            }
        )
    }

    function containsSpecialChars(str) {

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }

    const obtenerParametros = () => {

        window.showSpinner(true);

        try {
            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            // Buscamos el parametro
            GenericApi.getByDataGeneric("parametro/parametro-nombre", data).then(
                datos => {

                    window.showSpinner(false);

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        setModalState({ title: "Actuaciones", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            console.log(error);
        }
    }

    const enviarDatos = (valores) => {

        console.log(getActuacionCierraProceso);
        window.showSpinner(true);

        let data = {
            "data": {
                "type": "trazabilidad-actuaciones",
                "attributes": {
                    "uuid_actuacion": id,
                    "id_estado_actuacion": valor == 1 ? 1 : 2,
                    "observacion": valores.informacion,
                    "estado": 1,
                    "envia_correo": 1,
                    "id_proceso_disciplinario": procesoDisciplinarioId
                }
            }
        }

        GenericApi.addGeneric('trazabilidad-actuaciones', data).then(
            datos => {
                if (!datos.error) {
                    if (!datos.error) {
                        window.showSpinner(false);

                        let validacion = (valor == 1 ? 'aprobada' : 'rechazada');
                        let mensaje = 'La actuación ' + nombre + ' ha sido ' + validacion + ' con éxito.';
                        let tipoAct = tipoActuacion == "0" ? "Actuaciones" : (tipoActuacion == "1" ? 'Impedimento' : 'Comisorio');

                        if (valor == 1) {

                            if (getActuacionCierraProceso == 1) {

                                const datadata = {
                                    "data": {
                                        "type": "cerrar_etapa",
                                        "attributes": {
                                            "id_proceso_disciplinario": from.procesoDisciplinarioId,
                                            "id_etapa": selected_id_etapa,
                                            "id_funcionario_asignado": getid_funcionario_asignado,
                                            "descripcion": "",
                                            "created_user": ""
                                        }
                                    }
                                }

                                console.log(datadata);
                                GenericApi.addGeneric("cierre-etapa/actuaciones", datadata)



                                if (getActuacionConFirmas > 0 ) {
                                    setModalState({ title: "SINPROC No " + from.radicado + " :: " + tipoAct + " Aprobar/Rechazar", message: mensaje + " El proceso displinario fue CERRADO con exito.", show: true, redirect: '/ActuacionesSigueImpedimentos', from: { from, selected_id_etapa: selected_id_etapa, nombre: nombre, uuid_actuacion: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion}, alert: global.Constants.TIPO_ALERTA.EXITO });
                                }else if(getActuacionConFirmas == 0){
                                    setModalState({ title: "SINPROC No " + from.radicado + " :: " + tipoAct + " Aprobar/Rechazar", message: mensaje + " El proceso displinario fue CERRADO con exito.", show: true, redirect: '/ActuacionesCargarArchivoDefinitivo', from: { from, selected_id_etapa: selected_id_etapa, nombre: nombre, uuid_actuacion: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion}, alert: global.Constants.TIPO_ALERTA.EXITO });
                                } else {
                                    setModalState({ title: "SINPROC No " + from.radicado + " :: " + tipoAct + " Aprobar/Rechazar", message: mensaje + " EL proceso displinario fue CERRADO con exito.", show: true, redirect: '/ActuacionesInactivar', from: { from, selected_id_etapa: selected_id_etapa, nombre: nombre, uuid_actuacion: id, tipoActuacion: tipoActuacion }, alert: global.Constants.TIPO_ALERTA.EXITO });
                                }
                            } else {
                                if (getActuacionConFirmas > 0) {
                                    setModalState({ title: "SINPROC No " + from.radicado + " :: " + tipoAct + " Aprobar/Rechazar", message: mensaje, show: true, redirect: '/ActuacionesSigueImpedimentos', from: { from, selected_id_etapa: selected_id_etapa, nombre: nombre, uuid_actuacion: id, tipoActuacion: tipoActuacion , estadoActualActuacion: estadoActualActuacion }, alert: global.Constants.TIPO_ALERTA.EXITO });
                                }else if(getActuacionConFirmas == 0){
                                    setModalState({ title: "SINPROC No " + from.radicado + " :: " + tipoAct + " Aprobar/Rechazar", message: mensaje, show: true, redirect: '/ActuacionesCargarArchivoDefinitivo', from: { from, selected_id_etapa: selected_id_etapa, nombre: nombre, uuid_actuacion: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion}, alert: global.Constants.TIPO_ALERTA.EXITO });
                                }  else {

                                    let aprobacionRechazo = datos.attributes.despues_aprobacion_listar_actuacion;

                                    if (aprobacionRechazo == 1) {
                                        setModalState({ title: "SINPROC No " + from.radicado + " :: " + tipoAct + " Aprobar/Rechazar", message: mensaje, show: true, redirect: '/ActuacionesInactivar', from: { from, selected_id_etapa: selected_id_etapa, nombre: nombre, uuid_actuacion: id, tipoActuacion: tipoActuacion }, alert: global.Constants.TIPO_ALERTA.EXITO });
                                    } else {
                                        setModalState({ title: "SINPROC No " + from.radicado + " :: " + tipoAct + " Aprobar/Rechazar", message: mensaje, show: true, redirect: '/ActuacionesCargarArchivoDefinitivo', from: { from, selected_id_etapa: selected_id_etapa, nombre: nombre, uuid_actuacion: id, tipoActuacion: tipoActuacion , estadoActualActuacion: estadoActualActuacion}, alert: global.Constants.TIPO_ALERTA.EXITO });
                                    }
                                }
                            }

                        } else {
                            window.showSpinner(true);

                            if (tipoActuacion == 1 || tipoActuacion == 2) {
                                setModalState({ title: "SINPROC No " + from.radicado + " :: " + tipoAct + " Aprobar/Rechazar", message: mensaje, show: true, redirect: '/Transacciones', from: { from: from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.EXITO });
                            } else {
                                let dataRechazo = {
                                    "data": {
                                        "type": "transacciones",
                                        "attributes": {
                                            "id_proceso_disciplinario": from.procesoDisciplinarioId,
                                            "id_dependencia_origen": getIdDependenciaSolicitante,
                                            "usuario_a_remitir": solicitante,
                                            "descripcion_a_remitir": valores.informacion,
                                            "id_etapa": 3
                                        }
                                    }
                                }

                                GenericApi.addGeneric('transacciones/cambiar-usuario-proceso-disciplinario', dataRechazo).then(
                                    datos => {
                                        window.showSpinner(false);
                                        if (!datos.error) {
                                            // setModalState({ title: "SINPROC No " + from.radicado + " :: Actuaciones Aprobar/Rechazar", message: mensaje, show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.EXITO });
                                            setModalState({ title: "SINPROC No " + from.radicado + " :: Enviar al solicitante de anulación", message: mensaje + ' El proceso con radicado: ' + from.radicado + ' fue asignado al usuario: ' + getUsuarioNombre + ' ' + getUsuarioApellido + ' (' + getUsuarioName + ')  de la dependencia: ' + getUsuarioDependenciaNombre + '', show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.EXITO });


                                        } else {
                                            setModalState({ title: "SINPROC No " + from.radicado + " :: Enviar al solicitante de anulación", message: datos.error.toString(), show: true, redirect: '/MisPendientes', from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                                        }
                                    }
                                )
                            }
                        }
                    } else {
                        setModalState({ title: "SINPROC No " + from.radicado + " :: Actuaciones Aprobar/Rechazar", message: datos.error.toString(), show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            }
        )


    }

    const obtenerDatos = () => {
        GenericApi.getByIdGeneric('actuaciones', id).then(
            datos => {
                if (!datos.error) {
                    GenericApi.getByIdGeneric('mas_actuaciones', datos.data.attributes.id_actuacion).then(
                        datos => {
                            if (!datos.error) {
                                setActuacionCierraProceso(datos.data.attributes.cierra_proceso);
                            }
                        }
                    );
                }
            }
        );
    }

    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            <Formik
                initialValues={{
                    informacion: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    setCountTextArea(valores.informacion.length)
                    let errores = {}
                    if (!valores.informacion) {
                        errores.informacion = 'Debe ingresar un valor';
                    }
                    else if (valores.informacion.length <= getMinimoTextArea) {
                        errores.informacion = 'La descripción debe tener almenos ' + getMinimoTextArea + ' caracteres';
                    }
                    if (valores.informacion) {
                        if (containsSpecialChars(valores.informacion))
                            errores.informacion = 'Tiene caracteres inválidos';
                    }

                    return errores;
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`}><small>Ramas del proceso</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ActuacionesLista/`} state={{ from: from, selected_id_etapa: selected_id_etapa }}><small>Actuaciones</small></Link></li>
                                    <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={getRoutes.ver_detalle} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion }}><small>Actuación {nombre}</small></Link></li>
                                    <li className="breadcrumb-item"> <small>{tipoActuacion == 0 ? "Actuaciones" : (tipoActuacion == 1 ? 'Impedimentos' : 'Comisorio')} Aprobar/Rechazar</small></li>
                                </ol>
                            </nav>
                        </div>

                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">{tipoActuacion == 0 ? "Actuación" : (tipoActuacion == 1 ? 'Impedimento' : 'Comisorio')} {nombre} :: {titulo} </h3>
                            </div>
                            <div className="block-content">
                                <div className="row">
                                    <div className="col-md-12 mt-2">
                                        <div className="form-group">
                                            <label htmlFor="informacion">Motivo porqué se solicita {valor == 1 ? "aprobación" : "rechazo"} <span className="text-danger mt-2">*</span></label>
                                            <Field as="textarea" className="form-control mt-2" id="informacion" name="informacion" rows="6" placeholder="Información para su solicitud...."
                                                maxLength={getMaximoTextArea} minLength={getMinimoTextArea}></Field>
                                            <div className="text-right">
                                                <span className="text-primary">{countTextArea} / {getMaximoTextArea}</span>
                                            </div>
                                            <ErrorMessage name="informacion" component={() => (<span className="text-danger">{errors.informacion}</span>)} />
                                        </div>
                                    </div>
                                </div>
                                <div className="block-content block-content-full text-right">
                                    <button type="submit" className="btn btn-primary" >Aceptar</button>
                                    <Link to={getRoutes.ver_detalle} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, tipoActuacion: tipoActuacion }} className="font-size-h5 font-w600" >
                                        <button type="button" className="btn btn-danger" >Cancelar</button>
                                    </Link>
                                </div>
                            </div>
                        </div>

                    </Form>
                )}
            </Formik>
        </>


    );
}

export default ActuacionesAprobacionRechazar;
