import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom'
import DataTable from 'react-data-table-component';
import GenericApi from '../../Api/Services/GenericApi';
import Spinner from '../../Utils/Spinner';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import '../../Utils/Constants';
import { getUser } from '../../Utils/Common';

function ActuacionParametrosPlantillaForm(props) {
    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let id_etapa = from.idEtapa;
    let fechaRegistro = from.fechaRegistro;
    let fechaIngreso = from.fechaIngreso;
    let dependencia = getUser().nombre_dependencia ? getUser().nombre_dependencia.nombre : "";

    const [modalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getIdMasActuacion, setIdMasActuacion] = useState(props.id_mas_actuacion);
    const [getListaParametros, setListaParametros] = useState({ params: [] });
    const [getListaCamposPorParametro, setListaCamposPorParametro] = useState({ data: [] });
    const [getAntecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });
    const [getInteresadosLista, setInteresadosLista] = useState({ data: [], links: [], meta: [] });
    const [getEntidadesInvestigadoLista, setEntidadesInvestigadoLista] = useState({ data: [], links: [], meta: [] });

    const campos = [
        { title: 'Antecendentes', value: null, type: "Antecedentes" },
        { title: 'Dependencia', value: dependencia },
        { title: 'Investigados (Nombre)', value: null, type: "Investigados" },
        { title: 'Investigados (Cargo)', value: null, type: "CargosInvestigados" },
        { title: 'Investigados (Entidad)', value: null, type: "EntidadesInvestigados" },
        { title: 'Interesados', value: null, type: "Interesados" },
        { title: 'Fecha de ingreso', value: fechaIngreso },
        { title: 'Fecha de registro', value: fechaRegistro },
        { title: 'Número de auto (generado despues de aprobación)', value: '${numero_de_auto}' },
        { title: 'Número de radicado', value: radicado },
        //{ id: 8, title: 'Campo de entrada por usuario', value: null },
    ];

    useEffect(() => {
        async function fetchData() {
            setIdMasActuacion(props.id_mas_actuacion);
            cargarParametros();
            cargarAntecedentes();
            cargarInteresados();
            cargarEntidadesInvestigado();
        }
        fetchData();
    }, [props.id_mas_actuacion]);



    const selectCampos = () => {
        return (
            campos.map((item, i) => {
                return (
                    <option key={i} value={i}>{item.title}</option>
                )
            })
        )
    }

    const selectAntecedentes = () => {
        if (Array.isArray(getAntecedentesLista)) {
            return (
                getAntecedentesLista.map((item, i) => {
                    return (
                        <option key={item.attributes.descripcion} value={item.attributes.descripcion}>{item.attributes.descripcion}</option>
                    )
                })
            )
        }
    }

    const selectInteresados = () => {
        if (Array.isArray(getInteresadosLista)) {
            return (
                getInteresadosLista.map((item, i) => {
                    var nombre = `${item.attributes.sujeto_procesal_nombre} ${item.attributes.primer_apellido} ${item.attributes.segundo_apellido} ${item.attributes.primer_nombre} ${item.attributes.segundo_nombre}`;
                    return (
                        <option key={nombre} value={nombre}>{nombre}</option>
                    )
                })
            )
        }
    }

    const selectInvestigados = () => {
        if (Array.isArray(getEntidadesInvestigadoLista)) {
            return (
                getEntidadesInvestigadoLista.map((item, i) => {
                    var value = `${item.attributes.nombre_investigado}`;
                    var nombre = `${item.attributes.nombre_investigado} - ${item.attributes.cargo} (${item.attributes.nombre_entidad})`;
                    return (
                        <option key={value} value={value}>{nombre}</option>
                    )
                })
            )
        }
    }

    const selectCargosInvestigados = () => {
        if (Array.isArray(getEntidadesInvestigadoLista)) {
            return (
                getEntidadesInvestigadoLista.map((item, i) => {
                    var value = `${item.attributes.cargo}`;
                    var nombre = `${item.attributes.nombre_investigado} - ${item.attributes.cargo} (${item.attributes.nombre_entidad})`;
                    return (
                        <option key={value} value={value}>{nombre}</option>
                    )
                })
            )
        }
    }

    const selectEntidadesInvestigados = () => {
        if (Array.isArray(getEntidadesInvestigadoLista)) {
            return (
                getEntidadesInvestigadoLista.map((item, i) => {
                    var value = `${item.attributes.nombre_entidad}`;
                    var nombre = `${item.attributes.nombre_investigado} - ${item.attributes.cargo} (${item.attributes.nombre_entidad})`;
                    return (
                        <option key={value} value={value}>{nombre}</option>
                    )
                })
            )
        }
    }


    const cargarValoresDeParametro = (parametro) => {
        var campoParametro = getListaCamposPorParametro.data.filter(item => {
            return item.parametro == parametro;
        });
        var tempCampo = campoParametro[0];
        if (tempCampo != undefined && campos[tempCampo.id_campo] != undefined) {
            var itemCampo = campos[tempCampo.id_campo];
            //tiene un valor por defecto
            if (itemCampo.value != null) {
                return (
                    <>{itemCampo.value}</>
                )
            }
            else if (itemCampo.type == "Antecedentes") {
                return (
                    <select className="form-control" id={`antece-${parametro}`} name={`antece-${parametro}`}
                        onChange={e => selectChangeListaCampos(parametro, tempCampo.id_campo, e.target.value)}>
                        <option value="">Por favor seleccione</option>
                        {selectAntecedentes()}
                    </select>
                )
            }
            else if (itemCampo.type == "Interesados") {
                return (
                    <select className="form-control" id={`inter-${parametro}`} name={`inter-${parametro}`}
                        onChange={e => selectChangeListaCampos(parametro, tempCampo.id_campo, e.target.value)}>
                        <option value="">Por favor seleccione</option>
                        {selectInteresados()}
                    </select>
                )
            }
            else if (itemCampo.type == "Investigados") {
                return (
                    <select className="form-control" id={`ei-${parametro}`} name={`ei-${parametro}`}
                        onChange={e => selectChangeListaCampos(parametro, tempCampo.id_campo, e.target.value)}>
                        <option value="">Por favor seleccione</option>
                        {selectInvestigados()}
                    </select>
                )
            }
            else if (itemCampo.type == "CargosInvestigados") {
                return (
                    <select className="form-control" id={`ei-${parametro}`} name={`ei-${parametro}`}
                        onChange={e => selectChangeListaCampos(parametro, tempCampo.id_campo, e.target.value)}>
                        <option value="">Por favor seleccione</option>
                        {selectCargosInvestigados()}
                    </select>
                )
            }
            else if (itemCampo.type == "EntidadesInvestigados") {
                return (
                    <select className="form-control" id={`ei-${parametro}`} name={`ei-${parametro}`}
                        onChange={e => selectChangeListaCampos(parametro, tempCampo.id_campo, e.target.value)}>
                        <option value="">Por favor seleccione</option>
                        {selectEntidadesInvestigados()}
                    </select>
                )
            }
            else {
                return (
                    <label>Seleccione un campo</label>
                )
            }
        }
        return (<></>)
    }

    const selectChangeListaCampos = (parametro, id_campo, value) => {
        //console.log("selectChangeListaCampos >> " + parametro + " " + id_campo + " " + value);
        var newParams = getListaCamposPorParametro.data.map(item => {
            if (item.parametro == parametro) {
                item.id_campo = id_campo;
                var itemCampo = campos[id_campo];
                item.valor = (itemCampo.value != null) ? itemCampo.value : value;
            }
            return item;
        });
        setListaCamposPorParametro({ data: newParams });
        //console.log(newParams);
    }

    const columns = [
        {
            name: 'Parametros de la plantilla',
            cell: parametro => <div><strong>{parametro}</strong></div>,
            sortable: true,
            width: "15%"
        },
        {
            name: 'Campo',
            cell: parametro =>
                <select className="form-control" id={`id${parametro}`} name={`name${parametro}`}
                    onChange={e => selectChangeListaCampos(parametro, e.target.value, "")}>
                    <option value="">Por favor seleccione</option>
                    {selectCampos()}
                </select>,
            sortable: false,
            width: "25%",
        },
        {
            name: 'Valor seleccionado',
            width: "60%",
            cell: parametro =>
                <div className='row'>
                    <div className='col'>
                        {cargarValoresDeParametro(parametro)}
                    </div>
                </div>
        }
    ];


    const cargarParametros = () => {
        const data = {
            "data": {
                "type": 'mas_actuaciones',
                "attributes": {
                    "id": getIdMasActuacion
                }
            }
        }
        GenericApi.getByDataGeneric('mas_actuaciones/parametros-plantilla/' + getIdMasActuacion, data).then(
            datos => {
                if (!datos.error) {
                    setListaParametros(datos);
                    var parametrosCampos = datos.params.map((item, i) => {
                        return { parametro: item, id_campo: "", valor: "" };
                    });
                    //console.log(parametrosCampos);
                    setListaCamposPorParametro({ data: parametrosCampos });
                }
            }
        )
    }

    const cargarAntecedentes = () => {
        const data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": "antecedentes",
                    "descripcion": "descripcion",
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "id_dependencia": -1,
                    "estado": "1",
                    'per_page': 100,
                    'current_page': 1
                }
            }
        }
        GenericApi.getByDataGeneric('antecedentes/get-antecedentes/' + procesoDisciplinarioId, data).then(
            datos => {
                if (!datos.error) {
                    setAntecedentesLista(datos.data);
                }
            }
        )
    }

    const cargarInteresados = () => {
        const data = {
            "data": {
                "type": "interesado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "tipo_documento": "1",
                    "numero_documento": "1",
                    "primer_nombre": "1",
                    "segundo_nombre": "1",
                    "primer_apellido": "1",
                    "segundo_apellido": "1",
                    "estado": "1",
                    'per_page': 100,
                    'current_page': 1
                }
            }
        }

        GenericApi.getByDataGeneric('datos-interesado/datos-interesado/' + procesoDisciplinarioId, data).then(
            datos => {
                if (!datos.error) {
                    setInteresadosLista(datos.data);
                } else {
                    setModalState({ title: "Datos del interesado", message: datos.error.toString(), show: true, redirect: null, from: { from } });
                }
            }
        )
    }


    const cargarEntidadesInvestigado = () => {
        const data = {
            "data": {
                "type": "entidad_investigado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": "1",
                    "requiere_registro": true,
                    "estado": "1",
                    'per_page': 100,
                    'current_page': 1
                }
            }
        }

        GenericApi.getByDataGeneric("entidad-investigado/get-entidad-investigado/" + procesoDisciplinarioId, data).then(
            datos => {
                if (!datos.error) {
                    setEntidadesInvestigadoLista(datos.data)
                }
                else {
                    setModalState({ title: "Entidades del investigado", message: datos.error.toString(), show: true, redirect: null, from: { from } });
                }

            }
        )
    }

    const descargarArchivo = () => {
        try {
            window.showSpinner(true);

            var params = getListaCamposPorParametro.data.map(item => {
                return {
                    "param": item.parametro,
                    "value": item.valor
                };
            });
            console.log("######params#####");
            console.log(params);

            const data = {
                "data": {
                    "type": "mas_actuaciones",
                    "attributes": {
                        "params": params,
                    }
                }
            }
            GenericApi.getByDataGeneric('mas_actuaciones/plantilla-diligenciada/' + getIdMasActuacion, data).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64File(datos.content_type, datos.base_64, datos.file_name, "docx");
                        window.showSpinner(false);
                    }
                    else {
                        setModalState({ title: "SINPROC No " + radicado + " :: ", message: datos.error.toString(), show: true, redirect: null, from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64File(contentType, base64Data, fileName, extension) {
        //console.log("Datos que recibo", contentType, base64Data, fileName, extension);
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    return (
        <>
            <Spinner />
            <ModalGen data={modalState} />

            <div className='col-md-12'>
                <label>Parametros de la plantilla</label>
            </div>
            <div className='col-md-12 mt-2 mb-2'>
                <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                    columns={columns}
                    data={getListaParametros.params}
                    noDataComponent={global.Constants.DATA_TABLE.SIN_DATOS_TABLE}
                    striped
                />
            </div>
            <div className='col-md-12 mt-2 mb-2'>
                <label htmlFor="documento_ruta">Descargar documento pre-diligenciado con los valores seleccionados </label>
                <button type="button" title='Descargar documento diligenciado' onClick={() => descargarArchivo()} className="btn btn-primary ml-2"> <i className="fas fa-file-word"></i></button>
            </div>
        </>
    );
}

export default ActuacionParametrosPlantillaForm;
