import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import InfoErrorApi from '../Utils/InfoErrorApi';
import GenericApi from '../Api/Services/GenericApi';
import 'moment/locale/es';
import DataTable from 'react-data-table-component';

function TipoDocumentoLista() {
    const [tipoDocumentoLista, setTipoDocumentoLista] = useState({ data: [] });
    const [tipoDocumentoListaTotal, setTipoDocumentoListaTotal] = useState({ data: [], links: [], meta: [] });
    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);

    const columns = [
        {
            name: 'Nombre',
            selector: tipoDoc => tipoDoc.attributes.nombre,
            sortable: true,
            width: "50%"
        },
        {
            name: 'Estado',
            selector: tipoDoc => (tipoDoc.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
        },
        {
            name: 'Acciones',
            cell: row => <div><Link to={`${row.id}`}>
                <button type="button" className="btn btn btn-primary" title='Editar'>
                    <i className="fa fa-fw fa-edit"></i>
                </button>
            </Link></div>
        }
    ];

    useEffect(() => {
        async function fetchData() {

            cargarTiposDocumentos(1, 10);

        }
        fetchData();
    }, []);

    const cargarTiposDocumentos = (page, perPage) => {
        GenericApi.getAllGeneric('tipo-documento/tipo-documento-paginate' + '/' + page + '/' + perPage).then(
            datos => {
                if (!datos.error) {
                    setTipoDocumentoLista(datos);
                    setTipoDocumentoListaTotal(datos);
                }
                else
                    window.showModal()
            }
        )
    }

    const handlePageChange = page => {
        setPageActual(page);
        cargarTiposDocumentos(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {

        setPerPage(newPerPage);
        setPageActual(page);
        cargarTiposDocumentos(page, newPerPage);

    }

    const tipoDocumento = () => {
        return (
            tipoDocumentoLista.data.map((tipoDocumento, i) => {
                return (
                    <tr key={tipoDocumento.id}>
                        <td>{tipoDocumento.attributes.nombre}</td>
                        <td>
                            {tipoDocumento.attributes.estado == "1" ? 'Activo' : 'Inactivo'}
                        </td>
                        <td>
                            <Link to={`${tipoDocumento.id}`}>
                                <button type="button" className="btn btn-primary" title='Editar'>
                                    <i className="fa fa-fw fa-edit"></i>
                                </button>
                            </Link>
                            {/* <button type="button" className="btn btn-danger mr-1 mb-3">
                                <i className="fa fa-fw fa-trash-alt"></i>
                            </button> */}
                        </td>
                    </tr>
                )
            })
        )
    }

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {
                setTipoDocumentoLista(tipoDocumentoListaTotal);
            }
            else {
                var filteredData = {
                    data: tipoDocumentoListaTotal.data.filter(
                        suggestion => ((suggestion.id
                            + suggestion.attributes.nombre
                            + (suggestion.attributes.estado == "1" ? 'Activo' : 'Inactivo')).toLowerCase().includes(event.target.value.toLowerCase()
                            ))
                    )
                };
                setTipoDocumentoLista(filteredData)
            }
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="block block-rounded block-bordered">
            {<InfoErrorApi />}

            <div className="block block-themed">
                <div className="col-md-12">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <small>Lista de Tipo de Documentos</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
                <div className="block-header">
                    <h3 className="block-title">Tipos de Documentos registrados</h3>
                    <Link to={`/TipoDocumento/Add`} >
                        <button type="button" className="btn btn-sm btn-light mr-1 mb-3"><i class="far fa-plus-square"></i> Agregar Tipo de Documento</button>
                    </Link>
                </div>
                <div className="block-content">
                    <div className='row'>
                        <div className='col-md-12'>
                            <div className="form-group ">
                                <label htmlFor='search'>Buscar: </label>
                                <input type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-sm-12">
                            <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                columns={columns}
                                data={tipoDocumentoLista.data}
                                perPage={perPage}
                                page={pageActual}
                                pagination
                                noDataComponent="Sin datos"
                                paginationServer
                                paginationTotalRows={tipoDocumentoLista.meta ? tipoDocumentoLista.meta.total : 1}
                                onChangePage={handlePageChange}
                                onChangeRowsPerPage={handlePerRowsChange}
                                defaultSortFieldId="Nombre"
                                striped
                                icon
                                paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                defaultSortAsc={false}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default TipoDocumentoLista;
