import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import ParametrosMasApi from "./../../Api/Services/ParametrosMasApi";
import CierreEtapaApi from '../../Api/Services/CierreEtapaApi';
import { useLocation } from 'react-router-dom'
import { hasAccess } from '../../../components/Utils/Common';

function ClasificacionRadicadoLista() {


    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);

    const [listaTipoExpediente, setListaTipoExpediente] = useState({ data: {} });
    const [respuestaTipoExpediente, setTipoExpediente] = useState(false);

    const [listaTipoQueja, setListaTipoQueja] = useState({ data: {} });
    const [respuestaTipoQueja, setTipoQueja] = useState(false);

    const [listaTerminoRespuesta, setListaTerminoRespuesta] = useState({ data: {} });
    const [respuestaTerminoRespuesta, setTerminoRespuesta] = useState(false);
    const [estadoEtapaCapturaReparto, setEstadoEtapaCapturaReparto] = useState(false);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;

    const redirectToRoutes = () => {
        return <Navigate to={`/ClasificacionRadicadoLista/`} state={{ from: from }} />;
    }

    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "clasificacion_radicado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": "1",
                        "id_tipo_expediente": "1"
                    }
                }
            }

            ClasificacionRadicadoApi.getAllClasificacionRadicadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => {
                    if (!datos.error) {
                        setClasificacionRadicadoLista(datos)
                        setRespuestaClasificacionRadicado(true)
                        setClasificacionRadicadoListaTotal(datos)
                        setRespuestaClasificacionRadicadoTotal(true)
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal()
                    }
                    window.showSpinner(false);
                }
            )

            ParametrosMasApi.getAllTiposExpediente().then(
                datos => !datos.error ? (setListaTipoExpediente(datos), setTipoExpediente(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTiposQueja().then(
                datos => !datos.error ? (setListaTipoQueja(datos), setTipoQueja(true)) : window.showModal(1)
            )

            ParametrosMasApi.getAllTerminosRespuesta().then(
                datos => !datos.error ? (setListaTerminoRespuesta(datos), setTerminoRespuesta(true)) : window.showModal(1)
            )

            // VALIDA CIERRE DE ETAPA EVALUACION
            const dataCierreEtapa = {

                "data": {
                    "type": "cerrar_etapa",
                    "attributes": {
                        "id_proceso_disciplinario": "id_proceso_disciplinario",
                        "id_etapa": "id_etapa",
                        "id_delegada": "id_delegada",
                        "id_funcionario": "id_funcionario"
                    }
                }

            }

            CierreEtapaApi.getCierreEtapaByIdProDisciplinario(dataCierreEtapa, procesoDisciplinarioId, 1).then(
                datos => {
                    if (!datos.error) {
                        if (datos["data"].length > 0) {
                            setEstadoEtapaCapturaReparto(true);
                        }
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )
        }
        fetchData();
    }, []);


    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const [respuestaClasificacionRadicado, setRespuestaClasificacionRadicado] = useState(false);
    const [clasificacionRadicadoLista, setClasificacionRadicadoLista] = useState({ data: [], links: [], meta: [] });

    const [respuestaClasificacionRadicadoTotal, setRespuestaClasificacionRadicadoTotal] = useState(false);
    const [clasificacionRadicadoListaTotal, setClasificacionRadicadoListaTotal] = useState({ data: [], links: [], meta: [] });

    const handleSearch = (event) => {
        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setClasificacionRadicadoLista(clasificacionRadicadoListaTotal);
            }
            else {

                setClasificacionRadicadoLista(clasificacionRadicadoListaTotal.data.filter(
                    suggestion => ((suggestion.attributes.expediente.nombre
                        + suggestion.attributes.created_at + suggestion.attributes.estado).toLowerCase().includes(event.target.value.toLowerCase()))
                ));
            }
        } catch (error) {
            console.error(error);
        }

    };

    const listaClasificacionRadicado = () => {

        if (clasificacionRadicadoLista.data != null && typeof (clasificacionRadicadoLista.data) != 'undefined') {
            return (


                clasificacionRadicadoLista.data.map((clasificacion_radicado, i) => {
                    return (
                        cargarTabla(clasificacion_radicado)
                    )
                })
            )
        }
        else {
            return (

                clasificacionRadicadoLista.map((clasificacion_radicado, i) => {
                    return (
                        cargarTabla(clasificacion_radicado)
                    )
                })
            )
        }


    }

    const cargarTabla = (clasificacion_radicado) => {
        return (
            <tr key={clasificacion_radicado.id}>
                <td>
                    {clasificacion_radicado.attributes.expediente.nombre} {clasificacion_radicado.attributes.expediente.id === 3 || clasificacion_radicado.attributes.expediente.id === 2 ? clasificacion_radicado.attributes.tipo_queja.nombre : null}
                    {clasificacion_radicado.attributes.expediente.id === 1 ? clasificacion_radicado.attributes.tipo_derecho_peticion.nombre : null}
                    {clasificacion_radicado.attributes.expediente.id === 4 ? clasificacion_radicado.attributes.fecha_termino : null}
                    {clasificacion_radicado.attributes.expediente.id === 4 ? clasificacion_radicado.attributes.hora_termino : null}
                    <br />{clasificacion_radicado.attributes.observaciones}
                </td>
                <td>PENDIENTE</td>
                <td>{clasificacion_radicado.attributes.created_user}</td>
                <td>{clasificacion_radicado.attributes.created_at}</td>
                <td>Asignado</td>
                <td>Ver Normatividad</td>
            </tr>
        )
    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////


    return (
        <> <Formik>
            <Form>
                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Clasificacion Radicado Lista</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                <div className="block block-themed">
                    <div className="block-header">
                        <h3 className="block-title">SINPROC No {radicado} :: Clasificación del radicado</h3>
                        <>
                            {
                                (clasificacionRadicadoLista.data && hasAccess('CR_ClasificacionRadicado', 'Crear')) ? (
                                    (clasificacionRadicadoLista.data.length === 0 && hasAccess('CR_ClasificacionRadicado', 'Crear')) ? (

                                        <Link to={`/ClasificacionRadicadoForm/`} state={{ from: from }}>
                                            <button type="button" className="btn btn-sm btn-dark mr-1 mb-3"><i class="far fa-plus-square"></i> Agregar Clasificación del radicado</button>
                                        </Link>
                                    ) : null) : null
                            }
                        </>
                        <>
                            {
                                (clasificacionRadicadoLista && hasAccess('CR_ClasificacionRadicado', 'Crear')) ? (
                                    (clasificacionRadicadoLista.length === 0 && hasAccess('CR_ClasificacionRadicado', 'Crear')) ? (

                                        <Link to={`/ClasificacionRadicadoForm/`} state={{ from: from }}>
                                            <button type="button" className="btn btn-sm btn-dark mr-1 mb-3"><i class="far fa-plus-square"></i> Agregar Clasificación del radicado</button>
                                        </Link>
                                    ) : null) : null
                            }
                        </>

                    </div>

                    <div className="block-content">
                        <>
                            {
                                (hasAccess('CR_ClasificacionRadicado', 'Consultar')) ? (

                                    <div className='row'>

                                        <div className='col-md-12'>
                                            <div className="form-group ">
                                                <label htmlFor='search'>Buscar: </label>
                                                <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                            </div>
                                        </div>
                                    </div>
                                ) : null
                            }

                            {
                                (hasAccess('CR_ClasificacionRadicado', 'Consultar')) ? (

                                    <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                        <thead>
                                            <tr>
                                                <th>Tipo de expediente</th>
                                                <th>Registrado En</th>
                                                <th>Registrado por</th>
                                                <th>Fecha de registro</th>
                                                <th>Estado actual</th>
                                                <th>Termino</th>

                                            </tr>
                                        </thead>
                                        <tbody>
                                            {respuestaClasificacionRadicado ? listaClasificacionRadicado() : null}
                                        </tbody>
                                    </table>
                                ) : null
                            }
                        </>
                        <div className="text-right">
                            <Link to={`/RamasProceso/`} state={{ from: from }}>
                                <button type="button" className="btn btn-sm btn-success mr-1 mb-3"><i class="fas fa-backward"></i> Regresar </button>
                            </Link>
                        </div>

                    </div>
                </div>
            </Form>
        </Formik>
        </>
    );


}
export default ClasificacionRadicadoLista;
