                                    <Link to={`/ActuacionesLista/`} title='Regresar a lista de Actuaciones' state={{ from: from, selected_id_etapa: selected_id_etapa, disable: disable }}>
                                        <button type="button" className="btn btn-success"><i class="fas fa-backward"></i></button>
                                    </Link>
                                </div>

                                <div className='col-md-12'>
                                    <div className='mb-3'>
                                        <b style={{ fontSize: '18px' }}>{tipoActuacion == "0" ? "Actuaci√≥n" : (tipoActuacion == "1" ? 'Impedimento' : 'Comisorio')}: </b>{nombre}
                                    </div>
                                    <div>
                                        <button type='button' title='Descargar documento' className='btn btn-sm btn-primary' onClick={() => handleClicArchivoManual()}>
                                            <i className='fas fa-download mr-2'></i>Descargar Manual
                                        </button>
                                    </div>
                                </div>

                                {disable != true ? (
                                    (estadoActualActuacion != "Aprobada" ||
                                        (getArchivosListaSearch.data.length == 2 || getArchivosListaSearch.data.length > 2)) ? null :
                                        <div className='col-md-12'>
                                            <table className="table table-striped table-vcenter js-dataTable-full">
                                                <thead>
                                                    <tr>
                                                        {inputListArchivos.map((x, i) => {
                                                            return (
                                                                <div className="col-md-12" key={i}>
                                                                    <div className="form-group" style={{
                                                                        display: 'flex',
                                                                        justifyContent: 'flex-end'
                                                                    }}>
                                                                        <div className='row'>
                                                                            <div className='col-md-12'>
                                                                                <div className='col-md-12' style={{
                                                                                    textAlign: 'center',
                                                                                    marginLeft: '14%'
                                                                                }}>
                                                                                    <label>Cargar archivo definitivo<span className="text-danger">* </span></label>
                                                                                </div>
                                                                            </div>
                                                                            <div className='col-md-12' style={{
                                                                                display: 'flex',
                                                                                justifyContent: 'flex-end'
                                                                            }}>
                                                                                <div className='col-md-6'>
                                                                                    <label className="custom-file-label" htmlFor="example-file-input-custom" name="input-file" data-toggle="custom-file-input">{x.archivo.name}</label>
                                                                                    <input className="custom-file-input" data-toggle="custom-file-input" type="file" name="archivo" onChange={e => handleInputChangeArchivos(e, i, 0, 0, 0, "Documento definitivo", 2)} required />
                                                                                    <label >Peso del archivo: {formatBytes(x.archivo.size)}</label>
                                                                                </div>
                                                                            </div>
                                                                        </div>
                                                                    </div>
                                                                </div>
                                                            );
                                                        })}
                                                    </tr>
                                                </thead>
                                            </table>
                                        </div>
                                ) : null
                                }

                                <div className='mb-3 mt-4'>
                                    <b style={{ fontSize: '18px' }}>Archivos {tipoActuacion == "0" ? "Actuaci√≥n:" : (tipoActuacion == "1" ? 'Impedimento:' : 'Comisorio:')}</b>
                                </div>
                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                    <thead>
                                        <tr>
                                            <th>Tipo </th>
                                            <th>Documento</th>
                                            <th>Descarga </th>
                                            <th>Actualizar</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getRtaInfoArchivoActuaciones ? infoArchivos() : null}
                                    </tbody>
                                </table>

                                <strong>
                                    <span className='txt-blue-primary'>Estado de aprobaci√≥n: </span>
                                    {getActuacionEstaAprobadaSioNo && !getActuacionPendienteDeFirmas ? (" Aprobado y con firmas") : null}
                                    {!getActuacionEstaAprobadaSioNo && getActuacionPendienteDeFirmas ? (" Pendiente de aprobaci√≥n y pendiente de firmas") : null}
                                    {!getActuacionEstaAprobadaSioNo && !getActuacionPendienteDeFirmas ? (" Pendiente de aprobaci√≥n, con firmas") : null}

                                </strong>

                                {
                                    (hasAccess('Actuaciones', 'SolicitudDeAprobaci√≥n')) &&
                                        getActuacionEstaAprobadaSioNo == false &&
                                        getRtaJefe &&
                                        disable != true &&
                                        tipoActuacion != "1" &&
                                        !getActuacionPendienteDeFirmas &&
                                        !getValidarUsuariosPendienteFirma ?
                                        <div className='col-md-12'>
                                            <table>
                                                <thead>
                                                    <tr>
                                                        <th style={{ width: '30%' }}>Aprobar y/o rechazar {tipoActuacion == "0" ? "actuacion" : 'comisorio'}: </th>
                                                        <th style={{ paddingLeft: '50px' }}>
                                                            <div className="block-content block-content-full text-left">
                                                                <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Aprobar actuaci√≥n", valor: 1, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                                    <button type="button" className="btn btn-primary">Aprobar</button>
                                                                </Link>
                                                                <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Rechazar actuaci√≥n", valor: 2, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                                    <button type="button" className="btn btn-danger">Rechazar</button>
                                                                </Link>
                                                            </div>
                                                        </th>
                                                    </tr>
                                                </thead>
                                            </table>
                                        </div>
                                        : null
                                }

                                {
                                    (hasAccess('Actuaciones', 'AprobarYRechazarUnImpedimento')) &&
                                        getActuacionEstaAprobadaSioNo == false &&
                                        getRtaJefe &&
                                        disable != true &&
                                        tipoActuacion == "1" &&
                                        !getActuacionPendienteDeFirmas &&
                                        !getValidarUsuariosPendienteFirma ?
                                        <div className='col-md-12'>
                                            <table>
                                                <thead>
                                                    <tr>
                                                        <th style={{ width: '30%' }}>Aprobar y/o rechazar Impedimento: </th>
                                                        <th style={{ paddingLeft: '50px' }}>
                                                            <div className="block-content block-content-full text-left">
                                                                <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Aprobar actuaci√≥n", valor: 1, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                                    <button type="button" className="btn btn-primary">Aprobar</button>
                                                                </Link>
                                                                <Link to={`/ActuacionesAprobacionRechazar/`} state={{ getActuacionConFirmas: getListaFirmasMecanicas.data.length, from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Rechazar actuaci√≥n", valor: 2, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                                    <button type="button" className="btn btn-danger">Rechazar</button>
                                                                </Link>
                                                            </div>
                                                        </th>
                                                    </tr>
                                                </thead>
                                            </table>
                                        </div>
                                        : null
                                }

                                {
                                    (hasAccess('Actuaciones', 'SolicitudDeInactivaci√≥n')) && getUltimoEstado == "Solicitud inactivaci√≥n" && disable != true ? (
                                        <div className='col-md-12'>
                                            <label><b>Estado de aprobaci√≥n de inactivaci√≥n: </b></label>

                                            <Link to={`/ActuacionesAceptaRechazaSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, boton: "Acepta", solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                <button type="button" className="btn btn-primary ml-3">Aprobar</button>
                                            </Link>
                                            <Link to={`/ActuacionesAceptaRechazaSolicitudDeAnulacion/`} state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, boton: "Rechaza", solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                                <button type="button" className="btn btn-danger">Rechazar</button>
                                            </Link>
                                        </div>) : null
                                }

                            </div >
                        </div >

                        <div className="block block-themed">
                            <div className={(disable ? ("block-header bg-dark") : "block-header")}>
                                <h3 className="block-title"><strong>Usuarios para firma mec√°nica</strong></h3>
                            </div>
                            <div className="block-content">
                                {
                                    getUltimoEstado != "Remitida" &&
                                    getUltimoEstado != "Aprobada" &&
                                    getUltimoEstado != "Aprobada y pdf definitivo" &&
                                    getRtaJefe &&
                                    !getActuacionEstaAprobadaSioNo ? (
                                    (hasAccess('Actuaciones', 'AgregarUsuarioParaFirmaMecanica')) ? (
                                        <Link to="/AgregarUsuarioParaFirmaMecanica" state={{ from: from, selected_id_etapa: selected_id_etapa, id: id, nombre: nombre, estadoActualActuacion: estadoActualActuacion, titulo: "Agregar usuario para firma mec√°nica", valor: 1, solicitante: getUsuarioSolicitante, tipoActuacion: tipoActuacion }}>
                                            <button type="button" className="btn btn-primary mb-4" >Agregar usuario para firma mec√°nica</button>
                                        </Link>
                                    ) : null
                                ) : null}
                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                    <thead>
                                        <tr>
                                            <th>Usuario</th>
                                            <th>Tipo firma</th>
                                            <th>Estado </th>
                                            <th>Acciones</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getFirmasMecanicas ? ListaFirmasMecanicas() : null}
                                    </tbody>
                                </table>
                            </div>
                        </div>

                        <div className="block block-themed">
                            <div className={(disable ? ("block-header bg-dark") : "block-header")}>
                                <h3 className="block-title"><strong>Historial de aprobaci√≥nes {tipoActuacion == "0" ? "de la Actuaci√≥n" : (tipoActuacion == "1" ? 'del Impedimento' : ' del Comisorio')}</strong></h3>
                            </div>
                            <div className="block-content">
                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                    <thead>
                                        <tr>
                                            <th style={{ width: '23%' }}>Fecha</th>
                                            <th>Usuario</th>
                                            <th>Usuario aprueba/rechaza</th>
                                            <th>Estado</th>
                                            <th>Observaciones</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {getRtaInfoTrazabilidadActuaciones ? infoTrazabilidad() : null}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </Form >
                )
                }
            </Formik >


        </>
    );

}

export default ActuacionesVer;
