}catch(error){}};return/*#__PURE__*/_jsxs(_Fragment,{children:[/*#__PURE__*/_jsx(Spinner,{}),isRedirect?redirectToRoutes():null,/*#__PURE__*/_jsx(InfoExitoApiCustom,{}),/*#__PURE__*/_jsx(InfoErrorApi,{error:errorApi}),/*#__PURE__*/_jsx(InfoExitoApi,{}),/*#__PURE__*/_jsx("div",{className:"block block-rounded block-bordered",children:/*#__PURE__*/_jsx("div",{className:"block-content",children:/*#__PURE__*/_jsx("nav",{"aria-label":"breadcrumb",children:/*#__PURE__*/_jsxs("ol",{className:"breadcrumb breadcrumb-alt push",children:[/*#__PURE__*/_jsxs("li",{className:"breadcrumb-item",children:[" ",/*#__PURE__*/_jsx(Link,{underline:"hover",className:"text-dark",to:"/MisPendientes",children:/*#__PURE__*/_jsx("small",{children:"Mis Pendientes"})})]}),/*#__PURE__*/_jsxs("li",{className:"breadcrumb-item",children:[" ",/*#__PURE__*/_jsx(Link,{underline:"hover",className:"text-dark",to:"/RamasProceso/",state:{from:from},children:/*#__PURE__*/_jsx("small",{children:"Ramas del proceso"})})]}),/*#__PURE__*/_jsxs("li",{className:"breadcrumb-item",children:[" ",/*#__PURE__*/_jsx("small",{children:"Documento Cierre"})]})]})})})}),/*#__PURE__*/_jsx("div",{className:"content",children:/*#__PURE__*/_jsx("div",{className:"row",children:/*#__PURE__*/_jsx("div",{className:"col-md-12",children:/*#__PURE__*/_jsxs("div",{className:"block block-themed",children:[/*#__PURE__*/_jsx("div",{className:"block-header",children:/*#__PURE__*/_jsxs("h3",{className:"block-title",children:["SINPROC NO ",radicado," ::: DOCUMENTO CIERRE"]})}),/*#__PURE__*/_jsx("div",{className:"block-content",children:getDocumentoCierreLista.data.length>0?componentLista():componentRegistro()})]})})})})]});}export default DocumentoCierreForm;

