                            </div>
                            <div className='block-content'>
                                {getDocumentoCierreLista.data.length > 0 ? componentLista() : componentRegistro()}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );

}

export default DocumentoCierreForm;
