                        : 
                        (
                            semaforoColor(semaforo) == "Amarillo" 
                            ?
                                <div style={{ "background-color": "yellow", "color": "white", "width": "100%", "text-align": "center" }} title="AMARILLO"><strong>AMARILLO</strong></div>
