import React, { useEffect , useState } from "react";
import Spinner from '../Utils/Spinner';
import { Link } from "react-router-dom";
import { getUser } from '../../components/Utils/Common';
import GenericApi from '../Api/Services/GenericApi';

function Perfil() {

    const [getDatosUsuario, setDatosUsuario] = useState("");

    useEffect(() => {
        async function fetchData() {
           window.showSpinner(true);
           obtenerDatosUsuario();
        }
        fetchData();
    }, []);

    const obtenerDatosUsuario = () =>{
        GenericApi.getGeneric("usuario/get-usuario-por-name/"+getUser().nombre).then(
            datos => {
                if (!datos.error) {
                    setDatosUsuario(datos.data[0]);
                    console.log(datos.data[0]);
                }
                window.showSpinner(false);
            }
        )
    }


    return (
        <>
            {<Spinner />}
            <div className="row">
                <div className="col-md-12"> 
                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">Perfil Usuario</h3>
                        </div>
                        <div className="block-content">
                            <div className="row">
                                <div className="col-md-6 mb-2">
                                    <span className="txt-blue-primary"><strong >Nombre: </strong></span>
                                    {getDatosUsuario ? getDatosUsuario.attributes.nombre : null}
                                </div> 
                                <div className="col-md-6 mb-2">
                                    <span className="txt-blue-primary"><strong >Apellido: </strong></span>
                                    {getDatosUsuario ? getDatosUsuario.attributes.apellido : null}
                                </div> 
                                <div className="col-md-6 mb-2">
                                    <span className="txt-blue-primary"><strong >Usuario: </strong></span>
                                    {getDatosUsuario ? getDatosUsuario.attributes.name : null}
                                </div> 
                                <div className="col-md-6 mb-2">
                                    <span className="txt-blue-primary"><strong >Correo: </strong></span>
                                    {getDatosUsuario ? getDatosUsuario.attributes.email : null}
                                </div> 

                                <div className="col-md-12 my-3">
                                    Pendiente carga de firma mec√°nica 
                                    <Link to={`/AgregarFirma/`}>
                                        <button type="button" className="btn btn-rounded btn-outline-primary ml-2">
                                            {getDatosUsuario ?
                                                getDatosUsuario.attributes.firma_mecanica ? "Cambiar" : "Agregar"
                                            : null
                                            } firma mecanica
                                        </button>
                                    </Link>
                                </div> 

                                <div className="col-md-6 my-5">
                                    <Link to={`/DocumentosFirmadosOPendientesDeFirma/`}>
                                        <a>Ver documentos firmados o pendientes de firma</a>
                                    </Link>
                                </div> 

                            </div>
                        </div>
                    </div>
                </div>    
            </div>
        </>
    )

}

export default Perfil;
