import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState, useRef } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';
import Center from '../../Utils/Styles/Center.js'
import moment from 'moment';

function ProcesoDisciplinarioMigracionForm() {

    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [countTextArea, setCountTextArea] = useState(0);
    const [getAntecedente, setAntecedente] = useState({ title: "", message: "", show: false });
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
   

    const location = useLocation()
    const { from } = location.state

    const [existeInformacion, setExisteInformacion] = useState(false);
    const [descripcion, setDescripcion] = useState('');
    const [fecha, setFecha] = useState('');

    const [idAntecedente, setIdAntecedente] = useState(1);
    const [idProceso, setIdProceso] = useState(1);
    //let { idAntecedente } = useParams();

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            antecedente();
            obtenerParametros();
        }
        fetchData();
    }, []);

    
    function containsSpecialChars(str) {

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }

    const antecedente= () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-antecedente/51238/1/1/"+idAntecedente,).then(
                datos => {

                    if (!datos.error) {
                        setAntecedente(datos);
                        setExisteInformacion(true);
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    const obtenerParametros = () => {
        try {

            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            GenericApi.getByDataGeneric("parametro/parametro-nombre",data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    const handleClick = (event, i) => {

        if(i===1){
            setDescripcion(getAntecedente.data[0].attributes.antecedente);
        }

        else if(i===2){

            /*const date = '10/10/2016';
            const dateFormat = 'MM/DD/YYYY';
            const toDateFormat = moment(new Date(date)).format(dateFormat);
            const rta = moment(toDateFormat, dateFormat, true).isValid();*/

            const date = getAntecedente.data[0].attributes.fecha;
            const dateFormat = 'MM/DD/YYYY';
            const toDateFormat = moment(new Date(date)).format(dateFormat);

            if(moment(toDateFormat, dateFormat, true).isValid()){
                setFecha(getAntecedente.data[0].attributes.fecha);
            }
            else{
                setModalState({ title: "Error de formato", message: "No cumple con el formato de fecha.", show: true, from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
            }   
        }

    };


    const handleInputChange = (event, i) => {
     
        if(i===1){
            setDescripcion(event.currentTarget.value);
        }

        else if(i===2){
            setFecha(event.currentTarget.value)
        }

        return true;

        //return true;
    }



    const infoAntecedente = () => {

        if (getAntecedente.data != null && typeof (getAntecedente.data) != 'undefined') {
            return (

                getAntecedente.data.map((item, i) => {
                    return (
                        <>

                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="radicado_origen">Radicado origen</label>
                                <Field value={item.attributes.fecha} as="input" type="text" autoComplete="off" className="form-control" id="radicado_origen" name="radicado_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 1)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="radicado_origen">Radicado destino</label>
                                <Field value={item.attributes.fecha} as="input" type="text" autoComplete="off" className="form-control" id="radicado_destino" name="radicado_destino"></Field>
                            </div>
                        </div>

                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="radicado_origen">Vigencia origen</label>
                                <Field value={item.attributes.fecha} as="input" type="text" autoComplete="off" className="form-control" id="vigencia_origen" name="vigencia_origen"></Field>
                            </div>
                        </div>    

                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 2)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>  
                            
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="radicado_origen">Vigencia destino</label>
                                <Field value={item.attributes.fecha} as="input" type="text" autoComplete="off" className="form-control" id="vigencia_destino" name="vigencia_destino"></Field>
                            </div>
                        </div> 
                          
                        </>
                    )
                })
            )
        }
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;

        /*data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": datos.descripcion,
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "id_dependencia": dependencia ? dependencia : "",
                }
            }
        }

        GenericApi.addGeneric("antecedentes",data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: 'Registro Exitoso', show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.EXITO});
                }
                else {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
                window.showSpinner(false);

            }
        )*/
    }

    return (
        <>
            {<Spinner />}   
            {<ModalGen data={getModalState} />}
          
            <Formik
                initialValues={{
                    antecedente_origen: '',
                    antecedente_destino: '',
                    fecha_antecedente_origen: '',
                    fecha_antecedente_destino: '',
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/Buscador`}><small>Buscador</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ListarMigracionAntecedentes`}><small>Lista de antecedentes</small></Link></li>
                                <li class="breadcrumb-item"> <small>Antecedente</small></li>
                            </ol>
                        </nav>
                    </div>

                      
                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">SINPROC No  :: <strong>Migrar antecedente {idAntecedente}</strong></h3>
                            </div>

                            <div className="block-content">

                                <div className='text-right'>

                                    <Link to={`/AntecedentesLista/`} title='Regresar a lista de Antecedentes' state={{ from: from }}>
                                        <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                    </Link>
                                </div>

                                <div className="row">

                                    {infoAntecedente()}
                                    
                                </div>

                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-rounded btn-primary">Registrar</button>

                                <Link to={`/AntecedentesLista/`} state={{ from: from }}>
                                    <button type="button" className="btn btn-rounded btn-outline-primary">Cancelar</button>
                                </Link>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );
}


