                                                                <>                                                       
                                                                    <div className="bg-primary rounded-top font-w600 text-white text-center p-3">
                                                                        VISIBILIDAD
                                                                    </div>
                                                                    {
                                                                        getListaVisibilidadEstado.data.map((estado, i) => {
