                                                                                <div className='p-1' onClick={() => (onCambiarVisibilidad(estado.id))}>
                                                                                    <a className="dropdown-item" key={i}>
                                                                                            { (getIdEtapaVisibilidadActual.toString() === estado.id.toString()) ? <i className='fa fa-check'></i> : null } { estado.nombre }
