                {p.attributes.informacionPortalNotificaciones.attributes.detalle_incompleto ?
                    p.attributes.informacionPortalNotificaciones.attributes.detalle_incompleto + "... " : "-"}

                {p.attributes.informacionPortalNotificaciones.attributes.detalle ?
                    <button type="button"
