                    setHabilitarRegistrar(false);
                }
            }
            else {
                setHabilitarRegistrar(true);
            }
        }
        else if (aprobacion == "false") {
            if (getGestorRespuestaApi?.data[0]?.attributes?.rol_anterior?.nombre_completo || getUsuarioEnvioExpediente) {
                setHabilitarRegistrar(true);
            }
            else {
