    function primerArchivoPdf(list) {
        const extensionList = getFileExtension(list[0].archivo.name);

        if (extensionList != 'pdf') {
            setModalState({ title: "SINPROC No " + radicado + " :: Gestor Respuesta", message: 'El primer archivo seleccionado debe tener extensión .pdf', show: true, redirect: null, from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
            window.showModal(1)
            setHabilitarRegistrar(false);
        }
        else {
            setHabilitarRegistrar(true);
        }
    }

    /*
    * FUNIONES TEMPORALES -- CONSTRUIR COMPONENTE
    */
    const handleInputChangeFolios = (e, index) => {
        const { name, value } = e.target;
        const list = [...inputListArchivos];
        list[index][name] = value;
        setInputListArchivos(list);
    };

    const handleInputChangeArchivos = (e, index) => {

        const { name, files } = e.target;
        if (files.length > 0) {
            const extension = getFileExtension(files[0].name);
            const resultado = getFormatosApi.filter(datos => datos.attributes.nombre == extension);

            if (resultado.length > 0) {
                const list = [...inputListArchivos];
                list[index][name] = files[0];

                primerArchivoPdf(list);

                if (files[0]) {
                    list[index][name] = files[0];
                }
                else {
                    list[index][name] = '';
                    list[index].filebase64 = '';
                    list[index].size = 0;
                    list[index].id_mas_formato = null;
                    setInputListArchivos(list);
                }
                //Conversion a Base64
                Array.from(e.target.files).forEach(archivo => {
                    var reader = new FileReader();
                    reader.readAsDataURL(archivo);
                    reader.onload = function () {
                        var arrayAuxiliar = [];
                        var base64 = reader.result;
                        arrayAuxiliar = base64.split(',');
                        list[index].filebase64 = arrayAuxiliar[1];
                        list[index].id_mas_formato = resultado[0].id;
                        setInputListArchivos(list);
                    }
                })
                obtenerPesoTotalArchivos(list);
                compararNombresArchivos(list);
            }
            else {
                const list = [...inputListArchivos];
                list[index][name] = files[0];
                list[index].filebase64 = '';
                list[index].size = 0;
                list[index].id_mas_formato = null;
                setInputListArchivos(list);
                setModalState({ title: "SINPROC No " + radicado + " :: Gestor Respuesta", message: 'El archivo seleccionado no tiene un formato permitido', show: true, redirect: null, from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                window.showModal(1)
            }
        }
        else {
            const list = [...inputListArchivos];
            list[index][name] = '';
            list[index].filebase64 = '';
            list[index].size = 0;
            list[index].id_mas_formato = null;
            setInputListArchivos(list);
            obtenerPesoTotalArchivos(list);
            compararNombresArchivos(list);
        }
    }

    function getFileExtension(filename) {
        return filename.slice((filename.lastIndexOf(".") - 1 >>> 0) + 2);
    }

    function compararNombresArchivos(list) {
        if (getListNombresArchivos?.data.length > 0) {
            let existe = false;
            let nombre = "";
            list.forEach(
                (archivo, index) => {

                    let nombre_servidor = getListNombresArchivos.data.find(dato => dato.attributes.nombre_archivo == archivo.archivo.name);

                    if (nombre_servidor) {
                        nombre = nombre_servidor.attributes.nombre_archivo;
                        existe = true;
                        return
                    }

                }
            );

            if (existe) {
                setModalState({ title: "SINPROC No " + radicado + " :: Gestor Respuesta", message: "El nombre del archivo '" + nombre + "', ya es utilizado por otro documento registrado en el sistema, por favor elimine el archivo y vuelva a subirlo con un nombre distinto", show: true, redirect: null, from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                window.showModal(1)
            }
        }
    }

    function obtenerPesoTotalArchivos(list) {
        let peso = 0;
        list.forEach(
            (archivo, index) => {
                if (archivo.archivo.size) {
                    peso += archivo.archivo.size;
                }
            }
        )

        if (peso > 15000000) {
            setModalState({ title: "SINPROC No " + radicado + " :: Gestor Respuesta", message: 'El peso/tamaño de los todos los adjuntos superan los 15 Mb pertmitidos para el registro.', show: true, redirect: null, from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
            window.showModal(1)
        }

        setPesoTotalArchivos(peso);
    }

    function formatBytes(bytes, decimals = 3) {
        if (bytes === undefined) return '0 Bytes';
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const dm = decimals < 0 ? 0 : decimals;
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        //return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
        return Math.round(parseFloat((bytes / Math.pow(k, i)).toFixed(dm), 1)) + ' ' + sizes[i];
    }

    const handleOnSubmitDocument = (valores) => {

        let band = false;

        // VALIDA QUE NO EXISTA EL NOMBRE REPÉTIDO EN EL SISTEMA
        let nombre = "";
        let existe = false;
        if (getListNombresArchivos?.attributes && getListNombresArchivos?.data.length > 0) {
            for (let i = 0; i < inputListArchivos.length; i++) {

                let nombre_servidor = getListNombresArchivos.data.find(dato => dato.attributes.nombre_archivo == inputListArchivos[i].archivo.name);

                if (nombre_servidor) {
                    nombre = nombre_servidor.attributes.nombre_archivo;
                    existe = true;
                    band = true;
                }

                if (existe) {
                    setModalState({ title: "SINPROC No " + radicado + " :: Gestor Respuesta", message: "El nombre del archivo '" + nombre + "', ya es utilizado por otro documento registrado en el sistema, por favor elimine el archivo y vuelva a subirlo con un nombre distinto", show: true, redirect: null, from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                    window.showModal(1)
                    i = inputListArchivos.length + 1;
                }
            }
        }

        // VALIDA QUE NO SE PUEDAN SUBIR FORMATOS NO PERMITIDOS
        for (let i = 0; i < inputListArchivos.length; i++) {
            const extension = getFileExtension(inputListArchivos[i].archivo.name);
            const resultado = getFormatosApi.filter(datos => datos.attributes.nombre == extension);

            if (resultado.length <= 0) {
                setModalState({ title: "SINPROC No " + radicado + " :: Gestor Respuesta", message: 'Uno de los documentos adjuntados no tiene un formato correspondiente', show: true, redirect: null, from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                band = true;
            }
        }

        if (getPesoTotalArchivos > 15000000) {
            setModalState({ title: "SINPROC No " + radicado + " :: Gestor Respuesta", message: 'El peso/tamaño de los todos los adjuntos superan los 15 Mb pertmitidos para el registro.', show: true, redirect: null, from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });

            band = true;
        }

        if (band === false) {

            // PASO 1. VALIDAR CON SIRIUS
            // PASO 1.1 LOGIN Y RADICACION

            const attributes = [];

            // PASO 2. GUARDAR EN BASE DE DATOS
            const data = {
                "data": {
                    "type": "documento_sirius",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": from.id_etapa,
                        "id_fase": from.id_fase,
                        "nombre_archivo": inputListArchivos[0].archivo.name,
                        "estado": "1",
                        "num_folios": inputListArchivos[0].folios,
                        "num_radicado": radicado,
                        "vigencia": vigencia,
                        "extension": getFileExtension(inputListArchivos[0].archivo.name),
                        "peso": inputListArchivos[0].archivo.size,
                        "created_user": getNombreUsuario,
                        "id_mas_formato": inputListArchivos[0].id_mas_formato,
                        "es_compulsa": false,
                        "es_soporte": false,
                        "aprobado": false,
                        "descripcion": valores.descripcion,
                        "version": 1,
                        "nuevo_documento": true,
                        "file64": inputListArchivos[0].filebase64,
                        "id_tipo_evaluacion": tipoEvaluacion ? tipoEvaluacion : global.Constants.TIPOS_EVALUACION.SIN_EVALUACION
                    }
                }
            }
            window.showSpinner(true);

            //console.log(JSON.stringify(data));
            // console.log("Daticos, gestor respuesta con documento", data);

            GestorRespuestaApi.addGestorRespuestaDocumento(data).then(
                datos => {
                    if (!datos.error) {
                        /*setExitoApiTextsCustom(
                            {
                                titulo: 'Proceso Exitoso', 
                                cuerpo: 'El expediente ha sido reasignado a ' + datos?.data?.attributes?.rol_siguiente?.nombre_completo, 
                                boton: 'Aceptar'
                            }
                        );*/

                        if (datos?.data?.attributes?.usuario_actual) {//ES UN CASO DE EXITO, PERO COLOCANDO FALSO, PERMITE REDIRIGR A LA 'RAMA DEL PROCESO' EN LUGAR DE 'MIS PENDIENTES'
                            setModalState({ title: "SINPROC No " + radicado + " :: Gestor Respuesta", message: 'El proceso disciplinario es asignado a ' + datos?.data?.attributes?.rol_siguiente?.nombre_completo, show: true, redirect: '/RamasProceso/', from: { from }, alert: global.Constants.TIPO_ALERTA.EXITO });
                        }
                        else {
                            setModalState({ title: "SINPROC No " + radicado + " :: Gestor Respuesta", message: 'El proceso disciplinario es asignado a ' + datos?.data?.attributes?.rol_siguiente?.nombre_completo, show: true, redirect: '/MisPendientes', from: { from }, alert: global.Constants.TIPO_ALERTA.EXITO });
                        }
                    }
                    else {
                        setModalState({ title: "SINPROC No " + radicado + " :: Gestor Respuesta", message: datos.error.toString(), show: true, redirect: null, from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )

        }
    }

    const showModal = () => {
        window.showModalListaVersionesGestorRespuesta();
    }

    /*
    * FUNIONES TEMPORALES -- CONSTRUIR COMPONENTE
    */

    return (
        <>
            {<ModalGen data={getModalState} />}
            {<ModalListaVersionesGestorRespuesta object={getGestorRespuestaApi} />}
            {<Spinner />}


            <div className="w2d_block">
                <nav aria-label="breadcrumb">
                    <ol className="breadcrumb breadcrumb-alt push">
                        <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                        <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                        <li className="breadcrumb-item"> <small>Gestor Respuesta</small></li>
                    </ol>
                </nav>
            </div>


            <div className='content'>
                <div className='row'>
                    <div className='col-md-12'>
                        <div className='block block-themed'>
                            <div className={(disable ? ("block-header bg-dark") : "block-header")}>
                                <h3 className='block-title'> {getNombreProceso} ::: GESTOR RESPUESTA</h3>
                                {
                                    (getGestorRespuestaApi?.data && getGestorRespuestaApi?.data.length > 0)
                                        ?
                                        <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="Consultar Versiones" onClick={() => showModal()} data-original-title="Consultar Versiones"><span className="fas fa-clipboard"> </span></button>
                                        :
                                        null
                                }
                            </div>
                            <div className='block-content'>
                                <div className='col-md-12 text-right my-2'>

                                    <Link to={'/RamasProceso/'} title='Regresar a ramas del proceso' state={{ from: from, disable: disable }}>
                                        <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                    </Link>

                                </div>

                                {
                                    (getGestorRespuestaApi?.data && getGestorRespuestaApi?.data.length > 0)
                                        ?
                                        (
                                            (
                                                getGestorRespuestaApi?.data[0]?.attributes?.aprobado == "1" ||
                                                (getGestorRespuestaApi?.data[0]?.attributes?.version != getGestorRespuestaApi?.data[1]?.attributes?.version)
                                            )
                                        )
                                            ?
                                            componentAprobacion()
                                            :
                                            (disable != true ? (
                                                componentRegistro()
                                            ) : null)

                                        :
                                        (disable != true ? (
                                            componentRegistro()
                                        ) : null)
                                }
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </>
    );

}

export default GestorRespuestaForm;
