                    "nombre_archivo": inputListArchivos[0].archivo.name,
                    "extension": inputListArchivos[0].ext,
                    "peso": inputListArchivos[0].archivo.size,
                    "id_tipo_archivo": 2
                }
            }
        };

        GenericApi.addGeneric("archivo-actuaciones/up-documento", data).then(
            datos => {

                window.showSpinner(false);

                if (!datos.error) {
                    setModalState({ title: "SINPROC No " + from.radicado + " :: Cargue archivo definitivo", message: "¡Documento subido con éxito!", show: true, redirect: '/Transacciones', from: { from, selected_id_etapa: selected_id_etapa }, alert: global.Constants.TIPO_ALERTA.EXITO });
                } else {
                    setModalState({ title: "SINPROC No " + from.radicado + " :: Cargue archivo definitivo", message: datos.error.toString(), show: true, redirect: '/MisPendientes/', alert: global.Constants.TIPO_ALERTA.ERROR });
                }
            }
        )
    }

    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<ModalGen data={getModalState} />}

            <Formik>
                <Form>
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                <li class="breadcrumb-item"> <small>Actuacion {nombre}</small></li>
                            </ol>
                        </nav>
                    </div>

                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: <strong>Estado de la actuación</strong></h3>
                        </div>
                        <div className="block-content">
                            <div className='row'>
                                <div className='col-md-12'>
                                    {/* <Field type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" /> */}
                                    <p>Se ha generado el número de auto {getAuto != '' ? getAuto : null} para el documento { getNombreDelDocumento != '' ? getNombreDelDocumento : null}.</p>
                                    <p>La aprobación finalizará cuando los usuarios incluidos para firma aprueben el firmado del documento.</p>
                                    <button type='button' title='Descargar' className='btn btn-sm btn-primary' onClick={() => handleDownloadArchivo()}>Descargar documento diligenciado en Word con número de auto <i className="fas fa-download"></i> </button>
                                </div>
                                {/* <div className='col-md-12'>
                                    <table className="table table-striped table-vcenter js-dataTable-full">
                                        <thead>
                                            <tr>
                                                {inputListArchivos.map((x, i) => {
                                                    return (
                                                        <div className="col-md-12" key={i}>
                                                            <div className="form-group" style={{
                                                                display: 'flex',
