import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { Field, Form, Formik } from 'formik';
import AntecedentesApi from '../Api/Services/AntecedentesApi';
import { useParams } from "react-router";
import ClasificacionRadicadoApi from '../Api/Services/ClasificacionRadicadoApi';
import DatosInteresadoApi from '../Api/Services/DatosInteresadoApi';
import Spinner from '../Utils/Spinner';
import EntidadInvestigadoApi from '../Api/Services/EntidadInvestigadoApi';
import CierreEtapaApi from '../Api/Services/CierreEtapaApi';
import DocumentoSiriusApi from '../Api/Services/DocumentoSiriusApi';
import { Navigate } from "react-router-dom";
import moment from 'moment';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../Models/ParametroModel';
import ProcesoDiciplinarioApi from '../Api/Services/ProcesoDiciplinarioApi';

function RamasProceso() {
    const location = useLocation()
    const { from } = location.state

    const [respuestaAntecedente, setRespuestaAntecedente] = useState(false);
    const [antecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });

    const [errorApi, setErrorApi] = useState('');
    const [cambiaColorAntecedentes, setCambiaColorAntecedentes] = useState(false);
    const [cambiaColorDatosInteresado, setCambiaColorDatosInteresado] = useState(false);
    const [cambiaColorClasificacionRadicado, setCambiaColorClasificacionRadicado] = useState(false);
    const [cambiaColorEntidadInvestigado, setCambiaColorEntidadInvestigado] = useState(false);
    const [cambiaColorSoporteRadicado, setCambiaColorSoporteRadicado] = useState(false);
    const [estadoEtapaCapturaReparto, setEstadoEtapaCapturaReparto] = useState(false);
    const [cambiaColorComunicacionInteresado, setCambiaColorComunicacionInteresado] = useState(false);

    const [getEtapa, setEtapa] = useState(0);

    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;



    const [isRedirect, setIsRedirect] = useState(false);

    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/RamasProceso/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }} />;
    }

    useEffect(() => {
        async function fetchData() {
            //console.log(radicado + "-" + procesoDisciplinarioId + "-" + vigencia);
            window.showSpinner(true);


            const proceso_disciplinario = {
                "data": {
                    "type": "antecedentes",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                    }
                }
            }

            ProcesoDiciplinarioApi.getConsultarFasesProcesoDisciplinario(proceso_disciplinario, procesoDisciplinarioId ).then(
                datos => {


                    if (!datos.error) {

                        console.log(datos["data"]["attributes"]["antecedentes"]);
                        setEtapa("block-header block-header-default w2d_block-header");

                        if(datos["data"]["attributes"]["antecedentes"]===true){
                            setCambiaColorAntecedentes(true);
                        }

                        if(datos["data"]["attributes"]["datos_interesado"]===true){
                            setCambiaColorDatosInteresado(true);
                        }

                        if(datos["data"]["attributes"]["clasificacion_radicado"]===true){
                            setCambiaColorClasificacionRadicado(true);
                        }

                        if(datos["data"]["attributes"]["entidad_investigado"]===true){
                            setCambiaColorEntidadInvestigado(true);
                        }

                        if(datos["data"]["attributes"]["soporte_radicado"]===true){
                            setCambiaColorSoporteRadicado(true);
                        }
                    }
                    else{
                        window.showModal()
                    }

                }

            )


        }
        fetchData();
    }, []);


     const cerrarEtapa = (datos) => {

        let data

        data = {
            "data": {
                "type": "cerrar_etapa",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": datos.inputEtapa,
                    "id_delegada": 1,
                    "id_funcionario": 1
                }
            }
        }

        CierreEtapaApi.addCierreEtapa(data).then(
            datos => {
                if (!datos.error) {
                    window.showModal(2)
                    window.location.reload(false);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
            }
        )
    }


    const componentAntecedente = () => {

        var index = antecedentesLista.data.length - 1;

        var fecha_ultimo_antecedente = antecedentesLista.data.length > 0 ? (antecedentesLista.data[0].attributes.fecha_creado) : "";



        return (
            <>
                <div className="col-lg-12 push">
                    <div className="alert alert-info" role="alert">
                        <h3 className="text-center">(RADICADO) :: {radicado}</h3>
                        <span><strong>Antecente: </strong></span><br />
                        {
                            (antecedentesLista.data.length > 0) ? (
                                <>
                                    <span>{antecedentesLista.data[0].attributes.descripcion}</span><br />
                                </>
                            ) : <><span>
                            </span><br /></>
                        }




                        <br />
                        <div className="row">
                            <div className="col-lg-6"><span><strong>Registrado Por: </strong>PENDIENTE</span></div>
                            <hr />

                            {
                                (antecedentesLista.data.length > 0) ? (
                                    <>
                                        <div className="col-lg-6"><span><strong>Dependencia registro: </strong> {antecedentesLista.data[0].attributes.nombre_dependencia}</span></div>
                                    </>
                                ) : <><div className="col-lg-6"><span><strong>Dependencia registro: </strong></span></div></>
                            }


                            <hr />
                        </div>

                        <div className="row">
                            {
                                (antecedentesLista.data.length > 0) ? (


                                    <>
                                        <div className="col-lg-4"><span><strong>Vigencia: </strong>{vigencia}</span></div>
                                        <div className="col-lg-4"><span><strong>Fecha de registro: </strong> {fecha_ultimo_antecedente}</span></div>
                                    </>
                                ) : <>

                                    <div className="col-lg-4"><span><strong>Vigencia: {vigencia}</strong></span></div>
                                    <div className="col-lg-4"><span><strong>Fecha de registro: </strong></span></div>

                                </>
                            }

                            <div className="col-lg-4"><span><strong>Fecha de ingreso: </strong> {antecedentesLista.data[index].attributes.fecha_creado}</span></div>
                        </div>

                    </div>
                </div>
            </>
        )
    }

    return (
        <>
            {isRedirect ? redirectToRoutes() : null}
            <div className="row">

                <div className="col-md-12">
                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">

                                <li className="breadcrumb-item">
                                    <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li className="breadcrumb-item"><small>Ramas del proceso</small></li>
                            </ol>
                        </nav>
                      
                    </div>
                </div>

                {respuestaAntecedente ? componentAntecedente() : null}

                {/*CAPTURA Y REPARTO*/}
                <div class="col-lg-4 col-md-6 col-sm-6">
                    <div className="block block-themed">

                        <div className="block-header block-header-default" style={{background:"red"}}>
                            <h3 className="block-title">Captura y Reparto</h3>
                        </div>

                        <div class="block-content">

                            <ul className="nav-items push">

                                {/*ANTECEDENTES*/}
                                <li>
                                    <div className="media-body">

                                        <Link to={`/AntecedentesLista/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            {cambiaColorAntecedentes &&
                                                <button type="button" className="btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left"><i className="fas fa-clipboard-list"></i> Antecedentes</button>
                                            }
                                            {!cambiaColorAntecedentes &&
                                                <button type="button" className="btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left"><i className="fas fa-clipboard-list"></i> Antecedentes</button>
                                            }
                                        </Link>

                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>

                                    </div>

                                </li>

                                {/*DATOS DEL INTERESADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/DatosInteresadoLista/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            {cambiaColorDatosInteresado &&
                                                <button type="button" className="btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left" data-toggle="modal" data-target="#modal-datos-interesado"><i className="fas fa-users"></i> Datos del interesado</button>
                                            }
                                            {!cambiaColorDatosInteresado &&
                                                <button type="button" class="btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left" data-toggle="modal" data-target="#modal-datos-interesado"><i className="fas fa-users"></i> Datos del interesado</button>
                                            }
                                        </Link>

                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>

                                    </div>
                                </li>

                                {/*CLASIFICACION DEL RADICADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/ClasificacionRadicadoLista/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            {cambiaColorClasificacionRadicado &&
                                                <button type="button" className="btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left"><i class="fas fa-sitemap"></i> ClasificaciÃ³n del radicado</button>
                                            }

                                            {!cambiaColorClasificacionRadicado &&
                                                <button type="button" className="btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left"><i class="fas fa-sitemap"></i> ClasificaciÃ³n del radicado</button>
                                            }
                                        </Link>

                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                    </div>
                                </li>

                                {/*ENTIDAD DEL INVESTIGADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/EntidadInvestigadoLista/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            {cambiaColorEntidadInvestigado &&
                                                <button type="button" className="btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left"><i class="fas fa-hotel"></i> Entidad del investigado</button>
                                            }
                                            {!cambiaColorEntidadInvestigado &&
                                                <button type="button" className="btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left"><i class="fas fa-hotel"></i> Entidad del investigado</button>
                                            }
                                        </Link>

                                        <button type="button" class="btn btn-sm btn-warning mr-1 mb-3" data-toggle="modal" data-target="#modal-archivos"><i class="fas fa-folder-open"></i></button>
                                    </div>

                                </li>

                                {/*SOPORTE RADICADO*/}
                                <li>
                                    <div className="media-body">
                                        <Link to={`/SoporteRadicadoLista/`} state={{ from: new ParametroModel(radicado, procesoDisciplinarioId, vigencia) }}>
                                            {cambiaColorSoporteRadicado &&
                                                <button type="button" className="btn btn-sm btn-success w2d_btn-large mr-1 mb-3 text-left"><i class="fas fa-archive"></i> Soporte Radicado</button>
                                            }
                                            {!cambiaColorSoporteRadicado &&
                                                <button type="button" className="btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left"><i class="fas fa-archive"></i> Soporte Radicado</button>
                                            }
                                        </Link>
                                    </div>
                                </li>

                                {/*CERRAR ETAPA*/}
                                <li>

                                    <div className="form-group">
                                        {cambiaColorAntecedentes && cambiaColorDatosInteresado && cambiaColorClasificacionRadicado
                                            && cambiaColorEntidadInvestigado && cambiaColorSoporteRadicado && !estadoEtapaCapturaReparto && (

                                                <Formik

                                                    initialValues={{
                                                        inputCierreEtapa: '',
                                                        inputEtapa: 1,
                                                    }}
                                                    enableReinitialize
                                                    validate={(valores) => {

                                                        let errores = {}
                                                        if (!valores.inputCierreEtapa) {
                                                            errores.inputCierreEtapa = 'Debe seleccionar la opciÃ³n cierre de etapa'
                                                        }
                                                        return errores
                                                    }}

                                                    onSubmit={(valores, { resetForm }) => {
                                                        cerrarEtapa(valores);
                                                    }}
                                                >

                                                    <Form>
                                                        <div className="col-md-12">
                                                            <Field type="checkbox" className="form-check-input" id="inputCierreEtapa" name="inputCierreEtapa" />
                                                            <label className="form-check-label" htmlFor="example-checkbox-inline1">Â¿Con la informaciÃ³n actual se puede dar cierre a esta etapa?</label>
                                                        </div>

                                                        <div className="col-md-12">
                                                            <div className="block-content block-content-full text-center">
