import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Link } from "react-router-dom";
import ClasificacionRadicadoApi from "../../Api/Services/ClasificacionRadicadoApi";
import EntidadInvestigadoApi from "../../Api/Services/EntidadInvestigadoApi";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import { Navigate } from "react-router-dom";

function EntidadInvestigadoForm() {

    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [selectedRegistro, setSelectedRegistro] = useState("");

    let { radicado, procesoDisciplinarioId } = useParams();

    const redirectToRoutes = () => {
        return <Navigate to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`} />;
    }


    let selectChangeRegistro = (e) => {
        setSelectedRegistro(e);
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;
        //TIPO EXPEDIENTE => DERECHO DE PETICION => COPIAS
        if(selectedRegistro === "true" ){

            data = {
                "data": {
                    "type": "entidad_investigado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "nombre_entidad": datos.entidadInvestigado,
                        "nombre_investigado": datos.nombreInvestigado,
                        "cargo": datos.cargoInvestigado,
                        "codigo": datos.codigoEntidadInvestigado,
                        "observaciones": datos.observaciones,
                        "estado": true,
                        "requiere_registro":true

                    }
                }
            }
        }
        else{
            data = {
                "data": {
                    "type": "entidad_investigado",
                    "attributes": {
                        "id_proceso_disciplinario": procesoDisciplinarioId,
                        "id_etapa": 1,
                        "nombre_entidad": null,
                        "nombre_investigado": null,
                        "cargo": null,
                        "codigo": null,
                        "observaciones": null,
                        "estado": true,
                        "requiere_registro":false

                    }
                }
            }
        }

        //console.log(JSON.stringify(data));

        EntidadInvestigadoApi.addEntidadInvestigado(data).then(
            datos => {
                if(!datos.error){
                    window.showModal(2)
                    setIsRedirect(true)
                }
                else{
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }
                window.showSpinner(false);
            }
        )
    }



    // COMPOENENTE TIPO EXPEDIENTE
    const componenteEntidadInvestigado = (aplica_entidad) => {

        if(aplica_entidad === true){
            return (
                <>{ isRedirect ? redirectToRoutes() : null }
                <Spinner />
                { <InfoErrorApi error={errorApi}/> }
                { <InfoExitoApi /> }

                    <Formik
                        initialValues={{
                            entidadInvestigado: '',
                            codigoEntidadInvestigado: '',
                            nombreInvestigado:'',
                            cargoInvestigado:'',
                            observaciones:'',
                        }}
                        enableReinitialize

                        validate={(valores) => {
                            let errores = {}
                            if(!valores.entidadInvestigado){
                                errores.entidadInvestigado = 'Debe seleccionar un tipo de queja'
                            }
                            return errores
                        }}

                        onSubmit={(valores, {resetForm}) => {
                            //console.log (valores);
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="row">

                                <div className="block block-themed">

                                    <div className="block-content">
                                        <div className="row">
                                            <div className="col-md-6">
                                                <div className="form-group">
                                                    <label htmlFor="entidadInvestigado">Entidad del investigado <span className="text-danger">*</span></label>
                                                    <Field as="input" className="form-control" type="text" id="entidadInvestigado" name="entidadInvestigado" placeholder="Entidad del investigado" />
                                                </div>
                                            </div>

                                            <div className="col-md-6">
                                                <div className="form-group">
                                                    <label htmlFor="codigoEntidadInvestigado">CÃ³digo <span className="text-danger">*</span></label>
                                                    <Field as="input" className="form-control" type="text" id="codigoEntidadInvestigado" name="codigoEntidadInvestigado" placeholder="CÃ³digo" />
                                                </div>
                                            </div>

                                            <div className="col-md-6">
                                                <div className="form-group">
                                                    <label htmlFor="nombreInvestigado">Nombre del Investigado <span className="text-danger">*</span></label>
                                                    <Field as="input" className="form-control" type="text" id="nombreInvestigado" name="nombreInvestigado" placeholder="Nombre del Investigado" />
                                                </div>
                                            </div>

                                            <div className="col-md-6">
                                                <div className="form-group">
                                                    <label htmlFor="cargoInvestigado">Cargo del Investigado <span className="text-danger">*</span></label>
                                                    <Field as="input" className="form-control" type="text" id="cargoInvestigado" name="cargoInvestigado" placeholder="Cargo del Investigado" />
                                                </div>
                                            </div>


                                            <div className="col-md-12">
                                                <div className="form-group">
                                                    <label htmlFor="observaciones">Observaciones <span className="text-danger">*</span></label>
                                                    <Field as="textarea" className="form-control" id="observaciones" name="observaciones" rows="4"
                                                           placeholder="Observaciones"></Field>
                                                </div>
                                            </div>

                                            <div className="col-md-12">
                                                <div className="block-content block-content-full text-right">
                                                    <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                                </div>
                                            </div>

                                        </div>
                                    </div>
                                </div>

                            </div>

                        </Form>

                    </Formik>
                </>
            );
        }

        else{

            return (
                <>{ isRedirect ? redirectToRoutes() : null }
                <Spinner />
                { <InfoErrorApi error={errorApi}/> }
                { <InfoExitoApi /> }
                    <Formik
                        initialValues={{

                        }}
                        enableReinitialize


                        onSubmit={(valores, {resetForm}) => {
                            enviarDatos(valores);
                        }}
                    >

                        <Form>
                            <div className="row">

                                <div className="block block-themed">

                                    <div className="block-content">
                                        <div className="row">

                                            <div className="col-md-12">
                                                <div className="block-content block-content-full text-right">
                                                    <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Registrar</button>
                                                </div>
                                            </div>
                                            
                                        </div>
                                    </div>
                                </div>

                            </div>

                        </Form>

                    </Formik>
                </>
            );



        }
    };

    // COMPONENTE PRINCIPAL
    return (
        <>
{ isRedirect ? redirectToRoutes() : null }
                <Spinner />
                { <InfoErrorApi error={errorApi}/> }
                { <InfoExitoApi /> }
            <div className="content">

                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark"  to={`/RamasProceso/${radicado}/${procesoDisciplinarioId}`}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page"  to={`/EntidadInvestigadoLista/${radicado}/${procesoDisciplinarioId}`}><small>Entidad del investigado</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page"  to={`/EntidadInvestigadoForm/${radicado}/${procesoDisciplinarioId}`}><small>Nueva entidad del investigado</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                <div className="row">


                    <div className="col-md-12">
                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Entidad del investigado SINPROC No. {radicado}</h3>
                            </div>
                            <div className="block-content">

                                <div className="form-group">

                                    <div className="alert alert-warning alert-dismissable" role="alert">
                                        <h3 className="alert-heading font-size-h4 my-2">Alerta</h3>
                                        <p className="mb-0">Por la ClasificaciÃ³n del Expediente, Seleccione <strong>SI APLICA o NO APLICA</strong> el registro de esta informaciÃ³n</p>
                                    </div>

                                    <label htmlFor="require">Aplica registro<span className="text-danger">*</span></label>
                                    <select className="form-control" id="registroExpediente" name="registroExpediente" value={selectedRegistro}
                                            onChange={e => selectChangeRegistro(e.target.value)}>
                                        <option value="">Por favor seleccione</option>
                                        <option value="true">Si</option>
                                        <option value="false">No</option>
                                    </select>

                                    {/*DERECHO DE PETICION*/}
                                    { selectedRegistro === 'true' ? componenteEntidadInvestigado(true) : '' }
                                    {/*PODER REFERENTE A SOLIICTUD*/}
                                    { selectedRegistro === 'false' ? componenteEntidadInvestigado(false) : '' }

                                </div>

                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </>
    );
  
}
export default EntidadInvestigadoForm;
