import { useLocation } from 'react-router-dom'
import DataTable from 'react-data-table-component';
import GenericApi from '../../Api/Services/GenericApi';
import Spinner from '../../Utils/Spinner';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import '../../Utils/Constants';
import { getUser } from '../../Utils/Common';

function ActuacionParametrosPlantillaForm(props) {
    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let id_etapa = from.idEtapa;
    let fechaRegistro = from.fechaRegistro;
    let fechaIngreso = from.fechaIngreso;
    let dependencia = getUser().nombre_dependencia ? getUser().nombre_dependencia.nombre : "";

    const [modalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getListaParametros, setListaParametros] = useState({ params: [] });
    const [getListaCamposPorParametro, setListaCamposPorParametro] = useState({ data: [] });
    const [getAntecedentesLista, setAntecedentesLista] = useState({ data: [], links: [], meta: [] });
    const [getInteresadosLista, setInteresadosLista] = useState({ data: [], links: [], meta: [] });
    const [getEntidadesInvestigadoLista, setEntidadesInvestigadoLista] = useState({ data: [], links: [], meta: [] });
    const [getCargosInvestigadoLista, setCargosInvestigadoLista] = useState({ data: [], links: [], meta: [] });
    const [getEntidadInvestigadoLista, setEntidadInvestigadoLista] = useState({ data: [], links: [], meta: [] });
    const [isCheckAll, setIsCheckAll] = useState(false);
    const [isCheck, setIsCheck] = useState([]);
    const [lang, setLang] = useState([]);
    const [getParametrosCampos, setParametrosCampos] = useState();

    const campos = [
        { title: 'Antecedentes', value: null, type: "Antecedentes" },
        { title: 'Dependencia', value: dependencia },
        { title: 'Investigados (Nombre)', value: null, type: "Investigados" },
        { title: 'Investigados (Cargo)', value: null, type: "CargosInvestigados" },
        { title: 'Investigados (Entidad)', value: null, type: "EntidadesInvestigados" },
        { title: 'Interesados', value: null, type: "Interesados" },
        { title: 'Fecha de ingreso', value: fechaIngreso },
        { title: 'Fecha de registro', value: fechaRegistro },
        { title: 'Número de auto (generado despues de aprobación)', value: '${numero_de_auto}' },
        { title: 'Número de radicado', value: radicado },
        //{ id: 8, title: 'Campo de entrada por usuario', value: null },
    ];

    useEffect(() => {
        async function fetchData() {
            cargarParametros();
            cargarAntecedentes();
            cargarInteresados();
            cargarEntidadesInvestigado();
            //cargarCampos();
        }
        fetchData();
    }, [props.id_mas_actuacion]);

    const selectCampos = (parametro) => {
        return (
            campos.map((item, i) => {

                var itemFilter = getListaCamposPorParametro.data.filter(x => x.parametro == parametro);

                if (itemFilter != undefined && itemFilter.length > 0 && itemFilter[0].id_campo === i) {
                    return (
                        <option selected key={i} value={i}>{item.title}</option>
                    )
                } else {
                    return (
                        <option key={i} value={i}>{item.title}</option>
                    )
                }
            })
        )
    }

    const handleChangeInteresados = (e, parametro, id_campo) => {
        const { value, checked } = e.target;

        let valorId = getInteresadosLista.map((item) => {
            if (value == item.id) {
                item.checked = checked;
            }
            return item;
        });

        setInteresadosLista(valorId);
        let val = getInteresadosLista.map((p) => {
            if (p.checked == true) {
                var nombre = `${p.attributes.sujeto_procesal_nombre} ${p.attributes.primer_apellido} ${p.attributes.segundo_apellido} ${p.attributes.primer_nombre} ${p.attributes.segundo_nombre}`;
                return nombre;
            }
        }).filter(x => x != undefined);
        selectChangeListaCampos(parametro, id_campo, val.toString());

        if (checked) {
            // push selected value in list
            setLang(prev => [...prev, value]);
        } else {
            // remove unchecked value from the list
            setLang(prev => prev.filter(x => x !== value));
        }
    }

    const handleChangeAntecedentes = (e, parametro, id_campo) => {
        const { value, checked } = e.target;

        let valorId = getAntecedentesLista.map((item) => {
            if (value == item.id) {
                item.checked = checked;
            }
            return item;
        });

        setAntecedentesLista(valorId);
        let val = getAntecedentesLista.map((p) => {
            if (p.checked == true) {
                var nombre = `${p.attributes.descripcion}`;
                return nombre;
            }
        }).filter(x => x != undefined);
        selectChangeListaCampos(parametro, id_campo, val.toString());

        if (checked) {
            // Se añade el valor en la lista
            setLang(prev => [...prev, value]);
        } else {
            // Se quita el valor de la lista
            setLang(prev => prev.filter(x => x !== value));
        }
    }

    const handleChangeInvestigados = (e, parametro, id_campo) => {
        const { value, checked } = e.target;

        let valorId = getEntidadesInvestigadoLista.map((item) => {
            if (value == item.attributes.nombre_investigado) {
                item.checked = checked;
            }
            return item;
        });

        setEntidadesInvestigadoLista(valorId);
        let val = getEntidadesInvestigadoLista.map((p) => {
            if (p.checked == true) {
                var value = `${p.attributes.nombre_investigado}`;
                var nombre = `${p.attributes.nombre_investigado}`;

                return nombre;
            }
        }).filter(x => x != undefined);
        selectChangeListaCampos(parametro, id_campo, val.toString());

        if (checked) {
            // Se añade el valor en la lista
            setLang(prev => [...prev, value]);
        } else {
            // Se quita el valor de la lista
            setLang(prev => prev.filter(x => x !== value));
        }
    }

    const handleChangeCargosInvestigados = (e, parametro, id_campo) => {
        const { value, checked } = e.target;

        let valorId = getCargosInvestigadoLista.map((item) => {
            if (value == item.attributes.cargo) {
                item.checked = checked;
            }
            return item;
        });

        setCargosInvestigadoLista(valorId);
        let val = getCargosInvestigadoLista.map((p) => {
            if (p.checked == true) {
                var value = `${p.attributes.cargo}`;
                var nombre = `${p.attributes.cargo}`;

                return nombre;
            }
        }).filter(x => x != undefined);
        selectChangeListaCampos(parametro, id_campo, val.toString());

        if (checked) {
            // Se añade el valor en la lista
            setLang(prev => [...prev, value]);
        } else {
            // Se quita el valor de la lista
            setLang(prev => prev.filter(x => x !== value));
        }
    }

    const handleChangeEntidadesInvestigados = (e, parametro, id_campo) => {
        const { value, checked } = e.target;

        let valorId = getEntidadInvestigadoLista.map((item) => {
            if (value == item.attributes.nombre_entidad) {
                item.checked = checked;
            }
            return item;
        });

        setCargosInvestigadoLista(valorId);
        let val = getEntidadInvestigadoLista.map((p) => {
            if (p.checked == true) {
                var value = `${p.attributes.nombre_entidad}`;
                var nombre = `${p.attributes.nombre_entidad}`;

                return nombre;
            }
        }).filter(x => x != undefined);
        selectChangeListaCampos(parametro, id_campo, val.toString());

        if (checked) {
            // Se añade el valor en la lista
            setLang(prev => [...prev, value]);
        } else {
            // Se quita el valor de la lista
            setLang(prev => prev.filter(x => x !== value));
        }
    }

    const selectAntecedentes = (parametro, id_campo) => {
        if (Array.isArray(getAntecedentesLista)) {
            return (
                getAntecedentesLista.map((item, i) => {
                    return (
                        <div className="col-md-12" key={'keyCheck' + item.id}>
                            <input type="checkbox"
                                className="form-check-input"
                                id={'check' + item.id}
                                name={'check' + item.id}
                                value={item.id}
                                onChange={(e) => handleChangeAntecedentes(e, parametro, id_campo)}
                                defaultChecked={item.checked} /> {item.attributes.descripcion}
                        </div>
                        // <option key={item.attributes.descripcion} value={item.attributes.descripcion}>{item.attributes.descripcion}</option>
                    )
                })
            )
        }
    }

    const selectInteresados = (parametro, id_campo) => {
        if (Array.isArray(getInteresadosLista)) {
            return (
                getInteresadosLista.map((item, i) => {
                    var nombre = `${item.attributes.sujeto_procesal_nombre} ${item.attributes.primer_apellido} ${item.attributes.segundo_apellido} ${item.attributes.primer_nombre} ${item.attributes.segundo_nombre}`;
                    return (
                        <div className="col-md-12" key={'keyCheck' + nombre}>
                            <input type="checkbox"
                                className="form-check-input"
                                id={'check' + nombre}
                                name={'check' + nombre}
                                value={item.id}
                                onChange={(e) => handleChangeInteresados(e, parametro, id_campo)}
                                defaultChecked={item.checked} /> {nombre}
                        </div>
                        // <option key={nombre} value={nombre}>{nombre}</option>
                    )
                })
            )
        }
    }

    const selectInvestigados = (parametro, id_campo) => {
        if (Array.isArray(getEntidadesInvestigadoLista)) {
            return (
                getEntidadesInvestigadoLista.map((item, i) => {
                    var value = `${item.attributes.nombre_investigado}`;
                    var nombre = `${item.attributes.nombre_investigado}`;

                    if (nombre != "null") {
                        return (
                            <div className="col-md-12" key={nombre}>
                                <input type="checkbox"
                                    className="form-check-input"
                                    id={nombre}
                                    name={nombre}
                                    value={nombre}
                                    onChange={(e) => handleChangeInvestigados(e, parametro, id_campo)}
                                    defaultChecked={item.checked} /> {nombre}
                            </div>
                            //<option key={value} value={value}>{nombre}</option>
                        )
                    }
                })
            )
        }
    }

    const selectCargosInvestigados = (parametro, id_campo) => {
        if (Array.isArray(getCargosInvestigadoLista)) {
            return (
                getCargosInvestigadoLista.map((item, i) => {
                    var value = `${item.attributes.cargo}`;
                    var nombre = `${item.attributes.cargo}`;

                    if (nombre != "null") {
                        return (
                            <div className="col-md-12" key={nombre}>
                                <input type="checkbox"
                                    className="form-check-input"
                                    id={nombre}
                                    name={nombre}
                                    value={nombre}
                                    onChange={(e) => handleChangeCargosInvestigados(e, parametro, id_campo)}
                                    defaultChecked={item.checked} /> {nombre}
                            </div>
                            // <option key={value} value={value}>{nombre}</option>
                        )
                    }
                })
            )
        }
    }

    const selectEntidadesInvestigados = (parametro, id_campo) => {
        if (Array.isArray(getEntidadInvestigadoLista)) {
            return (
                getEntidadInvestigadoLista.map((item, i) => {
                    var value = `${item.attributes.nombre_entidad}`;
                    var nombre = `${item.attributes.nombre_entidad}`;

                    if (nombre != "null") {
                        return (
                            <div className="col-md-12" key={nombre}>
                                <input type="checkbox"
                                    className="form-check-input"
                                    id={nombre}
                                    name={nombre}
                                    value={nombre}
                                    onChange={(e) => handleChangeEntidadesInvestigados(e, parametro, id_campo)}
                                    defaultChecked={item.checked} /> {nombre}
                            </div>
                            //<option key={value} value={value}>{nombre}</option>
                        )
                    }
                })
            )
        }
    }

    const cargarValoresDeParametro = (parametro) => {

        var campoParametro = getListaCamposPorParametro.data.filter(item => {
            return item.parametro == parametro;
        });

        var tempCampo = campoParametro[0];

        if (tempCampo != undefined && campos[tempCampo.id_campo] != undefined) {
            var itemCampo = campos[tempCampo.id_campo];
            //tiene un valor por defecto

            if (itemCampo.value != null) {

                var newParamsItem = getListaCamposPorParametro.data.map(item => {
                    if (item.parametro == parametro) {
                        item.id_campo = tempCampo.id_campo;
                        var itemCampoId = campos[tempCampo.id_campo];
                        item.valor = (itemCampoId.value != null) ? itemCampoId.value : "";
                    }
                    return item;
                });

                // setListaCamposPorParametro({ data: newParams });

                return (
                    <>{itemCampo.value}</>
                )
            }
            else if (itemCampo.type == "Antecedentes") {
                return (
                    // <select className="form-control" id={`antece-${parametro}`} name={`antece-${parametro}`}
                    //     onChange={e => selectChangeListaCampos(parametro, tempCampo.id_campo, e.target.value)}>
                    //     <option value="">Por favor seleccione</option>
                    //     {selectAntecedentes()}
                    // </select>
                    <div className="custom-control custom-switch custom-control-lg mb-2">
                        {/* <div className="col-md-12" key="checkPrincipal">
                            <input type="checkbox"
                                className="form-check-input"
                                id="checkAll"
                                name="checkAll"
                                onChange={(e) => handleSelectAll(e)}
                            />
                            <label className="form-check-label" htmlFor="example-checkbox-inline1">Seleccionar todos</label>
                        </div> */}
                        {selectAntecedentes(parametro, tempCampo.id_campo)}
                    </div>
                )
            }
            else if (itemCampo.type == "Interesados") {
                return (
                    // <select className="form-control" id={`inter-${parametro}`} name={`inter-${parametro}`}
                    //     onChange={e => selectChangeListaCampos(parametro, tempCampo.id_campo, e.target.value)}>
                    //     <option value="">Por favor seleccione</option>
                    //     {selectInteresados()}
                    // </select>
                    <div className="custom-control custom-switch custom-control-lg mb-2">
                        {/* <div className="col-md-12" key="checkPrincipal">
                            <input type="checkbox"
                                className="form-check-input"
                                id="checkAll"
                                name="checkAll"
                                onChange={(e) => handleSelectAll(e)}
                            />
                            <label className="form-check-label" htmlFor="example-checkbox-inline1">Seleccionar todos</label>
                        </div> */}
                        {selectInteresados(parametro, tempCampo.id_campo)}
                    </div>
                )
            }
            else if (itemCampo.type == "Investigados") {
                return (
                    // <select className="form-control" id={`ei-${parametro}`} name={`ei-${parametro}`}
                    //     onChange={e => selectChangeListaCampos(parametro, tempCampo.id_campo, e.target.value)}>
                    //     <option value="">Por favor seleccione</option>
                    //     {selectInvestigados()}
                    // </select>
                    <div className="custom-control custom-switch custom-control-lg mb-2">
                        {/* <div className="col-md-12" key="checkPrincipal">
                            <input type="checkbox"
                                className="form-check-input"
                                id="checkAll"
                                name="checkAll"
                                onChange={(e) => handleSelectAll(e)}
                            />
                            <label className="form-check-label" htmlFor="example-checkbox-inline1">Seleccionar todos</label>
                        </div> */}
                        {selectInvestigados(parametro, tempCampo.id_campo)}
                    </div>
                )
            }
            else if (itemCampo.type == "CargosInvestigados") {
                return (
                    // <select className="form-control" id={`ei-${parametro}`} name={`ei-${parametro}`}
                    //     onChange={e => selectChangeListaCampos(parametro, tempCampo.id_campo, e.target.value)}>
                    //     <option value="">Por favor seleccione</option>
                    //     {selectCargosInvestigados()}
                    // </select>
                    <div className="custom-control custom-switch custom-control-lg mb-2">
                        {/* <div className="col-md-12" key="checkPrincipal">
                        <input type="checkbox"
                            className="form-check-input"
                            id="checkAll"
                            name="checkAll"
                            onChange={(e) => handleSelectAll(e)}
                        />
                        <label className="form-check-label" htmlFor="example-checkbox-inline1">Seleccionar todos</label>
                    </div> */}
                        {selectCargosInvestigados(parametro, tempCampo.id_campo)}
                    </div>
                )
            }
            else if (itemCampo.type == "EntidadesInvestigados") {
                return (
                    // <select className="form-control" id={`ei-${parametro}`} name={`ei-${parametro}`}
                    //     onChange={e => selectChangeListaCampos(parametro, tempCampo.id_campo, e.target.value)}>
                    //     <option value="">Por favor seleccione</option>
                    //     {selectEntidadesInvestigados()}
                    // </select>
                    <div className="custom-control custom-switch custom-control-lg mb-2">
                        {/* <div className="col-md-12" key="checkPrincipal">
                        <input type="checkbox"
                            className="form-check-input"
                            id="checkAll"
                            name="checkAll"
                            onChange={(e) => handleSelectAll(e)}
                        />
                        <label className="form-check-label" htmlFor="example-checkbox-inline1">Seleccionar todos</label>
                    </div> */}
                        {selectEntidadesInvestigados(parametro, tempCampo.id_campo)}
                    </div>
                )
            }
            else {
                return (
                    <label>Seleccione un campo</label>
                )
            }
        }
        return (<></>)
    }

    const selectChangeListaCampos = (parametro, id_campo, value) => {

        console.log("selectChangeListaCampos >> " + parametro + " " + id_campo + " " + value);

        var newParams = getListaCamposPorParametro.data.map(item => {
            if (item.parametro == parametro) {
                item.id_campo = id_campo;
                var itemCampo = campos[id_campo];
                item.valor = (itemCampo.value != null) ? itemCampo.value : value;
            }
            return item;
        });

        setListaCamposPorParametro({ data: newParams });
        //console.log(newParams);
    }

    const columns = [
        {
            name: 'Parametros de la plantilla',
            cell: parametro => <div><strong>{parametro}</strong></div>,
            sortable: true,
            width: "15%"
        },
        {
            name: 'Campo',
            cell: parametro =>
                <select className="form-control" id={`id${parametro}`} name={`name${parametro}`}
                    onChange={e => selectChangeListaCampos(parametro, e.target.value, "")}>
                    <option value="">Por favor seleccione</option>
                    {selectCampos(parametro)}
                </select>,
            sortable: false,
            width: "25%",
        },
        {
            name: 'Valor seleccionado',
            width: "60%",
            cell: parametro =>
                <div className='row'>
                    <div className='col'>
                        {cargarValoresDeParametro(parametro)}
                    </div>
                </div>
        }
    ];

    const cargarCampos = () => {

        GenericApi.getGeneric('parametro-campos').then(
            datos => {
                if (!datos.error) {
                    console.log("Parametro-campos -> ", datos);
                    setParametrosCampos(datos);
                }
            }
        )
    }

    const cargarParametros = () => {

        setListaParametros(null);

        console.log("getCampos -> ", getParametrosCampos);

        const data = {
            "data": {
                "type": 'mas_actuaciones',
                "attributes": {
                    "id": props.id_mas_actuacion
                }
            }
        }

        GenericApi.getByDataGeneric('mas_actuaciones/parametros-plantilla/' + props.id_mas_actuacion, data).then(
            datos => {
                if (!datos.error) {
                    setListaParametros(datos);

                    // var parametrosCampos = datos.params.map((item, i) => {
                    //     return { parametro: item, id_campo: "", valor: "" };
                    // });

                    var parametrosCampos = datos.params.map((item, i) => {

                        // QUEDA PENDIENTE VALIDAR CON LOS DATOS DE LA TABLA MAS_PARAMETRO_CAMPOS
                        var indexCampo = campos.findIndex(
                            x => x.title == item ||
                                (item == "Hechos" && x.title == "Antecedentes") ||
                                (item == "Radicación" && x.title == "Número de radicado") ||
                                (item == "Investigado" && x.title == "Investigados (Nombre)") ||
                                (item == "Cargo" && x.title == "Investigados (Cargo)") ||
                                (item == "Entidad" && x.title == "Investigados (Entidad)") ||
                                (item == "Quejoso" && x.title == "Interesados") ||
                                (item == "Dependencia Origen" && x.title == "Dependencia") ||
                                (item == "Delegada" && x.title == "Dependencia") ||
                                (item == "Radicado" && x.title == "Número de radicado") ||
                                (item == "Auto" && x.title == "Número de auto (generado despues de aprobación)"));

                        if (indexCampo >= 0) {
                            return { parametro: item, id_campo: indexCampo, valor: "" };
                        } else {
                            return { parametro: item, id_campo: "", valor: "" };
                        }
                    });

                    setListaCamposPorParametro({ data: parametrosCampos });

                }
            }
        )
    }

    const cargarAntecedentes = () => {
        const data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": "antecedentes",
                    "descripcion": "descripcion",
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "id_dependencia": -1,
                    "estado": "1",
                    'per_page': 100,
                    'current_page': 1
                }
            }
        }
        GenericApi.getByDataGeneric('antecedentes/get-antecedentes/' + procesoDisciplinarioId, data).then(
            datos => {
                if (!datos.error) {

                    var parametrosCampos = datos.data.map((item) => {
                        item.checked = false;
                        return item;
                    });

                    setAntecedentesLista(parametrosCampos);
                }
            }
        )
    }

    const cargarInteresados = () => {
        const data = {
            "data": {
                "type": "interesado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "tipo_documento": "1",
                    "numero_documento": "1",
                    "primer_nombre": "1",
                    "segundo_nombre": "1",
                    "primer_apellido": "1",
                    "segundo_apellido": "1",
                    "estado": "1",
                    'per_page': 100,
                    'current_page': 1
                }
            }
        }

        GenericApi.getByDataGeneric('datos-interesado/datos-interesado/' + procesoDisciplinarioId, data).then(
            datos => {
                if (!datos.error) {
                    var parametrosCampos = datos.data.map((item) => {
                        item.checked = false;
                        return item;
                    });
                    setInteresadosLista(parametrosCampos);
                } else {
                    setModalState({ title: "Datos del interesado", message: datos.error.toString(), show: true, redirect: null, from: { from } });
                }
            }
        )
    }

    const cargarEntidadesInvestigado = () => {
        const data = {
            "data": {
                "type": "entidad_investigado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": "1",
                    "requiere_registro": true,
                    "estado": "1",
                    'per_page': 100,
                    'current_page': 1
                }
            }
        }

        GenericApi.getByDataGeneric("entidad-investigado/get-entidad-investigado/" + procesoDisciplinarioId, data).then(
            datos => {
                if (!datos.error) {
                    var parametrosCampos = datos.data.map((item) => {
                        item.checked = false;
                        return item;
                    });
                    setEntidadesInvestigadoLista(parametrosCampos);
                    setCargosInvestigadoLista(parametrosCampos);
                    setEntidadInvestigadoLista(parametrosCampos);
                }
                else {
                    setModalState({ title: "Entidades del investigado", message: datos.error.toString(), show: true, redirect: null, from: { from } });
                }

            }
        )
    }

    const descargarArchivo = () => {
        try {
            window.showSpinner(true);

            var params = getListaCamposPorParametro.data.map(item => {
                return {
                    "param": item.parametro,
                    "value": item.valor
                };
            });
            console.log("######params#####");
            console.log(params);

            const data = {
                "data": {
                    "type": "mas_actuaciones",
                    "attributes": {
                        "params": params,
                    }
                }
            }
            GenericApi.getByDataGeneric('mas_actuaciones/plantilla-diligenciada/' + props.id_mas_actuacion, data).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64File(datos.content_type, datos.base_64, datos.file_name, "docx");
                        window.showSpinner(false);
                    }
                    else {
                        setModalState({ title: "SINPROC No " + radicado + " :: ", message: datos.error.toString(), show: true, redirect: null, from: { from }, alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                }
            )
        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64File(contentType, base64Data, fileName, extension) {
        //console.log("Datos que recibo", contentType, base64Data, fileName, extension);
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    return (
        <>
            <Spinner />
            <ModalGen data={modalState} />
            {getListaParametros != null ?
                <>
                    <div className='col-md-12'>
                        <label>Parametros de la plantilla</label>
                    </div>
                    <div className='col-md-12 mt-2 mb-2'>
                        <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                            columns={columns}
                            data={getListaParametros.params}
                            noDataComponent={global.Constants.DATA_TABLE.SIN_DATOS_TABLE}
                            striped
                        />
                    </div>
                    <div className='col-md-12 mt-2 mb-2'>
                        <label htmlFor="documento_ruta">Descargar documento pre-diligenciado con los valores seleccionados </label>
                        <button type="button" title='Descargar documento diligenciado' onClick={() => descargarArchivo()} className="btn btn-primary ml-2"> <i className="fas fa-file-word"></i></button>
                    </div>
                </>
                :
                <div className='col-md-12'>
                    <label>Cargando parametros de la plantilla...</label>
                </div>}
        </>
    );
}

export default ActuacionParametrosPlantillaForm;
