import React, { useEffect , useState } from "react";
import Spinner from '../Utils/Spinner';
import { Link } from "react-router-dom";
import { getUser } from '../../components/Utils/Common';
import GenericApi from '../Api/Services/GenericApi';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import InfoErrorApi from '../Utils/InfoErrorApi';
import ModalGen from '../Utils/Modals/ModalGeneric';


function AgregarFirma() {

    const [inputListfirmaMecanica, setInputListfirmaMecanica] = useState([{ folios: "", firmaMecanica: {}, filebase64: "", size: 0, id_mas_formato: null }]);
    const [getDatosUsuario, setDatosUsuario] = useState("");
    const [getNombreFirmaMecanica, setNombreFirmaMecanica] = useState("");
    const [getContraseña, setContraseña] = useState("");
    const [errorApi, setErrorApi] = useState('');
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });

    useEffect(() => {
        async function fetchData() {
           window.showSpinner(true);
           obtenerDatosUsuario();
           
        }
        fetchData();
    }, []);

    const obtenerDatosUsuario = () =>{
        GenericApi.getGeneric("usuario/get-usuario-por-name/"+getUser().nombre).then(
            datos => {
                if (!datos.error) {
                    setDatosUsuario(datos.data[0]);
                    setNombreFirmaMecanica(datos.data[0].attributes.firma_mecanica);
                }
                window.showSpinner(false);
            }
        )
    }

    const handleClicfirmaMecanica = () => {
        try {

            const data = {
                "data": {
                    "type": "usuario",
                    "attributes": {
                        "firma_mecanica": "",
                    }
                }
            }

            GenericApi.getByDataGeneric('usuario/get-firma-mecanica/' + getDatosUsuario.id , data).then(
                datos => {
                    if (!datos.error) {
                        downloadBase64FileManual(datos.content_type, datos.base_64, datos.file_name);
                    }
                    else {

                    }
                }
            )
        } catch (error) {
            console.error(error);
        }
    };

    function downloadBase64FileManual(contentType, base64Data, fileName) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName;
        downloadLink.click();
    }

    const handleInputChangeArchivosManual = (e, index) => {

        const { name, files } = e.target;
        if (files.length > 0) {
            const extension = getFileExtension(files[0].name);
            if (extension == "png") {

                const list = [...inputListfirmaMecanica];
                list[index][name] = files[0];

                console.log(list);

                if (files[0]) {
                    list[index][name] = files[0];
                } else {
                    list[index][name] = '';
                    list[index].filebase64 = '';
                    list[index].size = 0;
                    list[index].ext = "";

                    setInputListfirmaMecanica(list);
                }

                // Conversion a Base64
                Array.from(e.target.files).forEach(archivo => {
                    var reader = new FileReader();
                    reader.readAsDataURL(archivo);
                    reader.onload = function () {
                        var arrayAuxiliar = [];
                        var base64 = reader.result;
                        arrayAuxiliar = base64.split(',');
                        list[index].filebase64 = arrayAuxiliar[1];
                        list[index].ext = extension;
                        setInputListfirmaMecanica(list);
                    }
                })
                obtenerPesoTotalArchivosManual(list);
            } else {
                const list = [...inputListfirmaMecanica];
                list[index][name] = files[0];
                list[index].filebase64 = '';
                list[index].size = 0;
                list[index][name] = '';
                setInputListfirmaMecanica(list);
                setErrorApi("El archivo seleccionado no tiene un formato permitido. Debe ser *.png")
                window.showModal(1)
            }
        } else {
            const list = [...inputListfirmaMecanica];
            list[index][name] = '';
            list[index].filebase64 = '';
            list[index].size = 0;
            setInputListfirmaMecanica(list);
            obtenerPesoTotalArchivosManual(list);
        }

    }

    function obtenerPesoTotalArchivosManual(list) {
        let peso = 0;
        list.forEach(
            (archivo, index) => {
                console.log(archivo);
                if (archivo.size) {
                    peso += archivo.size;
                }
            }
        )

        if (peso > 15000000) {
            setErrorApi('El peso/tamaño de los todos los adjuntos superan los 15 Mb pertmitidos para el registro, verifique e tamaño y elimine algunos adjuntos, (Si desea enviar mas archivos realice otro proceso de registro de Soportes del radicado)')
            window.showModal(1)
        }

    }

    function getFileExtension(filename) {
        return filename.slice((filename.lastIndexOf(".") - 1 >>> 0) + 2);
    }

    const changecontraseña = (e) => {
        setContraseña(e.target.value);
    }

    function formatBytes(bytes, decimals = 3) {
        if (bytes === undefined) return '0 Bytes';
        if (bytes === 0) return '0 Bytes';
        const k = 1024;
        const dm = decimals < 0 ? 0 : decimals;
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];
        const i = Math.floor(Math.log(bytes) / Math.log(k));
        return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
    }

    const enviarDatos = (valores) => {
        console.log(getDatosUsuario);
        let data = {
            "data": {
                "type": "usuario",
                "attributes": {
                    "firma_mecanica": inputListfirmaMecanica[0].firmaMecanica.name,
                    "password_firma_mecanica": getContraseña,
                    "firma_mecanica_fileBase64": inputListfirmaMecanica[0].filebase64,
                }
            
            }
        }
        GenericApi.updateGeneric('usuario/set-firma-mecanica',getDatosUsuario.id, data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "Actuación :: Actualización con éxito", message: 'fue actualizada con éxito ', show: true, redirect: '/perfil', alert: global.Constants.TIPO_ALERTA.EXITO });
                }
                else {
                    setModalState({ title: "Actuación :: Error de actualización", message: datos.error.toString(), show: true, alert: global.Constants.TIPO_ALERTA.ERROR });
                }

            }
        )
    }


    return (
        <>
            {<Spinner />}
            {<ModalGen data={getModalState} />}
            {<InfoErrorApi error={errorApi} />}
            <Formik
                initialValues={{
                    firmaMecanica: inputListfirmaMecanica[0],
                    contraseña: getContraseña
                }}
                enableReinitialize
                validate={(valores) => {

                }}
                onSubmit={(valores, { resetForm }) => {
                    console.log(valores);
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>
                        <div className="block block-rounded block-bordered">
                            <div className="block block-themed">
                                <div className="block-header">
                                    <h3 className="block-title">Agregar firma mecánica</h3>
                                </div>
                                <div className="block-content">
                                    <div className="row">

                                        {inputListfirmaMecanica.map((x, i) => {
                                            return (
                                                <div className="col-md-6" key={i}>
                                                    <div className="form-group">
                                                        <div className='row'>
                                                            <div className='col-md-12'>
                                                                <label>Imagen firma mecánica (png) <span className="text-danger">*</span> :</label>
                                                            </div>
                                                            <div className='col-md-12' style={{ marginLeft: '13px' }}>
                                                                <div className='row'>
                                                                    <div className='col-10'>
                                                                        <label className="custom-file-label manual" htmlFor="example-file-input-custom2" data-toggle="manual">{x.firmaMecanica.name != null ? x.firmaMecanica.name : getNombreFirmaMecanica}</label>
                                                                        <input className="custom-file-input manual" data-toggle="custom-file-input2" type="file" name="firmaMecanica" onChange={e => handleInputChangeArchivosManual(e, i)} />
                                                                        <label>Peso : {formatBytes(x.firmaMecanica.size)} </label>
                                                                        <ErrorMessage name="firmaMecanica" component={() => (<span className="text-danger">{errors.archivo}</span>)} />
                                                                    </div>
                                                                    <div className='col-2'>
                                                                    {getDatosUsuario ?
                                                                        getDatosUsuario.attributes.firma_mecanica ? 
                                                                            <button type='button' title='Descargar documento' className='btn btn-sm btn-primary' onClick={() => handleClicfirmaMecanica()}>
                                                                                <i className='fas fa-download'></i>
                                                                            </button>
                                                                        : null
                                                                    : null
                                                                    }
                                                                        
                                                                    </div>

                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            );
                                        })}

                                        <div className="col-md-6">
                                            <div className="form-group">
                                                <label htmlFor="contraseña">Contraseña de firmado <span className="text-danger">*</span></label>
                                                <Field as="input" type="password" className="form-control" id="contraseña" name="contraseña" value={getContraseña} onChange={changecontraseña}></Field>
                                                <ErrorMessage name="contraseña" component={() => (<span className="text-danger">{errors.contraseña}</span>)} />
                                            </div>
                                        </div>

                                        <div className="block-content">
                                            <button type="submit" className="btn btn-rounded btn-primary" >
                                                {getDatosUsuario ?
                                                    getDatosUsuario.attributes.firma_mecanica ? 
                                                        "Actualizar"
                                                    :   "Agregar"
                                                : null
                                                }
                                            </button>
                                            <button className="btn btn-sm btn-primary" >
                                                Descargar documento ejemplo con firma cargada
                                            </button>
                                        </div>

                                    
                                    </div>
                                </div>
                            </div>

                        </div>

                    </Form>
                )}
            </Formik>
        </>
    )

}

export default AgregarFirma;
