import React, { useEffect, useState, } from 'react';
import { Form, Field, Formik } from 'formik';
import { Link } from 'react-router-dom';
import Spinner from '../Utils/Spinner';
import { getUser } from '../../components/Utils/Common';
import { ParametroModel } from '../Models/ParametroModel';
import ModalRemitirExpediente from '../Utils/Modals/ModalRemitirExpediente';
import { hasAccess } from '../../components/Utils/Common';
import DataTable from 'react-data-table-component';
import GenericApi from './../Api/Services/GenericApi';
import ModalGen from '../Utils/Modals/ModalGeneric';
import '../Utils/Constants';

function MisPendientes() {

    const paganationPerPages = global.Constants.DATA_TABLE.PAGINATION_PER_PAGE;
    const [perPage, setPerPage] = useState(global.Constants.DATA_TABLE.PAGINATION_PER_PAGE);
    const [pageActual, setPageActual] = useState(1);
    const [listaPendientes, setListaPendientes] = useState({ data: [], links: [], meta: [] });
    const [getSeach, setSeach] = useState('');
    const [selectedRango, setSelectedRango] = useState("");
    const [nombreUsuario, setNombreUsuario] = useState("");
    const [casosActivos, setCasosActivos] = useState(0);
    const [getFechaFiltro, setFechaFiltro] = useState();
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
    const [getDatosParaRemitir, setDatosParaRemitir] = useState({});
    const [getColorPrimary, setColorPrimary] = useState("btn btn-sm btn-primary w2d_btn-large mr-1 mb-3 text-left");

    const columns = [
        {
            name: 'SINPROC',
            selector: pendiente => pendiente.attributes.radicado,
            sortable: true,
            width: "12%"
        },
        {

            name: 'PROCESO',
            cell: pendiente => <div className="mt-2 mb-2 d-none d-sm-table-cell"><div className='row'>
                <div className="col-10">
                    <small><strong>Vigencia:</strong> {pendiente.attributes.vigencia}</small><br />
                    <small><strong>Fecha de registro:</strong> {pendiente.attributes.created_at}</small><br />
                    <small><strong>Sistema Disciplinarios</strong></small><br />
                    <small style={{ color: '#76B947' }}><strong>

                        {( 
                            pendiente.attributes.tipo_expediente != null && pendiente.attributes.tipo_expediente.id_tipo_expediente === '1' && pendiente.attributes.tipo_expediente.id_tipo_derecho_peticion === '1'?'Derecho Peticion Copias':null || 
                            pendiente.attributes.tipo_expediente != null && pendiente.attributes.tipo_expediente.id_tipo_expediente === '1' && pendiente.attributes.tipo_expediente.id_tipo_derecho_peticion === '2'?'Derecho Peticion General':null || 
                            pendiente.attributes.tipo_expediente != null && pendiente.attributes.tipo_expediente.id_tipo_expediente === '1' && pendiente.attributes.tipo_expediente.id_tipo_derecho_peticion === '3'?'Derecho Peticion Alerta control político':null || 
                            pendiente.attributes.tipo_expediente != null && pendiente.attributes.tipo_expediente.id_tipo_expediente === '2' && pendiente.attributes.tipo_expediente.id_tipo_queja === '1'?'Poder referente a solicitud Externa':null || 
                            pendiente.attributes.tipo_expediente != null && pendiente.attributes.tipo_expediente.id_tipo_expediente === '3' && pendiente.attributes.tipo_expediente.id_tipo_queja === '1'?'Queja Externa':null || 
                            pendiente.attributes.tipo_expediente != null && pendiente.attributes.tipo_expediente.id_tipo_expediente === '3' && pendiente.attributes.tipo_expediente.id_tipo_queja === '2'?'Queja Interna':null || 
                            pendiente.attributes.tipo_expediente != null && pendiente.attributes.tipo_expediente.id_tipo_expediente === '4' && pendiente.attributes.tipo_expediente.id_termino_respuesta === '1'?'Tutela días':null || 
                            pendiente.attributes.tipo_expediente != null && pendiente.attributes.tipo_expediente.id_tipo_expediente === '4' && pendiente.attributes.tipo_expediente.id_termino_respuesta === '2'?'Tutela horas':null ||
                            pendiente.attributes.tipo_expediente == null ? 'Sin Clasificación':null
                        )}

                        </strong>
                        </small>
                        <br />                    
                </div>
                <div className="col-1">
                    <button type="button" data-toggle="popover" data-placement="top" data-tooltip-suffix="Sales" title={pendiente.attributes.ultimo_antecedente.descripcion} className="btn btn-sm btn-light mr-2" ><span className="fa fa-search"> </span></button>
                </div>
            </div>
            </div>,
            selector: pendiente => (pendiente.attributes.vigencia),
            sortable: true,
            width: "35%"
        },
        {
            name: 'DIAS HÁBILES',
            selector: pendiente => (pendiente.attributes.dias_habiles),
            sortable: true,
            width: "12%"
        },
        {
            name: 'DIAS CALENDARIO',
            selector: pendiente => (pendiente.attributes.dias_calendario),
            sortable: true,
            width: "12%"
        },
        {
            name: 'ACCIONES',
            cell: pendiente => <div><Link to={`/LogProcesoDisciplinario/`} state={{
                from: new ParametroModel(pendiente.attributes.radicado, pendiente.id, pendiente.attributes.vigencia,
                    true, getColorPrimary, getColorPrimary, getColorPrimary, getColorPrimary, getColorPrimary)
            }}>

                <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="Consultar Historial" data-original-title="Consultar Historial"><span className="fas fa-folder-open"> </span></button>
            </Link>

                {
                    (hasAccess('MP_RemitirProceso', 'Consultar')) ? (
                        <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="Remitir proceso" onClick={() => showModal(pendiente)} data-original-title="Remitir proceso"><span className="fas fa-file-import"> </span></button>
                    ) : null
                }
                {/*<Link to="/RamasProceso" state={{ from: new ParametroModel(pendiente.attributes.radicado, pendiente.id, pendiente.attributes.vigencia)}}>
                        <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="" data-original-title="Ver rama del proceso"><span className="fas fa-code-branch"> </span></button>
                        </Link>

                        <button type="button" className="btn btn-primary" data-toggle="tooltip" data-html="true" title="" data-original-title="Imprimir Caratula"><span className="fas fa-file-alt"></span></button>

                        <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="" data-original-title="Remitir Proceso"><span className="fas fa-file-import"> </span></button> */}
                {
                    (hasAccess('MP_RamasProceso', 'Consultar')) ? (


                        <Link to={`/RamasProceso/`} state={{
                            from: new ParametroModel(pendiente.attributes.radicado,
                                pendiente.id,
                                pendiente.attributes.vigencia,
                                (pendiente.attributes.evaluacion ? pendiente.attributes.evaluacion.id : ""),
                                (pendiente.attributes.evaluacion ? pendiente.attributes.evaluacion.nombre.toUpperCase() : ""),
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                getColorPrimary,
                                (pendiente.attributes.ultimo_antecedente.usuario) ? (pendiente.attributes.ultimo_antecedente.usuario.nombre?pendiente.attributes.ultimo_antecedente.usuario.nombre:"") + ' ' + (pendiente.attributes.ultimo_antecedente.usuario.apellido?pendiente.attributes.ultimo_antecedente.usuario.apellido:"") : "",
                                pendiente.attributes.ultimo_antecedente.descripcion,
                                pendiente.attributes.created_at,
                                ((pendiente.attributes.proceso_sinproc) ? pendiente.attributes.proceso_sinproc.fecha_ingreso
                                    : (pendiente.attributes.proceso_sirius ? pendiente.attributes.proceso_sirius.fecha_ingreso
                                        : (pendiente.attributes.proceso_desglose.fecha_ingreso))),
                                (pendiente.attributes.ultimo_antecedente.dependencia) ? pendiente.attributes.ultimo_antecedente.dependencia.nombre : "",
                                )
                        }}>
                            <button type="button" className="btn btn-primary mr-2" data-toggle="tooltip" data-html="true" title="Ver rama del proceso" data-original-title="Ver rama del proceso"><span className="fas fa-code-branch"> </span></button>
                        </Link>
                    ) : null
                }</div>,
            width: "29%"
        }
    ];

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            setNombreUsuario(getUser().nombre);
            cargarPendientes(1, paganationPerPages);


        }
        fetchData();
    }, []);

    const cargarPendientes = (page, perPage) => {

        const data = {
            "data": {
                "type": "pendietes",
                "attributes": {
                    "fecha": getFechaFiltro,
                    'per_page': perPage,
                    'current_page': page,
                    "usuario_actual": nombreUsuario
                }
            }
        }
        console.log("DATA: "+JSON.stringify(data));
        GenericApi.getByDataGeneric('mis-pendientes-filter', data).then(

            datos => {
                if (!datos.error) {
                    setListaPendientes(datos)
                    setCasosActivos(datos.data.length)
                    window.showSpinner(false);
                }
                else {
                    setModalState({ title: "Mis pendientes", message: datos.error.toString(), show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.ERROR});
                    window.showSpinner(false);
                }

            }
        )
    }

    //cargamos el modal para remitir proceso
    const showModal = (atributo) => {
       
        setDatosParaRemitir(atributo);
        window.showModalRemitirProceso(atributo);

    }


    const handlePageChange = page => {
        //window.showSpinner(true);
        setPageActual(page);
        //cargarPendientes(page, perPage);
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        //window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        //cargarPendientes(page, newPerPage);
    }

    const enviarDatos = () => {

        window.showSpinner(true);
        setListaPendientes({ data: [], links: [], meta: [] });
        var result = new Date();
        result.setDate(result.getDate() - selectedRango);
        let day = ("0" + result.getDate()).slice(-2);  //get day with slice to have double digit day
        let month = ("0" + (result.getMonth() + 1)).slice(-2); //get your zero in front of single month digits so you have 2 digit months
        let date = result.getFullYear() + '-' + month + '-' + day;
        setFechaFiltro(date);

        const data = {
            "data": {
                "type": "pendientes",
                "attributes": {
                    "fecha": (selectedRango != global.Constants.RANGOS.TODOS) ? date : '',
                    'per_page': paganationPerPages,
                    'current_page': '1',
                    "usuario_actual": nombreUsuario
                }
            }
        }

        GenericApi.getByDataGeneric("mis-pendientes-filter",data).then(
            datos => {
                if (!datos.error) {
                    setListaPendientes(datos)
                }
                else {
                    setModalState({ title: "Mis pendientes", message: datos.error.toString(), show: true, redirect: '/MisPendientes', alert: global.Constants.TIPO_ALERTA.ERROR});
                }
                window.showSpinner(false);
            }
        )
    }
    return (
        <>
            {<Spinner />}
            {<ModalRemitirExpediente object={getDatosParaRemitir} />}
            {<ModalGen data={getModalState} />}

            <Formik
                initialValues={{
                    rango: ''
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}
                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {

                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="w2d_block">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                </ol>
                            </nav>
                        </div>
                        
                        <div className="block block-themed">

                            <div className="block-header">
                                <h3 className="block-title">Listado de Pendientes</h3>
                                <label><i class="fas fa-user-clock"></i> Mis casos activos: {casosActivos}</label>
                            </div>

                            <div className="block-content block-content-full">
                                <div className="row">

                                    <div className="col-12">
                                        <div className="form-group">
                                            <label className="pr-2" htmlFor="rango">Filtrar por</label>
                                            <div className="btn-group">
                                                <button type="submit" onClick={() => setSelectedRango(7)} className="btn btn-outline-primary">
                                                    <i class="far fa-calendar-alt"></i> Últimos 7 días
                                                </button>
                                                <button type="submit" onClick={() => setSelectedRango(1)} className="btn btn-outline-primary">
                                                    <i class="far fa-clock"></i> Desde ayer
                                                </button>
                                                <button type="submit" onClick={() => setSelectedRango(0)} className="btn btn-outline-primary">
                                                    <i class="far fa-hand-point-down"></i> Hoy
                                                </button>
                                                <button type="submit" onClick={() => setSelectedRango(global.Constants.RANGOS.TODOS)} className="btn btn-outline-primary">
                                                    <i class="fas fa-redo" ></i> Todo
                                                </button>
                                            </div>

                                        </div>
                                    </div>

                                </div>
                                <div className='row'>

                                    <div className='col-md-12'>
                                        <div className="form-group ">
                                            <label htmlFor='search'>Buscar: </label>
                                            <Field type="text" id="search" name="search" value={getSeach} onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                        </div>
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="block-content block-content-full">
                                        <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                            columns={columns}

                                            data={listaPendientes.data.filter((suggestion) => {
                                                if (getSeach === "") {
                                                    return suggestion;
                                                } else if (

                                                    ((suggestion.attributes.radicado + suggestion.attributes.nombre_tipo_expediente
                                                        + suggestion.attributes.vigencia + suggestion.attributes.created_at +
                                                        suggestion.attributes.dias_habiles + suggestion.attributes.dias_calendario).toLowerCase().includes(getSeach.toLowerCase()))

                                                ) {
                                                    return suggestion;
                                                }
                                            })}
                                            perPage={perPage}
                                            page={pageActual}
                                            pagination
                                            noDataComponent="Sin datos"
                                            paginationTotalRows={listaPendientes.data.length}
                                            onChangePage={handlePageChange}
                                            onChangeRowsPerPage={handlePerRowsChange}
                                            defaultSortFieldId="Nombre"
                                            striped
                                            paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                            defaultSortAsc={false}
                                        />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    )

}

export default MisPendientes;
