import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState, useRef } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';
import Center from '../../Utils/Styles/Center.js'
import moment from 'moment';

function EntidadMigracionForm() {

    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [countTextArea, setCountTextArea] = useState(0);
    const [getLista, setLista] = useState({ title: "", message: "", show: false });
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
   

    let { radicado, vigencia, id, item } = useParams();


    const [entidad, setEntidad] = useState('');
    const [direccion, setDireccion] = useState('');
    const [sector, setSector] = useState('');
    const [nombreInvestigado, setNombreInvestigado] = useState('');
    const [cargoInvestigado, setCargoInvestigado] = useState('');
    const [observaciones, setObservaciones] = useState('');
    const [registradoPor, setRegistradoPor] = useState('');

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            lista();
            
        }
        fetchData();
    }, []);


    const lista= () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-entidad/"+radicado+"/"+vigencia+"/"+item).then(
                datos => {

                    if (!datos.error) {
                        setLista(datos);
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                   
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }


    const handleClick = (event, i) => {

        if(i===1){
            setEntidad(getLista.data[0].attributes.entidad);
        }

        else if(i===2){
            setDireccion(getLista.data[0].attributes.direccion);      
        }

        else if(i===3){
            setSector(getLista.data[0].attributes.sector);
        }

        else if(i===4){
            setNombreInvestigado(getLista.data[0].attributes.identificacion_interesado);      
        }

        else if(i===5){
            setCargoInvestigado(getLista.data[0].attributes.tipo_sujeto_procesal);      
        }

        else if(i===6){
            setObservaciones(getLista.data[0].attributes.genero);      
        }

        else if(i===7){
            setRegistradoPor(getLista.data[0].attributes.genero);      
        }

    };


    const handleInputChange = (event, i) => {
     
        if(i===1){
            setNombres(event.currentTarget.value);
        }

        else if(i===2){
            setApellidos(event.currentTarget.value)
        }

        else if(i===3){
            setTipoIdentificacion(event.currentTarget.value)
        }

        else if(i===4){
            setIdentificacion(event.currentTarget.value)
        }

        else if(i===5){
            setTipoUsuario(event.currentTarget.value)
        }

        else if(i===6){
            setGenero(event.currentTarget.value)
        }

        return true;

        //return true;
    }



    const infoInteresado = () => {

        if (getLista.data != null && typeof (getLista.data) != 'undefined') {
            return (

                getLista.data.map((item, i) => {
                    return (
                        <>
                        {/* ENTIDAD */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="interesado_nombres_origen">Nombres</label>
                                <Field value={item.attributes.interesado} as="input" type="text" autoComplete="off" className="form-control" id="interesado_nombres_origen" name="interesado_nombres_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 1)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="interesado_nombres_destino">Nombres<span className="text-danger">*</span></label>
                                <Field value={nombres} as="input" type="text" autoComplete="off" className="form-control" id="interesado_nombres_destino" name="interesado_nombres_destino" onChange={event => handleInputChange(event, 1)}></Field>
                            </div>
                        </div> 

                        {/* APELLIDOS */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="interesado_apellidos_origen">Apellidos</label>
                                <Field value={item.attributes.interesado} as="input" type="text" autoComplete="off" className="form-control" id="interesado_apellidos_origen" name="interesado_apellidos_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 2)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    

                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="interesado_apellidos_destino">Apellidos<span className="text-danger">*</span></label>
                                <Field value={apellidos} as="input" type="text" autoComplete="off" className="form-control" id="interesado_apellidos_destino" name="interesado_apellidos_destino" onChange={event => handleInputChange(event, 2)}></Field>
                            </div>
                        </div>


                        {/* TIPO DE IDENTIFICACION */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="interesado_tipo_identificacion_origen">Tipo de identificación</label>
                                <Field value={item.attributes.identificacion_interesado} as="input" type="text" autoComplete="off" className="form-control" id="interesado_tipo_identificacion_origen" name="interesado_tipo_identificacion_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 3)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="interesado_tipo_identificacion_destino">Tipo de identificación<span className="text-danger">*</span></label>
                                <Field value={tipoIdentificacion} as="input" type="text" autoComplete="off" className="form-control" 
                                id="interesado_apellidos_destino" name="interesado_tipo_identificacion_destino" onChange={event => handleInputChange(event, 2)}></Field>
                            </div>
                        </div> 

                        {/* IDENTIFICACION */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="interesado_num_identificacion_origen">No. Identificación</label>
                                <Field value={item.attributes.identificacion_interesado} as="input" type="text" autoComplete="off" className="form-control" 
                                id="interesado_num_identificacion_origen" name="interesado_num_identificacion_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 4)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="interesado_num_identificacion_destino">No. Identificación<span className="text-danger">*</span></label>
                                <Field value={identificacion} as="input" type="text" autoComplete="off" className="form-control" 
                                id="interesado_num_identificacion_destino" name="interesado_num_identificacion_destino" onChange={event => handleInputChange(event, 4)}></Field>
                            </div>
                        </div> 


                        {/* TIPO SUJETO PROCESAL */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="interesado_sujeto_origen">Tipo de sujeto procesal</label>
                                <Field value={item.attributes.tipo_sujeto_procesal} as="input" type="text" autoComplete="off" className="form-control" 
                                id="interesado_sujeto_origen" name="interesado_sujeto_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 5)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="interesado_sujeto_destino">Tipo de sujeto procesal<span className="text-danger">*</span></label>
                                <Field value={tipoUsuario} as="input" type="text" autoComplete="off" className="form-control" 
                                id="interesado_sujeto_destino" name="interesado_sujeto_destino" onChange={event => handleInputChange(event, 5)}></Field>
                            </div>
                        </div>

                        {/* GENERO */}   
                        <div className="col-md-5">
                            <div className="form-group">
                                <label htmlFor="interesado_genero_origen">Género</label>
                                <Field value={item.attributes.genero} as="input" type="text" autoComplete="off" className="form-control" 
                                id="interesado_genero_origen" name="interesado_genero_origen"></Field>
                            </div>
                        </div>
                        
                        <div className="col-md-2">
                            <div className="form-group">
                                <Center>
                                    <button type="button" className="btn btn-success" onClick={event => handleClick(event, 6)}><i class="fas fa-angle-double-right"></i></button>
                                </Center>
                            </div>
                        </div>    
                            
                        <div className="col-md-5">
                           <div className="form-group">
                                <label htmlFor="interesado_sujeto_destino">Género<span className="text-danger">*</span></label>
                                <Field value={genero} as="input" type="text" autoComplete="off" className="form-control" 
                                id="interesado_sujeto_destino" name="interesado_sujeto_destino" onChange={event => handleInputChange(event, 6)}></Field>
                            </div>
                        </div> 

                        </>
                    )
                })
            )
        }
    }

    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;

        /*data = {
            "data": {
                "type": 'antecedente',
                "attributes": {
                    "antecedentes": 3,
                    "descripcion": datos.descripcion,
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "fecha_registro": Date.now(),
                    "id_dependencia": dependencia ? dependencia : "",
                }
            }
        }

        GenericApi.addGeneric("antecedentes",data).then(
            datos => {
                if (!datos.error) {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: 'Registro Exitoso', show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.EXITO});
                }
                else {
                    setModalState({ title: "SINPROC No "+radicado+" :: Crear Antecedente", message: datos.error.toString(), show: true, redirect: '/AntecedentesLista', from: {from}, alert: global.Constants.TIPO_ALERTA.ERROR });
                }
                window.showSpinner(false);

            }
        )*/
    }

    return (
        <>
            {<Spinner />}   
            {<ModalGen data={getModalState} />}
          
            <Formik
                initialValues={{
                    antecedente_origen: '',
                    antecedente_destino: '',
                    fecha_antecedente_origen: '',
                    fecha_antecedente_destino: '',
                }}
                enableReinitialize
                validate={(valores) => {
                    let errores = {}

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {
                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                    <div className="w2d_block">
                        <nav aria-label="breadcrumb">
                        <ol className="breadcrumb breadcrumb-alt push">
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/Buscador`}><small>Buscador</small></Link></li>
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ProcesoDisciplinarioMigracion/${radicado}/${vigencia}/${id}`}><small>Inicio proceso de migración</small></Link></li>
                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/ListarMigracionAntecedentes/${radicado}/${vigencia}/${id}`}><small>Lista de antecedentes</small></Link></li>
                            <li class="breadcrumb-item"> <small>antecedente</small></li>
                        </ol>
                        </nav>
                    </div>

                      
                        <div className="block block-themed">
                            <div className="block-header">
                                <h3 className="block-title">Proceso disciplinario No  :: <strong>Migrar interesado {item}</strong></h3>
                            </div>

                            <div className="block-content">

                                <div className='text-right'>

                                    <Link to={`/AntecedentesLista/`} title='Regresar a lista de Antecedentes' >
                                        <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                                    </Link>
                                </div>

                                <div className="row">

                                    {infoInteresado()}
                                    
                                </div>

                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-rounded btn-primary">Registrar</button>

                                <Link to={`/AntecedentesLista/`}>
                                    <button type="button" className="btn btn-rounded btn-outline-primary">Cancelar</button>
                                </Link>
                            </div>
                        </div>
                    </Form>
                )}
            </Formik>
        </>
    );
}


