import React, { useEffect, useState } from 'react';
import { ErrorMessage, Field, Form, Formik } from 'formik';
import { useLocation } from 'react-router-dom'
import LogApi from '../Api/Services/LogApi';
import moment from 'moment';
import { Link } from "react-router-dom";
function LogEtapaLista() {


    const [errorApi, setErrorApi] = useState('');

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;

    const [getRespuestaLog, setRespuestaLog] = useState(false);
    const [getLogLista, setLogLista] = useState({ data: [], links: [], meta: [] });



    useEffect(() => {
        async function fetchData() {

            const data = {
                "data": {
                    "type": "log_etapa",
                    "attributes": {
                        "descripcion": "descripcion",
                        "id_etapa": "fecha_registro",
                        "id_fase": "id_dependencia",
                        "id_proceso_disciplinario": "id_proceso_disciplinario",
                        "id_tipo_log": "id_tipo_log",
                        "id_estado": "id_estado",
                        "created_user": "created_user",
                        "id_dependencia_origen": "id_dependencia_origen"
                    }
                }
            }


            LogApi.getLogEtapaProcesoDisciplinario(data, procesoDisciplinarioId).then(
                datos => !datos.error ? (setLogLista(datos), setRespuestaLog(true)) : window.showModal(1)
            )

        }
        fetchData();
    }, []);


    const listaLog = () => {

        if (getLogLista.data != null && typeof (getLogLista.data) != 'undefined') {
            return (

                getLogLista.data.map((log, i) => {
                    return (
                        cargarLog(log, i)
                    )
                })
            )
        }
        else {
            return (

                getLogLista.map((log, i) => {
                    return (
                        cargarLog(log, i)
                    )
                })
            )
        }
    }


    const cargarLog = (log, i) => {

        return (
            <>
                <li class="timeline-event">
                    <div class="timeline-event-icon bg-info"><i class="fas fa-cog"></i></div>
                    <div class="timeline-event-block block block-rounded block-fx-pop" data-toggle="appear">
                        <div class="block-header block-header-default">
                            <h3 class="block-title">{(log.attributes.etapa) ? log.attributes.etapa.nombre : ""}</h3>
                            <div class="block-options">

                            </div>
                        </div>
                        <div class="block-content">


                            <table className="table table-bordered table-striped table-vcenter js-dataTable-full">

                                <tbody>

                                    <tr>
                                        <td>
                                            <strong> FECHA / ETAPA TRÁMITE: </strong>
                                        </td>
                                        <td>
                                            {moment(log.attributes.created_at, 'YYYY/MM/DD, h:mm:ss a').format("DD/MM/YYYY, h:mm:ss a")}  - {(log.attributes.etapa ? log.attributes.etapa.nombre : "")}
                                        </td>
                                    </tr>
                                    <tr>
                                        <td>
                                            <strong> DEPENDENCIA / FUNCIONARIO QUE REGISTRA: </strong>
                                        </td>

                                        <td>
                                            {(log.attributes.dependencia_origen ? log.attributes.dependencia_origen.nombre : "")} / {(log.attributes.funcionario_registra ? log.attributes.funcionario_registra.name : "")}
                                        </td>
                                    </tr>
                                    <tr>
                                        <td>
                                            <strong> RESPUESTA :: ACTUACIONES: </strong>
                                        </td>
                                        <td>
                                            {log.attributes.descripcion}
                                        </td>
                                    </tr>
                                    <tr>
                                        <td>
                                            <strong> DEPENDENCIA / FUNCIONARIO ACTUAL: </strong>
                                        </td>
                                        <td>
                                            {(log.attributes.dep_funcionario_actual ? log.attributes.dep_funcionario_actual.nombre : "")} / {(log.attributes.funcionario_actual ? log.attributes.funcionario_actual.name : "")}
                                        </td>
                                    </tr>
                                    <tr>
                                        <td>
                                            <strong> ESTADO TRÁMITE: </strong>
                                        </td>
                                        <td>
                                            {(log.attributes.estado_etapa ? log.attributes.estado_etapa.nombre : "")}
                                        </td>
                                    </tr>
                                </tbody>
                            </table>


                        </div>
                    </div>
                </li>
            </>
        )
    }





    return (
        <>

            <ul class="timeline timeline-alt">
                <div className="col-md-12">
                    <div className="block-content block-content-full text-right">
                        <Link to={`/MisPendientes/`} state={{ from: from }}>
                            <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button> 
                        </Link>
                    </div>
                </div>
                {getRespuestaLog ? listaLog() : null}
                <div className="col-md-12">
                    <div className="block-content block-content-full text-right">
                        <Link to={`/MisPendientes/`} state={{ from: from }}>
                            <button type="button" className="btn btn-success"><i className="fas fa-backward"></i> </button>
                        </Link>
                    </div>
                </div>
            </ul>




        </>
    );
}


export default LogEtapaLista;
