import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { Navigate, useParams } from "react-router";
import DocumentoSiriusApi from '../../Api/Services/DocumentoSiriusApi';
import CierreEtapaApi from '../../Api/Services/CierreEtapaApi';
import { useLocation } from 'react-router-dom';
import { hasAccess } from '../../../components/Utils/Common';
import Spinner from '../../Utils/Spinner';
import ListaBotones from '../../Utils/ListaBotones';
import DataTable from 'react-data-table-component';
import GenericApi from '../../Api/Services/GenericApi';

function SoporteRadicadoLista() {
    
    const [getEstadoLista, setEstadoLista] = useState('');
    const [errorApi, setErrorApi] = useState('');
    const [estadoEtapaCapturaReparto, setEstadoEtapaCapturaReparto] = useState(false);
    const [getSeach, setSeach] = useState('');
    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;
    let id_etapa = from.id_etapa;
    let id_fase = from.id_fase;
    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);
    const [soporteRadicadoLista, setSoporteRadicadoLista] = useState({ data: [], links: [], meta: [] });
    const [getListaDetalleCambios, setListaDetalleCambios] = useState({ data: [], links: [], meta: [] });

    const columns = [
        {
            name: 'Etapa',
            cell: documento_sirius => <div title={documento_sirius.attributes.etapa.nombre}>{documento_sirius.attributes.etapa.nombre}</div>,
            sortable: true,
            width: "10%"
        },
        {
            name: 'Fase',
            selector: documento_sirius => documento_sirius.attributes.fase.nombre,
            sortable: true,
            width: "10%"
        },
        {
            name: 'Descripción',
            cell: documento_sirius => <div title={documento_sirius.attributes.descripcion}>{documento_sirius.attributes.descripcion.substring(0, 50)}...</div>,
            sortable: true,
            width: "20%"
        },
        {
            name: 'Registrado Por',
            cell: documento_sirius => <div title={documento_sirius.attributes.nombre_completo}>{documento_sirius.attributes.nombre_completo}</div>,
            sortable: true
        },
        {
            name: 'Fecha de registro',
            cell: documento_sirius => <div title={documento_sirius.attributes.created_at}>{documento_sirius.attributes.created_at}</div>,
            sortable: true,
            width: "10%"
        },
        {
            name: 'Archivo',
            cell: documento_sirius => documento_sirius.attributes.nombre_archivo,
            sortable: true,
            width: "15%"
        },
        {
            name: 'Acciones',
            cell: documento_sirius => <div className='row'> 
                <div className='col-3'>
                    <button type='button' title='Descargar documento' className='btn btn-sm btn-primary' onClick={() => handleClicArchivo(documento_sirius)}><i className="fas fa-download"></i></button>
                </div>
                <div className='col-3'>
                    <button type='button' title='Consultar Descripcion' className='btn btn-sm btn-primary' data-toggle="modal" onClick={() => cargarDetalleCambiosEstado(documento_sirius.id)} data-target={'#modal-consultar-detalle'}><i className="fas fa-search"></i></button>
                </div>
                <div className='col'>
                    {
                        (hasAccess('CR_Soporte_Radicado', 'Inactivar')) ? (
                            <div>

                                {
                                    (documento_sirius.attributes.estado == 0) ? (
                                        <Link to={`/SoporteRadicadoCambiarEstadoForm/${documento_sirius.id}`} state={{ from: from }}>
                                            <button type='button' title='Activar Soporte' className='btn btn-sm btn-success' data-toggle="modal" data-target={'#modal-cambiar-estado'}><i className="fas fa-plus-circle"></i></button>
                                        </Link>
                                    ) : null
                                }
                                {
                                    (documento_sirius.attributes.estado == 1 && (soporteRadicadoLista.data.length > 1 || pageActual > 1)) ? (
                                        <Link to={`/SoporteRadicadoCambiarEstadoForm/${documento_sirius.id}`} state={{ from: from }}>
                                            <button type='button' title='Inactivar Soporte' className='btn btn-sm btn-danger'><i className="fas fa-minus-circle"></i></button>
                                        </Link>


                                    ) : null
                                }
                                <div className="modal fade" id={'modal-consultar-detalle'} tabIndex="-1" role="dialog" aria-labelledby="modal-block-normal" aria-hidden="true">
                                    <div className="modal-dialog modal-lg" role="document" >
                                        <div className="modal-content">
                                            <div className="block block-themed block-transparent mb-0">
                                                <div className="block-header bg-primary-dark">
                                                    <h3 className="block-title">Detalle del cambio en el antecedente</h3>
                                                    <div className="block-options">
                                                        <button type="button" className="btn-block-option" data-dismiss="modal" aria-label="Close">
                                                            <i className="fa fa-fw fa-times"></i>
                                                        </button>
                                                    </div>
                                                </div>
                                                <div className="block-content" style={{ 'height': '300px', 'overflow': 'scroll', 'display': 'block' }}>
                                                    Observaciones generales: {documento_sirius.attributes.descripcion}<br /><br />
                                                </div>
                                                {
                                                    (getListaDetalleCambios.data.length > 0) ? (
                                                        <div>
                                                            <div className="block-content">
                                                                <spam className="block-title">Historial de cambios en el antecedente</spam>
                                                            </div>

                                                            <div className="block-content" style={{ 'height': '300px', 'overflow': 'scroll', 'display': 'block' }}>
                                                                <table className="table table-bordered table-striped table-vcenter js-dataTable-full">
                                                                    <thead>
                                                                        <tr>
                                                                            <th>Usuario Registra</th>
                                                                            <th>Dependencia</th>
                                                                            <th>Descripción</th>
                                                                            <th>Fecha</th>
                                                                        </tr>
                                                                    </thead>
                                                                    <tbody>
                                                                        {listaDetalleCambios()}
                                                                    </tbody>
                                                                </table>
                                                            </div>
                                                        </div>
                                                    ) : null
                                                }


                                                <div className="block-content block-content-full text-right bg-light">
                                                    <button type="button" className="btn btn-sm btn-primary" data-dismiss="modal">Cerrar</button>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ) : null
                    }
                </div>
            </div>,
            width: "20%"
        }
    ];

    const [getRoutes, setRoutes] = useState({
        id_etapa: id_etapa,
        id_fase: 1,
        crear_registro: "/SoporteRadicadoForm",
        consultar_registros: "/SoporteRadicadoLista",
        adjuntar_documento: "/SoporteRadicadoForm",
        repositorio_documentos: "/SoporteRadicadoLista",
        modulo: "CR_SoporteRadicado",
        funcionalidad_crear: "Crear",
        funcionalidad_consultar: "Consultar"
    });

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            setEstadoLista("Activos")
            cargarDocumentos(1, 10,'1');

        }
        fetchData();
    }, []);

    const cargarDocumentos = (page, perPage, estado) => {
        const data = {
            "data": {
                "type": "documento_sirius",
                "attributes": {
                    "id_proceso_disciplinario": "id_proceso_disciplinario",
                    "id_etapa": id_etapa,
                    "id_fase": id_fase,
                    "url_archivo": "url_archivo",
                    "nombre_archivo": "nombre_archivo",
                    "estado": estado,
                    "file64": "file64",
                    "num_folios": "num_folios",
                    "num_radicado": "pendiente",
                    "extension": "doc",
                    "peso": "peso",
                    'per_page': perPage,
                    'current_page': page
                }
            }
        }

        if (hasAccess('CR_SoporteRadicado', 'Consultar')) {
            DocumentoSiriusApi.getDocumentacionSiriusByIdProDisciplinario(data, procesoDisciplinarioId, perPage, page, estado).then(
                datos => {
                    if (!datos.error) {
                        setSoporteRadicadoLista(datos)
                        window.showSpinner(false);
                        cierreEtapa();
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showSpinner(false);
                        window.showModal()
                    }
                    
                }
            )
        }
    }

    const handlePageChange = page => {
        window.showSpinner(true);
        setPageActual(page);
        cargarDocumentos(page, perPage, (getEstadoLista == "Inactivos"?'0':"1"));
    }

    const handlePerRowsChange = async (newPerPage, page) => {
        window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        cargarDocumentos(page, newPerPage, (getEstadoLista == "Inactivos"?'0':"1"));

    }

    const cargarDetalleCambiosEstado = (id) => {

        console.log('log-proceso-disciplinario/get-log-proceso', id);

        GenericApi.getByIdGeneric('log-proceso-disciplinario/get-log-proceso', id).then(
            datos => !datos.error ? (setListaDetalleCambios(datos)) : (window.showModal(1)));
    }

    function cierreEtapa() {
        // VALIDA CIERRE DE ETAPA CAPTURA Y REPARTO
        const dataCierreEtapa = {

            "data": {
                "type": "cerrar_etapa",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": id_etapa,
                    "id_delegada": "id_delegada",
                    "id_funcionario": "id_funcionario"
                }
            }

        }

        CierreEtapaApi.getCierreEtapaByIdProcesoDisciplinario(dataCierreEtapa).then(
            datos => {
                if (!datos.error) {
                    if (datos["data"].length > 0) {
                        setEstadoEtapaCapturaReparto(true);
                    }
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                }

            }
        )
    }





    const handleClicArchivo = (documento_sirius) => {
        try {
            let nombre_documento = documento_sirius.id;
            let extension = documento_sirius.attributes.extension;
            let es_compulsa = false;

            if (documento_sirius.attributes.compulsa) {
                es_compulsa = true;
            }

            const data = {
                "data": {
                    "type": "documeto_sirius",
                    "attributes": {
                        "radicado": radicado,
                        "vigencia": vigencia,
                        "id_documento_sirius": nombre_documento,
                        "extension": extension,
                        "es_compulsa": es_compulsa
                    }
                }
            }

            DocumentoSiriusApi.getDocumento(data).then(
                datos => {
                    if (!datos.error) {
                        //console.log(datos.content_type);
                        downloadBase64File(datos.content_type, datos.base_64, nombre_documento, extension);
                    }
                    else {
                        setErrorApi(datos.error.toString())
                        window.showModal(1)
                    }
                }
            )
        } catch (error) {
            console.error(error);
        }
    };

    const handleCallback = (childData) => {
        try {
        

            window.showSpinner(true);
            setEstadoLista(childData == '0' ? "Inactivos" : "Activos")
            cargarDocumentos(1, 10, childData);
        } catch (error) {

        }

    }

    function downloadBase64File(contentType, base64Data, fileName, extension) {
        const linkSource = `data:${contentType};base64,${base64Data}`;
        const downloadLink = document.createElement("a");
        downloadLink.href = linkSource;
        downloadLink.download = fileName + "." + extension;
        downloadLink.click();
    }

    const listaSoporteRadicado = () => {
        if (soporteRadicadoLista.data != null && typeof (soporteRadicadoLista.data) != 'undefined') {
            return (
                soporteRadicadoLista.data.map((documento_sirius, i) => {
                    return (
                        cargarTabla(documento_sirius, i)
                    )
                })
            )
        }
        else {
            return (
                soporteRadicadoLista.map((documento_sirius, i) => {
                    return (
                        cargarTabla(documento_sirius, i)
                    )
                })
            )
        }
    }

    const cargarTabla = (documento_sirius, i) => {
        return (
            <tr key={documento_sirius.id}>
                <td className="text-center">{i + 1}</td>
                <td>{documento_sirius.attributes.etapa.nombre}</td>
                <td>{documento_sirius.attributes.fase.nombre}</td>
                <td title={documento_sirius.attributes.nombre_completo}>{documento_sirius.attributes.nombre_completo}</td>
                <td>{documento_sirius.attributes.created_at}</td>
                <td>
                    {documento_sirius.attributes.nombre_archivo}
                </td>
                <td>
                    <div>
                        <button type='button' title='Descargar documento' className='btn btn-sm btn-primary' onClick={() => handleClicArchivo(documento_sirius)}><i className="fas fa-download"></i></button>
                    </div>
                </td>
            </tr>
        )
    }


    const listaDetalleCambios = () => {
        if (getListaDetalleCambios.data != null && typeof (getListaDetalleCambios.data) != 'undefined') {
            return (
                getListaDetalleCambios.data.map((cambio, i) => {
                    return (
                        <tr key={cambio.id}>
                            <td>
                                {cambio.attributes.created_user}
                            </td>
                            <td>
                                {cambio.attributes.dependencia_origen ? cambio.attributes.dependencia_origen.nombre : ""}
                            </td>
                            <td>
                                {cambio.attributes.descripcion}
                            </td>
                            <td>
                                {cambio.attributes.created_at}
                            </td>
                        </tr>
                    )
                })
            )
        }
    }

    return (
        <>
            {<Spinner />}
            <Formik>
                <Form>
                    <div className="col-md-12">
                        <div className="block block-rounded block-bordered">
                            <div className="block-content">
                                <nav aria-label="breadcrumb">
                                    <ol className="breadcrumb breadcrumb-alt push">
                                        <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                        <li className="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                        <li className="breadcrumb-item"> <small>Lista Soporte del radicado</small></li>
                                    </ol>
                                </nav>
                            </div>
                        </div>
                    </div>

                    <div className="block block-themed">
                        <div className="block-header block-header-default">
                            <h3 className="block-title">SINPROC No {radicado} :: <strong>Consultar lista Soporte del radicado</strong></h3>

                        </div>

                        <div className="block-content">
                            <>
                                {
                                    (hasAccess('CR_SoporteRadicado', 'Consultar')) ? (
                                        <div className='row'>

                                            <div className='col-md-3'>
                                                <div className="form-group ">
                                                    <Field type="text" id="search" name="search" onChange={e => setSeach(e.target.value)} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                                </div>
                                            </div>
                                            {/*ListaBotones(getRoutes)*/}
                                            <ListaBotones getRoutes={getRoutes} from={from} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />
                                        </div>
                                    ) : null
                                }
                                {
                                    (hasAccess('CR_SoporteRadicado', 'Consultar')) ? (
                                        <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                            columns={columns}

                                            data={soporteRadicadoLista.data.filter((suggestion) => {
                                                if (getSeach === "") {
                                                    return suggestion;
                                                } else if (

                                                    (((suggestion.attributes.etapa ? suggestion.attributes.etapa.nombre : "")
                                                        + (suggestion.attributes.fase ? suggestion.attributes.fase.nombre : "")
                                                        + suggestion.attributes.nombre_completo
                                                        + suggestion.attributes.nombre_archivo
                                                        + suggestion.attributes.created_at + suggestion.attributes.id_etapa +
                                                        suggestion.attributes.id_fase).toLowerCase().includes(getSeach.toLowerCase()))

                                                ) {
                                                    return suggestion;
                                                }
                                            })}

                                            perPage={perPage}
                                            page={pageActual}
                                            pagination
                                            noDataComponent="Sin datos"
                                            paginationServer
                                            paginationTotalRows={soporteRadicadoLista.meta ? soporteRadicadoLista.meta.total : 1}
                                            onChangePage={handlePageChange}
                                            onChangeRowsPerPage={handlePerRowsChange}
                                            defaultSortFieldId="Nombre"
                                            striped
                                            icon
                                            paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                            defaultSortAsc={false}
                                        />
                                    ) : null
                                }
                            </>
                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );


}
export default SoporteRadicadoLista;
