import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState, useRef } from 'react';
import { Link } from "react-router-dom";
import { getUser } from '../../Utils/Common';
import { useLocation } from 'react-router-dom';
import '../../Utils/Constants';
import ModalGen from '../../Utils/Modals/ModalGeneric';
import GenericApi from '../../Api/Services/GenericApi';
import { useParams } from "react-router";
import Spinner from '../../Utils/Spinner';
import Center from '../../Utils/Styles/Center.js'
import moment from 'moment';

function DatoInteresadoMigracionForm() {

    const [getMaximoTextArea, setMaximoTextArea] = useState(0);
    const [getMinimoTextArea, setMinimoTextArea] = useState(0);
    const [countTextArea, setCountTextArea] = useState(0);
    const [getLista, setLista] = useState({ title: "", message: "", show: false });
    const [getModalState, setModalState] = useState({ title: "", message: "", show: false });
   

    let { radicado, vigencia, item } = useParams();

    const [existeInformacion, setExisteInformacion] = useState(false);
    const [primerNombre, setPrimerNombre] = useState('');
    const [segundoNombre, setSegundoNombre] = useState('');
    const [primerApellido, setPrimerApellido] = useState('');
    const [segundoApellido, setSegundoApellido] = useState('');
    const [numeroDocumento, setNumeroDocumento] = useState('');
    const [email, setEmail] = useState('');
    const [telefono, setTelefono] = useState('');
    const [telefono2, setTelefono2] = useState('');
    const [lugarNacimiento, setLugarNacimiento] = useState('');
    const [profesion, setProfesion] = useState('');
    const [idOrientacionSexual, setIdOrientacionSexual] = useState('');
    const [idSexo, setIdSexo] = useState('');
    const [idNacionalidad, setIdNacionalidad] = useState('');
    const [codMunicipio, setCodMunicipio] = useState('');
    const [localidad, setLocalidad] = useState('');
    const [ciudad, setCiudad] = useState('');
    const [barrio, setBarrio] = useState('');
    const [direccion, setDireccion] = useState('');
    const [diligenciadoPor, setDiligenciadoPor] = useState('');
    const [idTipoEntidad, setIdTipoEntidad] = useState('');
    const [nombreEntidad, setNombreEntidad] = useState('');
    const [sectorEntidad, setSectorEntidad] = useState('');
    const [tipoInteresado, setTipoInteresado] = useState('');

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            lista();
            
        }
        fetchData();
    }, []);

    
    function containsSpecialChars(str) {

        const result = global.Constants.TEXT_AREA.CARACTERES_ESPECIALES.split('').some(specialChar => {
            if (str.includes(specialChar)) {
                return true;
            }

            return false;
        });

        return result;
    }

    const lista= () => {
        try {

            //buscamos el parametro
            GenericApi.getGeneric("migracion-interesado/"+radicado+"/"+vigencia+"/"+item).then(
                datos => {

                    if (!datos.error) {
                        setLista(datos);
                        setExisteInformacion(true);
                        obtenerParametros();
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                   
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    const obtenerParametros = () => {
        try {

            const data = {
                "data": {
                    "type": 'mas_parametro',
                    "attributes": {
                        "nombre": "minimo_caracteres_textarea|maximo_caracteres_textarea"
                    }
                }
            }

            //buscamos el parametro
            GenericApi.getByDataGeneric("parametro/parametro-nombre",data).then(
                datos => {

                    if (!datos.error) {

                        if (datos["data"].length > 0) {

                            datos["data"].filter(data => data["attributes"]["nombre"].includes('minimo_caracteres_textarea')).map(filteredName => (
                                setMinimoTextArea(filteredName["attributes"]["valor"])
                            ))
                            datos["data"].filter(data => data["attributes"]["nombre"].includes('maximo_caracteres_textarea')).map(filteredName => (
                                setMaximoTextArea(filteredName["attributes"]["valor"])
                            ))

                        }
                    } else {
                        setModalState({ title: "Antecedentes", message: datos.error.toString(), show: true, redirect: '/AntecedentesForm', alert: global.Constants.TIPO_ALERTA.ERROR });
                    }
                    window.showSpinner(false);
                }
            )
        } catch (error) {
            // console.log(error);
        }
    }

    const handleClick = (event, i) => {

        if(i===1){
            setPrimerNombre(getLista.data[0].attributes.primerNombre);
        }

        else if(i===2){
            setSegundoNombre(getLista.data[0].attributes.segundoNombre);      
        }

        else if(i===3){
            setPrimerApellido(getLista.data[0].attributes.primerApellido);
        }

        else if(i===4){
            setSegundoApellido(getLista.data[0].attributes.segundoApellido);      
        }

        else if(i===5){
            setNumeroDocumento(getLista.data[0].attributes.numeroDocumento);      
        }

        else if(i===6){
            setEmail(getLista.data[0].attributes.email);      
        }

    };


    const handleInputChange = (event, i) => {
     
        if(i===1){
            setPrimerNombre(event.currentTarget.value);
        }

        else if(i===2){
            setSegundoNombre(event.currentTarget.value)
        }

        else if(i===3){
            setPrimerApellido(event.currentTarget.value)
        }

        else if(i===4){
            setSegundoApellido(event.currentTarget.value)
        }

        else if(i===5){
            setNumeroDocumento(event.currentTarget.value)
        }

        else if(i===6){
            setEmail(event.currentTarget.value)
        }

        return true;

        //return true;
    }



    const infoInteresado = () => {

        if (getLista.data != null && typeof (getLista.data) != 'undefined') {
            return (

