import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import InfoErrorApi from '../Utils/InfoErrorApi';
import InfoExitoApi from '../Utils/InfoExitoApi';
import InfoCustom from '../Utils/InfoCustom';
import { Link } from "react-router-dom";
//import ParametrosMasApi from '../Api/Services/ParametrosMasApi';
import GenericApi from '../Api/Services/GenericApi';

import { Navigate } from "react-router-dom";
import { useParams } from "react-router";
import Autocomplete from "../Autocomplete/Autocomplete";
import Spinner from '../Utils/Spinner';





function AdministracionUsuarioForm() {

    const [errorApi, setErrorApi] = useState('');
    const [listaRoles, setListaRoles] = useState({ data: {} });
    const [respuestaRoles, setRespuestaRoles] = useState(false);
    const [isRedirect, setIsRedirect] = useState(false);
    const [idUsuario, setIdUsuario] = useState('');
    const [formIntialValuesUsuario, setFormIntialValuesUsuario] = useState();
    const [respuestaUsuarioTotal, setRespuestaUsuarioPermitidoTotal] = useState(false);
    const [usuarioPermitidoListaTotal, setUsuarioPermitidoListaTotal] = useState({ data: [], links: [], meta: [] });
    /*Redirects*/
    const redirectToRoutes = () => {
        return <Navigate to={`/AdministracionUsuarioLista/`} />;
    }

    useEffect(() => {
        //window.showSpinner(true)
        async function fetchData() {

            //window.showSpinner(true);

            GenericApi.getAllGeneric('role').then(
                datos => !datos.error ? (setListaRoles(datos), setRespuestaRoles(true), console.log(datos)) : window.showModal(1), window.showSpinner(false)
            )

            GenericApi.getAllGeneric('usuario').then(
                datos => !datos.error ? (setUsuarioPermitidoListaTotal(datos), setRespuestaUsuarioPermitidoTotal(true), window.showSpinner(false)) : window.showModal(1), window.showSpinner(false)
            )

        }
        fetchData();
    }, []);

    const handleCallback = (childData) => {
        try {
            setIdUsuario(childData)
        } catch (error) {

        }

    }


    const enviarDatos = (datos) => {
        window.showSpinner(true);
        let data;
        if (formIntialValuesUsuario) {

            data = {

                "data": {
                    "type": "users",
                    "attributes": {
                        "id": idUsuario,
                        "estado": 1
                    }
                }
            }
        }

        let yaExiste = false;
        usuarioPermitidoListaTotal.data.forEach(element => {

            if (element["attributes"]["id"] == idUsuario) {

                yaExiste = true;
            }

        });

        if (yaExiste == false) {
            GenericApi.addGeneric('usuario', data).then(
                datos => {

                    if (!datos.error) {
                        window.showModal(2)
                        setIsRedirect(true);
                        window.showSpinner(false)
                    }
                    else {
                        //console.log("hay error");
                        console.log(datos.error);
                        window.showModal(1)
                        window.showSpinner(false)
                    }
                    //window.showSpinner(false);
                }
            )
        }
        else {
            window.showModal(5)
            window.showSpinner(false)
        }



    }

    return (
        <>
            {<Spinner />}
            {isRedirect ? redirectToRoutes() : null}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            {<InfoCustom mensaje={"Este usuario ya existe"} />}
            <Formik
                initialValues={{
                    idUsuario: idUsuario ? idUsuario : '',
                }}
                enableReinitialize
                validate={(valores) => {

                    setFormIntialValuesUsuario(valores);

                    let errores = {}

                    return errores
                }}
                onSubmit={(valores, { resetForm }) => {

                    enviarDatos(valores);
                }}
            >

                {({ errors }) => (
                    <Form>

                        <div className="col-md-12">
                            <div className="block block-rounded block-bordered">
                                <div className="block-content">
                                    <nav aria-label="breadcrumb">
                                        <ol className="breadcrumb breadcrumb-alt push">

                                            <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/EntidadesInteresadoLista`}><small>Administracion de Usuarios</small></Link></li>
                                            <li class="breadcrumb-item"> <small>Crear Usuario</small></li>
                                        </ol>
                                    </nav>
                                </div>
                            </div>
                        </div>

                        <div className="block block-rounded block-bordered">

                            <div className="block-content block-content-full">
                                <div className="row">

                                    <div className="col-md-12">
                                        <div className="form-group">

                                        <label htmlFor='idUsuario'>Nombres  <span className="text-danger">*</span></label>
                                        <label htmlFor='idUsuario'>Email <span className="text-danger">*</span></label>
                                       
                                            <label htmlFor='idUsuario'>Roles <span className="text-danger">*</span></label>

                                            <Autocomplete suggestions={listaRoles} parentCallback={handleCallback} />

                                            <ErrorMessage name="idUsuario" component={() => (<span className="text-danger">{errors.idEntidad}</span>)} />
                                        </div>

                                    </div>


                                </div>

                            </div>
                            <div className="block-content block-content-full text-right bg-light">
                                <button type="submit" className="btn btn-primary"><i className="fas fa-save"></i> Crear </button>
                            </div>

                        </div>
                    </Form>
                )}
            </Formik>
        </>
    )

}

export default AdministracionUsuarioForm;
