import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { useParams } from "react-router";
import { Navigate } from "react-router-dom";
import Spinner from '../../Utils/Spinner';
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ParametrosMasApi from '../../Api/Services/ParametrosMasApi';
import ClasificacionRadicadoApi from '../../Api/Services/ClasificacionRadicadoApi';
import { Link } from "react-router-dom";
import DatePicker from 'react-datetime';
import 'react-datetime/css/react-datetime.css';
import moment from 'moment';
import 'moment/locale/es';
import { getUser } from '../../../components/Utils/Common';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import DatePerson from "../../DatePerson/DatePerson";
import { hasAccess } from '../../../components/Utils/Common';
import ClasificacionRadicado from './ClasificacionRadicado';


function ClasificacionRadicadoForm() {

    const [errorApi, setErrorApi] = useState('');
   
    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;


    const [getParametros, setParametros] = useState({
        id_proceso_disciplinario: procesoDisciplinarioId,
        id_etapa: from.idEtapa,
        reclasificacion: false,
        route: "/ClasificacionRadicadoLista/",
    });

    useEffect(() => {
        async function fetchData() {

        }
        fetchData();
    }, []);


    return (
        
        <>
            <Spinner />
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <div className="row">

                <div className="col-md-12">
                    <div className="block block-rounded block-bordered">
                        <div className="block-content">
                            <nav aria-label="breadcrumb">
                                <ol className="breadcrumb breadcrumb-alt push">
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                    <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" aria-current="page" to={`/ClasificacionRadicadoLista/`} state={{ from: from }}><small>Clasificacion del radicado Lista</small></Link></li>
                                    <li class="breadcrumb-item"> <small>Nueva clasificacion del radicado</small></li>
                                </ol>
                            </nav>
                        </div>
                    </div>
                </div>

                <div className="col-md-12">
                    <div className="block block-themed">
                        <div className="block-header">
                            <h3 className="block-title"> SINPROC No {radicado} :: <strong>Crear Clasificación del Radicado</strong></h3>
                        </div>

                        <ClasificacionRadicado getParametros={getParametros} id="cr_componente" name="cr_componente" />

                    </div>
                </div>
            </div>

        </>
    );

}
export default ClasificacionRadicadoForm;
