import { ErrorMessage, Field, Form, Formik } from 'formik';
import React, { useEffect, useState } from 'react';
import { Link } from "react-router-dom";
import { useParams } from "react-router";
import InfoErrorApi from '../../Utils/InfoErrorApi';
import InfoExitoApi from '../../Utils/InfoExitoApi';
import ModalDatosInteresado from '../../Utils/Modals/ModalDatosInteresado';
import DatosInteresadoApi from '../../Api/Services/DatosInteresadoApi';
import Spinner from '../../Utils/Spinner';
import { Navigate } from "react-router-dom";
import ParametrosMasApi from "../../Api/Services/ParametrosMasApi";
import CierreEtapaApi from '../../Api/Services/CierreEtapaApi';
import moment from 'moment';
import { useLocation } from 'react-router-dom'
import { ParametroModel } from '../../Models/ParametroModel';
import { hasAccess } from '../../../components/Utils/Common';
import ListaBotones from '../../Utils/ListaBotones';
import GenericApi from './../../Api/Services/GenericApi';
import DataTable from 'react-data-table-component';

function DatosInteresadoLista() {


    const [getRoutes, setRoutes] = useState({
        id_etapa: 1,
        id_fase: 3,
        crear_registro: "/DatosInteresadoForm",
        consultar_registros: "/DatosInteresadoLista",
        adjuntar_documento: "/SoporteRadicadoForm",
        repositorio_documentos: "/SoporteRadicadoLista",
        modulo: "CR_Interesado",
        funcionalidad_crear: "Crear",
        funcionalidad_consultar: "Consultar"
    });


    const [errorApi, setErrorApi] = useState('');
    const [isRedirect, setIsRedirect] = useState(false);
    const [listaTipoDocumento, setListaTipoDocumento] = useState({ data: {} });
    const [respuestaTipoDocumento, setRespuestaTipoDocumento] = useState(false);
    const [estadoEtapaCapturaReparto, setEstadoEtapaCapturaReparto] = useState(false);
    const [getEstadoLista, setEstadoLista] = useState('');
    const [datosInteresado, setDatosInteresado] = useState({});
    const [getListaDetalleCambios, setListaDetalleCambios] = useState({ data: [], links: [], meta: [] });
    const [perPage, setPerPage] = useState(10);
    const [pageActual, setPageActual] = useState(1);

    const location = useLocation()
    const { from } = location.state
    let radicado = from.radicado;
    let procesoDisciplinarioId = from.procesoDisciplinarioId;
    let vigencia = from.vigencia;
    let id_etapa = from.id_etapa;

    const redirectToRoutes = () => {
        return <Navigate to={`/DatosInteresadoLista/`} state={{ from: from }} />;
    }

    useEffect(() => {
        async function fetchData() {
            window.showSpinner(true);
            setEstadoLista("Activos");
            cargarDatosInteresados(1, 10, "1");


        }
        fetchData();
    }, []);

    const columns = [
        {
            name: 'Fecha registro',
            selector: datosInteresado => datosInteresado.attributes.created_at,
            sortable: true,
            width: "20%"
        },
        {
            name: 'Persona',
            cell: datosInteresado => <div>{datosInteresado.attributes.primer_apellido} {datosInteresado.attributes.segundo_apellido}<br /> {datosInteresado.attributes.primer_nombre} {datosInteresado.attributes.segundo_nombre}</div>,
            sortable: true,
            width: "18%"
        },
        {
            name: 'Entidad',
            cell: datosInteresado => <div>{(datosInteresado.attributes.nombre_entidad) ? datosInteresado.attributes.nombre_entidad : (datosInteresado.attributes.entidad ? datosInteresado.attributes.entidad : (datosInteresado.attributes.id_entidad ? datosInteresado.attributes.id_entidad : "SIN ENTIDAD"))}</div>,
            sortable: true,
        },
        {
            name: '	Registro por',
            cell: datosInteresado => datosInteresado.attributes.nombre_completo,
            sortable: true,
        },
        {
            name: 'Estado',
            selector: datosInteresado => (datosInteresado.attributes.estado == "1" ? 'Activo' : 'Inactivo'),
            sortable: true,
        },
        {
            name: 'Acciones',
            cell: datosInteresado => <div className='row'>
                <div className='col-4'>
                    <button type='button' className='btn btn-sm btn-primary' onClick={() => showModal(datosInteresado.id)}><span className="fas fa-search"></span></button>

                </div>
                <div className='col'>
                    {
                        (hasAccess('CR_Interesado', 'Inactivar')) ? (
                            <div>

                                {
                                    (datosInteresado.attributes.estado == 0) ? (
                                        <Link to={`/DatosInteresadoCambiarEstadoForm/${datosInteresado.id}`} state={{ from: from }}>
                                            <button type='button' title='Activar Datos Interesado' className='btn btn-sm btn-success' data-toggle="modal" data-target={'#modal-cambiar-estado'}><i className="fas fa-plus-circle"></i></button>
                                        </Link>
                                    ) : null
                                }

                                {
                                    (datosInteresado.attributes.estado == 1  && (datosInteresadoListaSearch.data.length > 1 || pageActual >1)) ? (
                                        <Link to={`/DatosInteresadoCambiarEstadoForm/${datosInteresado.id}`} state={{ from: from }}>
                                            <button type='button' title='Inactivar Datos' className='btn btn-sm btn-danger'><i className="fas fa-minus-circle"></i></button>
                                        </Link>
                                    ) : null
                                }

                            </div>
                        ) : null
                    }
                </div>
            </div>
        }
    ];


    const validaCierre = () => {
        // VALIDA CIERRE DE ETAPA CAPTURA Y REPARTO
        const dataCierreEtapa = {

            "data": {
                "type": "cerrar_etapa",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "id_etapa": 1,
                    "id_delegada": "id_delegada",
                    "id_funcionario": "id_funcionario"
                }
            }

        }

        CierreEtapaApi.getCierreEtapaByIdProcesoDisciplinario(dataCierreEtapa).then(
            datos => {
                if (!datos.error) {
                    if (datos["data"].length > 0) {
                        setEstadoEtapaCapturaReparto(true);

                    }
                    window.showSpinner(false);
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showModal(1)
                    window.showSpinner(false);
                }
            }
        )
    }
    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const [respuestaDatosInteresadoSearch, setRespuestaDatosInteresadoSearch] = useState(false);
    const [datosInteresadoListaSearch, setDatosInteresadoListaSearch] = useState({ data: [], links: [], meta: [] });

    const [respuestaDatosInteresadoTotal, setRespuestaDatosInteresadoTotal] = useState(false);
    const [datosInteresadoListaTotal, setDatosInteresadoListaTotal] = useState({ data: [], links: [], meta: [] });

    const handleSearch = (event) => {

        try {
            if ((event.target.value.trim().trim() == "") || (event.target.value.trim().length == 0)) {

                setDatosInteresadoListaSearch(datosInteresadoListaTotal);
            }
            else {


                var filteredData = {
                    data: datosInteresadoListaTotal.data.filter(
                        suggestion => ((suggestion.attributes.primer_apellido
                            + suggestion.attributes.segundo_apellido + suggestion.attributes.primer_nombre +
                            suggestion.attributes.segundo_nombre + suggestion.attributes.nombre_entidad
                            + suggestion.attributes.created_at + suggestion.attributes.nombre_completo
                        ).toLowerCase().includes(event.target.value.toLowerCase()))
                    )
                };
                setDatosInteresadoListaSearch(filteredData)
            }
        } catch (error) {
            console.error(error);
        }

    };

    const enviarDatos = (estado, id) => {

        try {


            //actualizamos el estado
            window.showSpinner(true);
            const data = {

                "data": {
                    "type": "interesado",
                    "attributes": {
                        "estado": estado
                    }
                }
            }


            DatosInteresadoApi.updateDatosInteresado(data, id).then(
                datos => {

                    if (!datos.error) {
                        DatosInteresadoApi.getAllDatosInteresadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                            datos => {
                                if (!datos.error) {
                                    setDatosInteresadoListaTotal(datos);
                                    setRespuestaDatosInteresadoTotal(true);
                                    setDatosInteresadoListaSearch(datos); setRespuestaDatosInteresadoSearch(true);
                                    //window.showModal(2); 
                                    window.showSpinner(false);

                                }
                                else {
                                    window.showModal(1);
                                    window.showSpinner(false);
                                }
                            }
                        )

                        //setIsRedirect(true);
                    }
                    else {
                        //console.log("hay error");
                        console.log(datos.error);
                        window.showSpinner(false)
                    }

                }
            )

        } catch (error) {
            window.showSpinner(false);
        }
    }


    const listaDatisIntersado = () => {

        if (datosInteresadoListaSearch.data != null && typeof (datosInteresadoListaSearch.data) != 'undefined') {
            return (

                datosInteresadoListaSearch.data.map((datosInteresado, i) => {
                    return (
                        cuerpoTabla(datosInteresado, i)
                    )
                })
            )
        }
        else {
            return (

                datosInteresadoListaSearch.map((datosInteresado, i) => {
                    return (
                        cuerpoTabla(datosInteresado, i)
                    )
                })
            )
        }

    }

    const cuerpoTabla = (datosInteresado, i) => {

        return (
            <tr key={datosInteresado.id}>
                <td>{datosInteresado.attributes.created_at}</td>
                <td style={{ width: "20%" }}>{datosInteresado.attributes.primer_apellido} {datosInteresado.attributes.segundo_apellido} {datosInteresado.attributes.primer_nombre} {datosInteresado.attributes.segundo_nombre}</td>
                <td style={{ width: "20%" }}>{(datosInteresado.attributes.nombre_entidad) ? datosInteresado.attributes.nombre_entidad : (datosInteresado.attributes.entidad ? datosInteresado.attributes.entidad : (datosInteresado.attributes.id_entidad ? datosInteresado.attributes.id_entidad : "SIN ENTIDAD"))}</td>
                <td>{datosInteresado.attributes.nombre_completo}</td>
                <td style={{ width: "10%" }}>{datosInteresado.attributes.estado == '0' ? "Inactivo" : "Activo"}</td>
                <td>
                    <>
                        {
                            (hasAccess('CR_Interesado', 'Inactivar')) ? (
                                <div>
                                    <button type='button' className='btn btn-sm btn-primary' onClick={() => showModal(datosInteresado.id)}><span className="fas fa-search"></span></button>

                                    {
                                        (datosInteresado.attributes.estado == 0) ? (
                                            <Link to={`/DatosInteresadoCambiarEstadoForm/${datosInteresado.id}`} state={{ from: from }}>
                                                <button type='button' title='Activar Datos Interesado' className='btn btn-sm btn-success' data-toggle="modal" data-target={'#modal-cambiar-estado' + i}><i className="fas fa-plus-circle"></i></button>
                                            </Link>
                                        ) : null
                                    }

                                    {
                                        (datosInteresado.attributes.estado == 1) ? (
                                            <Link to={`/DatosInteresadoCambiarEstadoForm/${datosInteresado.id}`} state={{ from: from }}>
                                                <button type='button' title='Inactivar Datos' className='btn btn-sm btn-danger'><i className="fas fa-minus-circle"></i></button>
                                            </Link>
                                        ) : null
                                    }

                                </div>
                            ) : null
                        }
                    </>

                </td>

            </tr>
        )
    }

    ///////////////////////////////////////////////////////////
    /////////////////// PARA LA BÚSQUEDA //////////////////////
    ///////////////////////////////////////////////////////////

    const showModal = (id) => {
        window.showSpinner(true);
        //buscamos el detalle
        DatosInteresadoApi.getAllDatosInteresadoById(id).then(
            datos => {
                console.log(JSON.stringify(datos));
                if (!datos.error) {
                    if (datos["data"].length > 0) {
                        setDatosInteresado(datos["data"][0]["attributes"]);
                        window.showModalDatosInteresado(datos["data"][0]["attributes"]);
                        window.showSpinner(false);
                        //buscamos las modificaciones sobre le registro
                    }
                }
                else {
                    setErrorApi(datos.error.toString())
                    window.showSpinner(false)
                    window.showModal(1)
                }
            }

        )

        GenericApi.getByIdGeneric('log-proceso-disciplinario/get-log-proceso', id).then(
            datos2 => {
                console.log(JSON.stringify(datos2));
                if (!datos2.error) {
                    setListaDetalleCambios(datos2);
                } else {
                    window.showModal(1);
                }
                console.log(datos2);


            }
        );

    }

    const cargarDatosInteresados = (page, perPage, estado) => {
        console.log("SANDRA: " + estado)


        const data = {
            "data": {
                "type": "interesado",
                "attributes": {
                    "id_proceso_disciplinario": procesoDisciplinarioId,
                    "tipo_documento": "1",
                    "numero_documento": "1",
                    "primer_nombre": "1",
                    "segundo_nombre": "1",
                    "primer_apellido": "1",
                    "segundo_apellido": "1",
                    "estado": estado,
                    'per_page': perPage,
                    'current_page': page
                }
            }
        }

        if (hasAccess('CR_Interesado', 'Consultar')) {

            DatosInteresadoApi.getAllDatosInteresadoByIdProDisciplinario(data, procesoDisciplinarioId).then(
                datos => !datos.error ? (setDatosInteresadoListaTotal(datos), setRespuestaDatosInteresadoTotal(true),
                    setDatosInteresadoListaSearch(datos), setRespuestaDatosInteresadoSearch(true), validaCierre()) : (window.showModal(1), validaCierre())
            )
        }
        else {
            window.showSpinner(false)
        }

    }

    const handlePageChange = page => {
        window.showSpinner(true);
        setPageActual(page);
        cargarDatosInteresados(page, perPage, '1');
    }

    const handlePerRowsChange = async (newPerPage, page) => {

        window.showSpinner(true);
        setPerPage(newPerPage);
        setPageActual(page);
        cargarDatosInteresados(page, newPerPage, '1');

    }


    const handleCallback = (childData) => {
        try {
            window.showSpinner(true);
            setEstadoLista(childData == '0' ? "Inactivos" : "Activos")
            cargarDatosInteresados(1, 10, childData);
        } catch (error) {

        }

    }

    return (
        <>
            {<Spinner />}
            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}

            <div className="col-md-12">
                <div className="block block-rounded block-bordered">
                    <div className="block-content">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb breadcrumb-alt push">
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/MisPendientes`}><small>Mis Pendientes</small></Link></li>
                                <li class="breadcrumb-item"> <Link underline="hover" className="text-dark" to={`/RamasProceso/`} state={{ from: from }}><small>Ramas del proceso</small></Link></li>
                                <li class="breadcrumb-item"> <small>Datos del interesado Lista</small></li>
                            </ol>
                        </nav>
                    </div>
                </div>
            </div>

            {isRedirect ? redirectToRoutes() : null}
            {<ModalDatosInteresado datosInteresado={datosInteresado} getListaDetalleCambios={getListaDetalleCambios} />}

            {<InfoErrorApi error={errorApi} />}
            {<InfoExitoApi />}
            <Formik>
                <Form>

                    <div className="block block-themed">

                        <div className="block-header">
                            <h3 className="block-title">SINPROC No {radicado} :: <strong>Consultar lista de datos del interesado {getEstadoLista}</strong></h3>
                        </div>

                        <div className="block-content">
                            <>

                                <div className='row'>

                                    <div className='col-md-3'>
                                        <div className="form-group ">
                                            <Field type="text" id="search" name="search" onChange={handleSearch} onKeyPress={(e) => { e.key === 'Enter' && e.preventDefault(); }} className="form-control border border-success" placeholder="Buscar" />

                                        </div>
                                    </div>

                                    {/*ListaBotones(getRoutes)*/}
                                    <ListaBotones getRoutes={getRoutes} from={from} parentCallback={handleCallback} id="botonesNavegacion" name="botonesNavegacion" />

                                </div>


                                {
                                    (hasAccess('CR_Interesado', 'Consultar')) ? (
                                        <div className="row">
                                            <div className="col-sm-12">

                                                <DataTable className="table table-bordered table-striped table-vcenter js-dataTable-full"
                                                    columns={columns}
                                                    data={datosInteresadoListaSearch.data}
                                                    perPage={perPage}
                                                    page={pageActual}
                                                    pagination
                                                    noDataComponent="Sin datos"
                                                    paginationServer
                                                    paginationTotalRows={datosInteresadoListaSearch.meta ? datosInteresadoListaSearch.meta.total : 1}
                                                    onChangePage={handlePageChange}
                                                    onChangeRowsPerPage={handlePerRowsChange}
                                                    defaultSortFieldId="Nombre"
                                                    striped
                                                    icon
                                                    paginationComponentOptions={{ rowsPerPageText: 'Filas por página' }}
                                                    defaultSortAsc={false}
                                                />

                                            </div>
                                        </div>
                                    ) : null
                                }
                            </>

                        </div>
                    </div>
                </Form>
            </Formik>
        </>
    );


}
export default DatosInteresadoLista;
